INSERT INTO `sach` (`sach_id`, `dmuc_id`, `sach_name`, `tacgia`, `nxb`, `soluong`, `dongia`, `sotrang`, `loaibia`, `giamgia`, `gioithieu`, `hinhanh`, `post_date`, `luot_mua`) VALUES
(1, 1, 'Bí Kíp Chinh Phục Khách Hàng Qua Điện Thoại', 'Alphabooks', 'Lao động xã hội - Alphabooks', 25, 79000, 279, 'mềm', '10', '<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><strong><span style="line-height: 24px; color: #ff3300; font-size: 12pt;">B&iacute; K&iacute;p Chinh Phục Kh&aacute;ch H&agrave;ng Qua Điện Thoại</span></strong></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">Thương trường l&agrave; một chiến trường thầm lặng v&ocirc; c&ugrave;ng khốc liệt, nơi m&agrave; c&aacute;c doanh nghiệp cần c&oacute; mọi thứ vũ kh&iacute; lợi hại để sẵn s&agrave;ng chiến đấu. Tuy nhi&ecirc;n hiện nay, khi những c&ocirc;ng cụ hữu &iacute;ch như chiến lược gi&aacute;, mẫu m&atilde; v&agrave; chất lượng của sản phẩm đ&atilde; gần như b&atilde;o h&ograve;a th&igrave; phong c&aacute;ch phục vụ lại nổi l&ecirc;n th&agrave;nh một yếu tố chiếm lợi thế gần như tuyệt đối, v&agrave; những người nắm giữ cũng như sử dụng ch&uacute;ng kh&ocirc;ng ai kh&aacute;c ch&iacute;nh l&agrave; những nh&acirc;n vi&ecirc;n b&aacute;n h&agrave;ng trực tiếp phục vụ kh&aacute;ch h&agrave;ng.</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">B&aacute;n h&agrave;ng qua điện thoại đang l&agrave; h&igrave;nh thức tiếp thị rộng r&atilde;i của c&aacute;c c&ocirc;ng ty trong việc tiếp cận kh&aacute;ch h&agrave;ng của m&igrave;nh. Tuy nhi&ecirc;n, l&agrave;m sao để kh&aacute;ch h&agrave;ng c&oacute; thể vui vẻ chấp nhận cuộc điện thoại của m&igrave;nh th&igrave; kh&ocirc;ng phải l&agrave; điều dễ d&agrave;ng. 40+&nbsp;&nbsp;B&iacute; k&iacute;p chinh phục kh&aacute;ch h&agrave;ng qua điện thoại l&agrave; một cuốn cẩm nang nhỏ gọn, hữu &iacute;ch d&agrave;nh cho mọi nh&acirc;n vi&ecirc;n cần trau dồi th&ecirc;m những kỹ năng giao tiếp cũng như khả năng thuyết phục kh&aacute;ch h&agrave;ng th&ocirc;ng qua điện thoại, khiến họ h&agrave;i l&ograve;ng v&agrave; vui vẻ mua h&agrave;ng, quan trọng nhất l&agrave; khiến họ trở th&agrave;nh những kh&aacute;ch h&agrave;ng trung th&agrave;nh của bạn.</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">Tất cả những b&iacute; kịp bạn cần đều nằm trong cuốn s&aacute;ch n&agrave;y. Với 40 +&nbsp;&nbsp;t&igrave;nh huống bị từ chối v&agrave; phương ph&aacute;p đối ph&oacute; thực tế, được viết ra dựa tr&ecirc;n những chia sẻ kinh nghiệm của c&aacute;c nh&acirc;n vi&ecirc;n tiếp thị xuất sắc, qua những khảo s&aacute;t, ph&acirc;n t&iacute;ch, nghi&ecirc;n cứu về kịch bản đối thoại giữa họ v&agrave; kh&aacute;ch h&agrave;ng. Cuốn s&aacute;ch kh&ocirc;ng chỉ tiết lộ những phương thức tiếp thị th&agrave;nh c&ocirc;ng m&agrave; c&ograve;n cho bạn c&aacute;i nh&igrave;n đầy th&uacute; vị về quy luật từ chối của kh&aacute;ch h&agrave;ng.</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><strong><span style="line-height: 24px; font-size: 12pt;">MỤC LỤC:</span></strong></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">Chương 1: Khi kh&aacute;ch h&agrave;ng từ chối ngay từ c&acirc;u n&oacute;i đầu ti&ecirc;n</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">Chương 2: Khi bị nh&acirc;n vi&ecirc;n lễ t&acirc;n g&acirc;y kh&oacute; dễ</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">Chương 3: Khi ph&aacute;t hiện ra nhu cầu của kh&aacute;ch h&agrave;ng</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">Chương 4: Khi kh&aacute;ch h&agrave;ng lo ngại về chất lượng sản phẩm hoặc c&ocirc;ng ty của bạn</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">Chương 5: Khi kh&aacute;ch h&agrave;ng thắc mắc về gi&aacute; cả</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">Chương 6: Để kh&aacute;ch h&agrave;ng kh&ocirc;ng n&oacute;i lời từ chối</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">Chương 7: Sổ tay nghệ thuật xử l&yacute; t&igrave;nh huống khi bị kh&aacute;ch h&agrave;ng từ chối trong tiếp thị điện thoại</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><strong><span style="line-height: 24px; font-size: 12pt;">Mời bạn đ&oacute;n đọc.</span></strong></span></p>', '567740-bi-kip-chinh-phuc-khach-hang.jpg', '2013-12-17', 0),
(2, 1, 'Lee Kun-Hee - Những Lựa Chọn Chiến Lược Và Kỳ Tích Samsung', 'Ji Pyeong Gil', 'Thế giới - Alphabooks', 15, 119000, 368, 'mềm', '10', '<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><strong><span style="line-height: 26px; color: #ff3300; font-size: 13pt;">Lee Kun-Hee - Những Lựa Chọn Chiến Lược V&agrave; Kỳ T&iacute;ch Samsung</span></strong></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">Năm 1987, chủ tịch s&aacute;ng lập Samsung qua đời, hai tuần sau đ&oacute; Lee Kun- Hee, con trai thứ ba của &ocirc;ng tiếp quản đế chế Samsung. Mặc d&ugrave; đ&atilde; l&agrave; một trong những tập đo&agrave;n h&agrave;ng đầu H&agrave;n Quốc, nhưng c&aacute;c sản phẩm của Samsung khi ấy hầu như chỉ được ti&ecirc;u thụ trong nước, v&igrave; bị &ldquo;l&eacute;p vế &rdquo;khi xuất khẩu ra những thị trường kh&oacute; t&iacute;nh hơn như Ch&acirc;u &Acirc;u, Mỹ bởi chất lượng thấp d&ugrave; gi&aacute; th&agrave;nh rẻ mạt.</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">Đứng trước t&igrave;nh trạng tr&igrave; trệ của tập đo&agrave;n, Lee Kun- Hee đ&atilde; thực hiện một loạt những cải c&aacute;ch thay đổi. Sau 20 năm (1993-2013) kể từ ng&agrave;y chủ tịch Samsung Lee Kun-hee đưa ra tuy&ecirc;n bố h&ugrave;ng hồn g&acirc;y chấn động tại hội nghị Bắc Kinh: &ldquo;Samsung kh&ocirc;ng phải l&agrave; số 1 hay số 2. Chỉ duy nhất l&agrave; hạng 1,5 m&agrave; th&ocirc;i.&rdquo;. Đến thời điểm hiện nay &ndash; năm 2013 &ndash; Samsung đ&atilde; vươn l&ecirc;n vị tr&iacute; h&agrave;ng đầu thế giới.</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">Cuốn s&aacute;ch n&agrave;y sẽ cho bạn biết những chọn lựa chiến lược nhạy b&eacute;n v&agrave; đ&uacute;ng đắn của chủ tịch Lee đ&atilde; từng bước đưa Samsung đến vị tr&iacute; ng&agrave;y h&ocirc;m nay như thế n&agrave;o. B&ecirc;n cạnh đ&oacute; l&agrave; tinh thần v&agrave; &yacute; ch&iacute; lao động, th&aacute;i độ l&agrave;m việc quyết liệt, phong c&aacute;ch quản l&yacute; độc đ&aacute;o v&agrave; phần n&agrave;o độc đo&aacute;n của Lee Kun Hee.</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">Những điều m&agrave; Lee Kun Hee đ&atilde; l&agrave;m kh&ocirc;ng chỉ thể hiện một &yacute; ch&iacute; mạnh mẽ điển h&igrave;nh cho tinh thần H&agrave;n Quốc m&agrave; hơn thế, n&oacute; đ&atilde; vượt xa tầm nh&igrave;n của những người c&ugrave;ng thời. V&igrave; vậy, cuốn s&aacute;ch n&agrave;y sẽ gi&uacute;p c&aacute;c bạn, đặc biệt l&agrave; những người đang v&agrave; sẽ l&agrave;m chủ một doanh nghiệp c&oacute; thể vững tin hơn trong những quyết định kh&aacute;c biệt của m&igrave;nh.</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">Với c&aacute;c bạn trẻ, học sinh sinh vi&ecirc;n Việt Nam khi đọc cuốn s&aacute;ch n&agrave;y sẽ hiểu được rằng, đằng sau những th&agrave;nh c&ocirc;ng rực rỡ, đằng sau l&agrave;n s&oacute;ng văn h&oacute;a H&agrave;n Quốc mạnh mẽ hiện nay l&agrave; cả một khối quyết t&acirc;m hừng hực, ki&ecirc;n cường. Alpha Books rất mong c&aacute;c bạn c&oacute; thể cảm nhận được khối quyết t&acirc;m ấy v&agrave; chuyển tải n&oacute; v&agrave;o việc học tập, lao động v&agrave; s&aacute;ng tạo của m&igrave;nh.</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><strong><span style="line-height: 24px; font-size: 12pt;">Th&ocirc;ng tin t&aacute;c giả:</span></strong></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">Ji Pyeong Gil l&agrave; một người rất am hiểu về tập đo&agrave;n Samsung. &Ocirc;ng từng d&agrave;nh nhiều năm t&igrave;m hiểu về Samsung trước khi bắt tay viết cuốn s&aacute;ch n&agrave;y.</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><strong><span style="line-height: 24px; font-size: 12pt;">Mời bạn đ&oacute;n đọc.</span></strong></span></p>', '8687lee-kun-hee.jpg', '2013-12-17', 0),
(3, 1, '72 Thuật Tấn Công Tâm Lý Trong Bán Lẻ', 'Alphabooks', 'Lao động xã hội - Alphabooks', 30, 79000, 291, 'mềm', '10', '<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><strong><span style="line-height: 26px; color: #ff3300; font-size: 13pt;">72 Thuật Tấn C&ocirc;ng T&acirc;m L&yacute; Trong B&aacute;n Lẻ</span></strong></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">&ldquo;Một người b&aacute;n h&agrave;ng th&agrave;nh c&ocirc;ng nhất định phải l&agrave; một nh&agrave; t&acirc;m l&yacute; học vĩ đại&rdquo;- l&agrave; c&acirc;u danh ng&ocirc;n nổi tiếng của ng&agrave;nh b&aacute;n h&agrave;ng. Tr&ecirc;n thực tế,&nbsp;&nbsp;mỗi nh&acirc;n vi&ecirc;n b&aacute;n h&agrave;ng từ khi t&igrave;m được kh&aacute;ch h&agrave;ng cần đứng tr&ecirc;n g&oacute;c độ của kh&aacute;ch h&agrave;ng để suy x&eacute;t vấn đề, nhận biết trong suy nghĩ kh&aacute;ch h&agrave;ng đang xem x&eacute;t về c&aacute;i g&igrave;, trao đổi v&agrave; đ&aacute;p ứng nhu cầu cảu họ; c&oacute; vậy bạn mới c&oacute; thể b&aacute;n được h&agrave;ng, n&acirc;ng cao được th&agrave;nh t&iacute;ch của m&igrave;nh.</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">Cuốn s&aacute;ch n&agrave;y sẽ gi&uacute;p bạn c&oacute; một c&aacute;i nh&igrave;n đầy đủ về nghề b&aacute;n h&agrave;ng, cũng như cung cấp cho bạn những kiến thức, kỹ năng cần thiết để biến m&igrave;nh trở th&agrave;nh một người b&aacute;n h&agrave;ng th&agrave;nh c&ocirc;ng bậc thầy.</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">V&agrave; dưới đ&acirc;y l&agrave; mục lục của cuốn s&aacute;ch:</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">1- B&aacute;n h&agrave;ng kh&ocirc;ng phải l&agrave; một c&ocirc;ng việc thấp k&eacute;m</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">2- Xem bản th&acirc;n như l&agrave; sản phẩm</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">3- Tạo ấn tượng đầu ti&ecirc;n thật kh&oacute; phai</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">4- Khả năng x&aacute;c định mục ti&ecirc;u cao</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">5-H&atilde;y l&agrave; chỉnh bản th&acirc;n m&igrave;nh</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">6-Tự khẳng định, n&acirc;ng cao bản th&acirc;n.</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">7- Bộc lộ năng lực ứng biến nhanh nhậy th&ocirc;ng minh</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">8- C&oacute; t&agrave;i năng l&agrave;m bản th&acirc;n tỏa s&aacute;ng</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">9- Muốn l&agrave;m bạn h&agrave;ng, h&atilde;y l&agrave;m bạn b&egrave;.</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">10-Sự ch&acirc;n th&agrave;nh cho con đường ti&ecirc;u thụ th&ecirc;m d&agrave;i v&agrave; rộng.</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">11-Thất bại chỉ c&aacute;ch th&agrave;nh c&ocirc;ng một bước th&ocirc;i.</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><strong><span style="line-height: 24px; font-size: 12pt;">Mời bạn đ&oacute;n đọc.</span></strong></span></p>', '320072-thuat-tan-cong-tam-ly-trong-ban-le.jpg', '2013-12-17', 0),
(4, 1, 'Khoảng Cách Từ Nói Đến Làm', 'Jeffrey Pfeffer - Robert I. Sutton', 'Lao động xã hội - Alphabooks', 35, 99000, 359, 'mềm', '15', '<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><strong><span style="line-height: 26px; color: red; font-size: 13pt;">Khoảng C&aacute;ch Từ N&oacute;i Đến L&agrave;m - C&aacute;c Doanh Nghiệp Th&ocirc;ng Minh Đ&atilde; Biến L&yacute; Thuyết Th&agrave;nh H&agrave;nh Động Như Thế N&agrave;o?</span></strong></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">Tại sao lại c&oacute; qu&aacute; nhiều khoảng c&aacute;ch giữa những điều m&agrave; c&aacute;c c&ocirc;ng ty biết l&agrave; họ n&ecirc;n l&agrave;m với những việc họ thực sự l&agrave;m?</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">Tại sao lại c&oacute; qu&aacute; nhiều c&aacute;c c&ocirc;ng ty kh&ocirc;ng thể &aacute;p dụng những kinh nghiệm v&agrave; kiến thức m&agrave; họ đ&atilde; phải rất nỗ lực mới t&iacute;ch lũy được?</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><strong><span style="line-height: 24px; font-size: 12pt;">Khoảng C&aacute;ch Từ N&oacute;i Đến L&agrave;m</span></strong><span style="line-height: 24px; font-size: 12pt;">&nbsp;(The Knowing-Doing Gap) l&agrave; cuốn s&aacute;ch đầu ti&ecirc;n đối mặt với th&aacute;ch thức biến kiến thức về c&aacute;ch n&acirc;ng cao hiệu suất hoạt động th&agrave;nh h&agrave;nh động thực sự tạo ra những kết quả c&oacute; thể định lượng được.</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">Trong cuốn s&aacute;ch n&agrave;y, Jeffrey Pfeffer v&agrave; Robert Sutton, những t&aacute;c giả v&agrave; giảng vi&ecirc;n nổi tiếng, đ&atilde; x&aacute;c định nguy&ecirc;n nh&acirc;n của khoảng c&aacute;ch từ-biết-tới-l&agrave;m v&agrave; đưa ra c&aacute;ch lấp đầy khoảng c&aacute;ch đ&oacute;. Th&ocirc;ng điệp trong cuốn s&aacute;ch rất r&otilde; r&agrave;ng &ndash; c&aacute;c c&ocirc;ng ty muốn biến kiến thức th&agrave;nh h&agrave;nh động phải tr&aacute;nh xa &ldquo;bẫy n&oacute;i su&ocirc;ng&rdquo;(smart talk trap). Những kế hoạch, ph&acirc;n t&iacute;ch, cuộc họp, buổi tr&igrave;nh b&agrave;y của nh&agrave; điều h&agrave;nh để truyền cảm hứng cho c&ocirc;ng việc kh&ocirc;ng thể thay thế cho h&agrave;nh động.</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">Những c&ocirc;ng ty m&agrave; h&agrave;nh động dựa tr&ecirc;n kiến thức sẽ loại bỏ được nỗi sợ h&atilde;i, sự cạnh tranh nội bộ, biết được c&aacute;i g&igrave; quan trọng v&agrave; củng cố vai tr&ograve; người l&atilde;nh đạo, người vốn hiểu r&otilde; c&ocirc;ng việc của từng nh&acirc;n vi&ecirc;n trong c&ocirc;ng ty.</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">C&aacute;c t&aacute;c giả lấy v&iacute; dụ từ h&agrave;ng t&aacute; c&ocirc;ng ty để chỉ ra tại sao một số c&oacute; thể vượt qua khoảng c&aacute;ch từ-biết-đến-l&agrave;m, một số d&ugrave; cố nhưng kh&ocirc;ng thể v&agrave; một số kh&aacute;c tr&aacute;nh được ngay từ đầu.&nbsp;<strong>Khoảng C&aacute;ch Từ N&oacute;i Đến L&agrave;m</strong>&nbsp;chắc chắn l&agrave; cuốn s&aacute;ch hữu &iacute;ch đối với những gi&aacute;m đốc điều h&agrave;nh ở mọi nơi tr&ecirc;n thế giới h&agrave;ng ng&agrave;y đang phải đấu tranh để gi&uacute;p c&ocirc;ng ty của họ vừa biết lại vừa l&agrave;m được những g&igrave; họ biết. Đ&acirc;y l&agrave; một cẩm nang thực tế, hữu &iacute;ch trong vấn đề n&acirc;ng cao hiệu quả hoạt động trong doanh nghiệp ng&agrave;y nay.</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><strong><span style="line-height: 24px; font-size: 12pt;">Th&ocirc;ng tin t&aacute;c giả:</span></strong></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">Jeffrey Pfeffer l&agrave; một nh&agrave; l&yacute; luận kinh doanh người Mỹ. &Ocirc;ng l&agrave; gi&aacute;o sư chuy&ecirc;n về H&agrave;nh vi tổ chức tại Trường Cao học Kinh doanh thuộc Đại học Stanford, v&agrave; được coi l&agrave; một trong những nh&agrave; tư tưởng c&oacute; ảnh hưởng s&acirc;u rộng nhất hiện nay.</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">Robert I. Sutton l&agrave; gi&aacute;o sư Khoa học v&agrave; Kỹ thuật Quản l&yacute; tại Đại học Stanford. Sutton đ&atilde; viết 5 quyển s&aacute;ch trong đ&oacute; c&oacute; &ldquo;The No A** hole Rule: Building a Civilized Workplace and Surviving One That Isn&rsquo;t&rdquo; nằm trong danh s&aacute;ch b&aacute;n chạy nhất của New York Times v&agrave; đ&atilde; gi&agrave;nh giải Quill Award d&agrave;nh cho s&aacute;ch kinh doanh hay nhất năm 2007.</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><strong><span style="line-height: 24px; font-size: 12pt;">Mời bạn đ&oacute;n đọc.</span></strong></span></p>', '1196khoang-cach-tu-noi-den-lam.jpg', '2013-12-17', 0),
(5, 1, 'Gian Truân Chỉ Là Thử Thách', 'Hồ Văn Trung', 'Thuận hóa – Firstnews', 10, 98000, 445, 'mềm', '10', '<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: center; line-height: 19.5px;" align="center"><span style="font-family: ''Times New Roman'';"><strong><span style="line-height: 24px; font-size: 12pt;">GIAN TRU&Acirc;N CHỈ L&Agrave; THỬ TH&Aacute;CH</span></strong></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: center; line-height: 19.5px;" align="center"><span style="font-family: ''Times New Roman'';"><strong><span style="line-height: 24px; font-size: 12pt;">T&aacute;c giả: Hồ Văn Trung</span></strong></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: center; line-height: 19.5px;" align="center"><span style="font-family: ''Times New Roman'';"><strong><span style="line-height: 24px; font-size: 12pt;">***</span></strong></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">Sinh ra v&agrave; lớn l&ecirc;n ở l&agrave;ng qu&ecirc;&nbsp;&nbsp;La Kh&ecirc;, x&atilde; Hương Vinh, Hương Tr&agrave;, Thừa Thi&ecirc;n Huế. Ngay từ khi vừa mới sinh ra, Hồ Văn Trung đ&atilde; sớm phải g&aacute;nh chịu nỗi mất m&aacute;t qu&aacute; lớn: buổi s&aacute;ng mẹ anh đau x&eacute; l&ograve;ng khi hay tin chồng mất th&igrave; buổi chiều mẹ đ&oacute;n nhận tiếng kh&oacute;c ch&agrave;o đời của anh. Chịu cảnh mồ c&ocirc;i ngay từ l&uacute;c lọt l&ograve;ng nhưng cuộc đời kh&ocirc;ng l&agrave;m Hồ Văn Trung ng&atilde; gục; m&agrave; ch&iacute;nh những kh&oacute; khăn, mất m&aacute;t đ&oacute; đ&atilde; t&ocirc;i r&egrave;n anh trở th&agrave;nh một doanh nh&acirc;n th&agrave;nh đạt như ng&agrave;y h&ocirc;m nay.</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">Qua những trang viết của m&igrave;nh, Hồ Văn Trung đưa người đọc về những ng&agrave;y gian kh&oacute; v&agrave; đầy nhọc nhằn nơi v&ugrave;ng qu&ecirc; La Kh&ecirc; m&ecirc;nh m&ocirc;ng ruộng đồng, s&ocirc;ng nước. Ở đ&oacute;, anh c&ugrave;ng người mẹ v&agrave; người chị g&aacute;i lớn hơn anh 5 tuổi sống c&ugrave;ng nhau trong một t&uacute;p lều dựng tạm, kh&ocirc;ng c&oacute; bất kỳ một t&agrave;i sản n&agrave;o, kể cả ruộng vườn v&agrave; tr&acirc;u b&ograve;. Điều qu&yacute; gi&aacute; nhất m&agrave; anh c&oacute;, l&agrave; tấm l&ograve;ng bao la của người mẹ ngh&egrave;o. V&igrave; muốn c&aacute;c con c&oacute; chữ nghĩa bằng bạn bằng b&egrave;, c&oacute; cuộc sống tốt đẹp hơn, người mẹ đ&oacute; đ&atilde; kh&ocirc;ng nề h&agrave; việc g&igrave;, từ d&atilde;i nắng dầm sương ngo&agrave;i đồng ruộng đến l&agrave;m thu&ecirc;, ở đợ cho người ta.</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">C&oacute; lẽ, sinh ra trong gia đ&igrave;nh kh&oacute; khăn, sớm chịu cảnh mồ c&ocirc;i n&ecirc;n &yacute; thức của anh về ho&agrave;n cảnh gia đ&igrave;nh cũng h&igrave;nh th&agrave;nh sớm hơn. Ngay từ nhỏ, Hồ Văn Trung đ&atilde; biết l&agrave;m việc để phụ gi&uacute;p mẹ v&agrave; chị, chấp nhận quần &aacute;o r&aacute;ch, bụng đ&oacute;i đến trường. Sau n&agrave;y anh viết: &ldquo;Ch&uacute;ng ta kh&ocirc;ng thể quyết định nơi m&igrave;nh sinh ra hay ho&agrave;n cảnh n&agrave;o sẽ nu&ocirc;i dưỡng m&igrave;nh. Sinh ra trong một ho&agrave;n cảnh &eacute;o le, lớn l&ecirc;n trong thiếu ăn, đ&oacute;i khổ nhưng hơn hết, t&ocirc;i lại thấy m&igrave;nh l&agrave; một đứa trẻ may mắn&rdquo;.</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">May mắn v&igrave; ch&iacute;nh ho&agrave;n cảnh kh&oacute; khăn, thiếu thốn đ&oacute; đ&atilde; tạo n&ecirc;n động lực để Hồ Văn Trung quyết t&acirc;m tho&aacute;t ra khỏi nghịch cảnh m&agrave; số phận ngỡ tưởng đ&atilde; an b&agrave;i cho anh. Anh nhận ra chỉ c&oacute; học vấn mới gi&uacute;p anh thực hiện quyết t&acirc;m đ&oacute;. Vốn l&agrave; một cậu b&eacute; th&ocirc;ng minh, s&aacute;ng dạ c&ocirc;ng th&ecirc;m quyết t&acirc;m tho&aacute;t ngh&egrave;o lu&ocirc;n s&ocirc;i sục trong l&ograve;ng, Hồ Văn Trung đ&atilde; xuất sắc trải qua c&aacute;c kỳ thi như đệ thất , t&uacute; t&agrave;i b&aacute;n, t&uacute; t&agrave;i to&agrave;n rồi v&agrave;o trường đại học m&agrave; l&uacute;c bấy giờ kh&ocirc;ng phải ai cũng l&agrave;m được điều đ&oacute;.</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">Học ở trường Đại học Huế được một năm, Hồ Văn Trung lại nung nấu &yacute; định v&agrave;o S&agrave;i G&ograve;n với một niềm tin rằng &ldquo;bầu trời kh&ocirc;ng thể m&atilde;i tối đen, phải đến l&uacute;c &aacute;nh mặt trời h&eacute; dạng&rdquo; v&agrave; &ldquo;ph&iacute;a cuối đường hầm sẽ c&oacute; &aacute;nh s&aacute;ng của hy vọng&rdquo;. &ldquo;Giấc mơ S&agrave;i G&ograve;n&rdquo; được anh l&yacute; giải: &ldquo;Đ&oacute; l&agrave; nơi chứa đựng bao thứ tươi đẹp m&agrave; một người trẻ như t&ocirc;i lu&ocirc;n khao kh&aacute;t đặt ch&acirc;n đến. L&agrave; mảnh đất phồn hoa, tr&ugrave; ph&uacute;, l&agrave; niềm hy vọng của biết bao nhi&ecirc;u người ở tỉnh lẻ&rdquo;.</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">Tự truyện&nbsp;<strong>Gian Tru&acirc;n Chỉ L&agrave; Thử Th&aacute;ch</strong>&nbsp;được Hồ Văn Trung kể với giọng điệu ch&acirc;n phương, mộc mạc. Anh vốn kh&ocirc;ng phải l&agrave; nh&agrave; văn n&ecirc;n c&oacute; cảm gi&aacute;c những c&acirc;u chuyện được kể bằng gan ruột v&agrave; bằng tấm l&ograve;ng của một người con đang tri &acirc;n với mảnh đất nơi m&igrave;nh đ&atilde; sinh ra.</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">Một trong những đặc điểm quan trọng l&agrave;m n&ecirc;n sức hấp dẫn cho t&aacute;c phẩm ở thể loại tự truyện ch&iacute;nh l&agrave; yếu tố sự thật. Ở tự truyện của m&igrave;nh, Hồ Văn Trung đ&atilde; trung th&agrave;nh gần như tuyệt đối với yếu tố n&agrave;y khi anh kể chi tiết về cuộc đời m&igrave;nh. Tr&ecirc;n nền sự thật đ&oacute;, người đọc c&agrave;ng kh&acirc;m phục hơn &yacute; ch&iacute; v&agrave; nghị lực của anh.</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">Kh&ocirc;ng &ldquo;tươi đẹp&rdquo; như h&igrave;nh dung, cuộc sống những ng&agrave;y đầu ở S&agrave;i G&ograve;n của Hồ Văn Trung lại l&agrave; chuỗi ng&agrave;y lang bạt với c&aacute;i bụng l&uacute;c n&agrave;o cũng trong t&igrave;nh trạng đ&oacute;i meo. Thậm ch&iacute; c&oacute; lần v&igrave; kh&ocirc;ng chịu nổi, anh đ&atilde; ngất xỉu trong nh&agrave; học tr&ograve; m&agrave; m&igrave;nh đang dạy k&egrave;m. Chật vật xoay xở ở S&agrave;i G&ograve;n, c&ugrave;ng với sự gi&uacute;p đỡ của một gia đ&igrave;nh c&ugrave;ng qu&ecirc;, Hồ Văn Trung đ&atilde; ổn định được cuộc sống của m&igrave;nh v&agrave; tiếp tục học ở trường Đại học Khoa học S&agrave;i G&ograve;n. Đ&acirc;y cũng ch&iacute;nh l&agrave; bước ngoặt dẫn anh v&agrave;o con đường mới, một định hướng mới với bao thăng trầm nổi tr&ocirc;i theo vận nước.</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">Những năm đầu thập ni&ecirc;n 70, khi t&igrave;nh h&igrave;nh trong nước đầy hỗn loạn, t&igrave;nh trạng chiến tranh ng&agrave;y c&agrave;ng khốc liệt, với tấm l&ograve;ng y&ecirc;u nước cũng như bao thanh ni&ecirc;n trong thời loạn ấy anh sinh vi&ecirc;n Hồ Văn Trung đ&atilde; dấn th&acirc;n v&agrave;o đấu tranh để mong t&igrave;m một niềm h&ograve;a b&igrave;nh cho đất nước, nhưng kh&ocirc;ng may cuộc đời tr&ocirc;i nổi của anh lại đẩy anh v&agrave;o t&ugrave; tội của cả hai chế độ cũ v&agrave; mớ. Kh&ocirc;ng cam phận, Hồ Văn Trung lại nung nấu &yacute; định đi về miền đất hứa để c&oacute; một tương lai tươi s&aacute;ng. Vượt qua những ng&agrave;y l&ecirc;nh đ&ecirc;nh tr&ecirc;n biển, c&oacute; l&uacute;c tưởng như đ&atilde; cận kề c&aacute;i chết khi gặp s&oacute;ng dữ, kh&ocirc;ng x&aacute;c định được phương hướng v&agrave; đặc biệt l&agrave; l&uacute;c gặp hải tặc. Nhưng vượt qua những gian nan v&agrave; nguy hiểm, cuối c&ugrave;ng anh v&agrave; người bạn đời của m&igrave;nh đ&atilde; được đặt ch&acirc;n l&ecirc;n nước &Uacute;c. Hai vợ chồng lại tiếp tục chuỗi ng&agrave;y vất vả mưu sinh với đầy rẫy thiếu thốn cả về vật chất lẫn tinh thần. Cũng c&oacute; l&uacute;c, anh v&agrave; người vợ của m&igrave;nh phải chịu cảnh tay trắng khi bị lừa hết t&agrave;i sản.</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">Đọc&nbsp;<strong>Gian Tru&acirc;n Chỉ L&agrave; Thử Th&aacute;ch</strong>, người đọc kh&ocirc;ng khỏi ngạc nhi&ecirc;n v&igrave; đ&acirc;u Hồ Văn Trung lại c&oacute; đủ sức mạnh, &yacute; ch&iacute; để vượt qua những thử th&aacute;ch, kh&oacute; khăn m&agrave; cuộc đời b&agrave;y ra cho anh. Để rồi nơi xứ người với hai b&agrave;n tay trắng, Hồ Văn Trung đ&atilde; tự kiến tạo cho m&igrave;nh một gia đ&igrave;nh hạnh ph&uacute;c c&ugrave;ng một sự nghiệp vững v&agrave;ng v&agrave; bề thế. Giờ đ&acirc;y, anh đ&atilde; l&agrave; &ocirc;ng chủ của tập đo&agrave;n Trangs c&oacute; trụ sở v&agrave; chi nh&aacute;nh khắp nơi tr&ecirc;n thế giới như: &Uacute;c, Mỹ, Anh, Việt Nam, Africa&hellip; Một điều đ&aacute;ng qu&yacute; ở Hồ Văn Trung, ấy l&agrave; khi đ&atilde; th&agrave;nh danh ở xứ người, th&igrave; anh vẫn kh&ocirc;ng qu&ecirc;n Việt Nam - nơi đ&atilde; cho anh d&aacute;ng v&oacute;c v&agrave; h&igrave;nh h&agrave;i. Anh trở về nước với lương t&acirc;m, với ho&agrave;i b&atilde;o để gi&uacute;p cải thiện đất nước tốt hơn, n&acirc;ng cao đời sống người d&acirc;n v&agrave; hy vọng họ sẽ tạo ra tr&ecirc;n qu&ecirc; hương m&igrave;nh những thay đổi t&iacute;ch cực.</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">Suy cho c&ugrave;ng, đ&uacute;ng như t&ecirc;n của tự truyện - gian tru&acirc;n cũng chỉ l&agrave; thử th&aacute;ch. Th&agrave;nh c&ocirc;ng sẽ đến với những ai biết đứng dậy, tự t&igrave;m con đường đi cho m&igrave;nh. Bởi đ&uacute;ng như Hồ Văn Trung viết: &ldquo;T&ocirc;i tin rằng cuộc đời c&oacute; hai chữ `số phận` v&agrave; `cơ hội`. Nhưng số phận v&agrave; cơ hội tốt chỉ mở ra với những người biết tự định đoạt cuộc sống của m&igrave;nh, biết phấn đấu, học hỏi kh&ocirc;ng ngừng để vươn l&ecirc;n. Kh&ocirc;ng ai c&oacute; cuộc đời ho&agrave;n hảo, kh&ocirc;ng ai c&oacute; thể chọn nơi m&igrave;nh sinh ra, nhưng ch&uacute;ng ta ho&agrave;n to&agrave;n c&oacute; thể quyết định cuộc đời m&igrave;nh. Đừng bao giờ nản ch&iacute;! Đừng bao giờ bu&ocirc;ng xu&ocirc;i! Đừng bao giờ tuyệt vọng! Đừng bao giờ ngừng cố gắng v&agrave; hy vọng v&igrave; chắc chắn th&agrave;nh c&ocirc;ng sẽ đến với những ai biết tạo ra cơ hội cho ch&iacute;nh m&igrave;nh&rdquo;.</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">Tự truyện&nbsp;<strong>Gian Tru&acirc;n Chỉ L&agrave; Thử Th&aacute;ch</strong>&nbsp;của Hồ Văn Trung được xem như l&agrave; một tiếng l&ograve;ng lu&ocirc;n tha thiết với qu&ecirc; hương, bản qu&aacute;n, với những người th&acirc;n y&ecirc;u; đ&atilde; trở th&agrave;nh h&agrave;nh trang gi&uacute;p t&aacute;c giả vượt qua những ch&ocirc;ng gai, thử th&aacute;ch của cuộc đời. Cuốn s&aacute;ch c&oacute; &iacute;ch cho nhiều người, nhất l&agrave; đối với thế hệ trẻ muốn giữ g&igrave;n, nu&ocirc;i dưỡng, vun đắp ước vọng vượt kh&oacute;, vượt khổ để vươn tới th&agrave;nh c&ocirc;ng v&igrave; bản th&acirc;n, gia đ&igrave;nh v&agrave; phụng sự x&atilde; hội.</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><strong><span style="line-height: 24px; font-size: 12pt;">Mời bạn đ&oacute;n đọc.</span></strong></span></p>', '7421gian-truan-chi-la-thu-thach.jpg', '2013-12-17', 0);
INSERT INTO `sach` (`sach_id`, `dmuc_id`, `sach_name`, `tacgia`, `nxb`, `soluong`, `dongia`, `sotrang`, `loaibia`, `giamgia`, `gioithieu`, `hinhanh`, `post_date`, `luot_mua`) VALUES
(6, 1, 'Lãnh Đạo Đích Thực', 'Bill George', 'Thời Đại - Dtbooks', 45, 110000, 344, 'cứng', '15', '<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: center; line-height: 19.5px;" align="center"><span style="font-family: ''Times New Roman'';"><strong><span style="line-height: 24px; font-size: 12pt;">L&Atilde;NH ĐẠO Đ&Iacute;CH THỰC</span></strong></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: center; line-height: 19.5px;" align="center"><span style="font-family: ''Times New Roman'';"><strong><span style="line-height: 24px; font-size: 12pt;">T&aacute;c giả: Bill George</span></strong></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: center; line-height: 19.5px;" align="center"><span style="font-family: ''Times New Roman'';"><strong><span style="line-height: 24px; font-size: 12pt;">***</span></strong></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">Bill George - Gi&aacute;o sư về khoa học l&atilde;nh đạo của ĐH Harvard, đồng thời từng l&agrave; một trong những nh&agrave; l&atilde;nh đạo đặc biệt th&agrave;nh c&ocirc;ng tại Hoa Kỳ, đ&atilde; viết trong cuốn s&aacute;ch n&agrave;y những đ&uacute;c kết từ cuộc đời l&atilde;nh đạo hơn 30 năm của &ocirc;ng về b&iacute; quyết quan trọng để trở th&agrave;nh một nh&agrave; l&atilde;nh đạo đ&iacute;ch thực v&agrave; x&acirc;y dựng c&ocirc;ng ty ph&aacute;t triển bền vững.</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">Cuốn s&aacute;ch được Bill George viết ra cho một thế hệ l&atilde;nh đạo mới, tức l&agrave; bao gồm từ những CEO vừa được bổ nhiệm cho đến những sinh vi&ecirc;n đang muốn trở th&agrave;nh l&atilde;nh đạo trong tương lai, v&agrave; tất cả những nh&agrave; l&atilde;nh đạo nằm ở giữa hai cực n&oacute;i tr&ecirc;n &ndash; những người đang phải chuẩn bị nhận th&ecirc;m những tr&aacute;ch nhiệm mới. Th&ocirc;ng điệp của cuốn s&aacute;ch n&agrave;y n&oacute;i th&igrave; dễ m&agrave; l&agrave;m th&igrave; kh&ocirc;ng dễ ch&uacute;t n&agrave;o: ch&uacute;ng ta cần những nh&agrave; l&atilde;nh đạo đ&iacute;ch thực (authentic leaders) để điều h&agrave;nh c&aacute;c tổ chức, những nh&agrave; l&atilde;nh đạo cam kết cao với việc quản l&yacute; c&aacute;c t&agrave;i sản của tổ chức v&agrave; việc tạo ra những kh&aacute;c biệt trong cuộc sống của những người m&agrave; họ phục vụ.</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">Vậy nh&agrave; l&atilde;nh đạo đ&iacute;ch thực l&agrave; g&igrave;? Theo Bill Geogre, khi c&aacute;c nh&agrave; l&atilde;nh đạo l&agrave; những người phụng sự tận tụy, l&atilde;nh đạo một c&aacute;ch đ&iacute;ch thực v&agrave; ch&acirc;n th&agrave;nh, th&igrave; họ sẽ x&acirc;y dựng được những tổ chức bền vững, những tổ chức tạo ra điều tốt v&agrave; lợi &iacute;ch cho con người, tạo ra sự kh&aacute;c biệt to lớn tr&ecirc;n thế giới. V&agrave; c&oacute; một c&aacute;ch tốt hơn để dẫn dắt c&ocirc;ng ty &ndash; c&aacute;ch thức x&acirc;y dựng một tổ chức bền vững v&agrave; l&agrave;m lợi cho mọi chủ thể li&ecirc;n quan, kh&aacute;ch h&agrave;ng, người lao động v&agrave; cổ đ&ocirc;ng. Điều đ&oacute; kh&ocirc;ng c&oacute; nghĩa l&agrave; nh&agrave; l&atilde;nh đạo đ&iacute;ch thực sẽ kh&ocirc;ng được tưởng thưởng cho những nỗ lực của m&igrave;nh. Ngược lại, phần thưởng của họ sẽ vượt xa hơn những phần thưởng t&agrave;i ch&iacute;nh th&ocirc;ng thường, m&agrave; trở th&agrave;nh sự thỏa m&atilde;n khi tạo ra sự kh&aacute;c biệt trong cuộc sống của kh&aacute;ch h&agrave;ng, tạo ra cơ hội cho nh&acirc;n vi&ecirc;n, tạo ra lợi nhuận cao trong d&agrave;i hạn cho c&aacute;c cổ đ&ocirc;ng. Đồng thời, nh&agrave; l&atilde;nh đạo đ&iacute;ch thực cũng c&oacute; một cuộc sống ch&iacute;nh trực v&agrave; c&acirc;n bằng giữa gia đ&igrave;nh v&agrave; c&ocirc;ng việc. Ch&acirc;n dung nh&agrave; l&atilde;nh đạo đ&iacute;ch thực được Bill George dựng l&ecirc;n kh&aacute;c nhiều so với h&igrave;nh ảnh những nh&agrave; l&atilde;nh đạo m&agrave; trước đ&acirc;y ch&uacute;ng ta từng biết: chỉ biết sống với c&ocirc;ng việc; v&agrave; &ocirc;ng đ&atilde; chứng minh kiểu l&atilde;nh đạo cũ đ&oacute; kh&ocirc;ng mang lại hiệu quả l&acirc;u d&agrave;i.</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">Cuốn s&aacute;ch cũng b&agrave;n về quan hệ giữa nh&agrave; l&atilde;nh đạo v&agrave; tổ chức m&agrave; họ dẫn dắt, qua đ&oacute; thể hiện c&aacute;ch thức nh&agrave; l&atilde;nh đạo ph&aacute;t triển bản th&acirc;n từ mối tương t&aacute;c với tổ chức, từ đ&oacute; tạo ra những c&ocirc;ng ty đ&iacute;ch thực. Những tổ chức như vậy sẽ c&oacute; t&iacute;nh cạnh tranh rất cao tr&ecirc;n thị trường v&agrave; sẽ ph&aacute;t triển bền vững.</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">Qua những minh họa, ph&acirc;n t&iacute;ch thực tế v&agrave; s&acirc;u sắc từ c&aacute;c tập đo&agrave;n lớn tr&ecirc;n thế giới đồng thời từ ch&iacute;nh h&agrave;nh tr&igrave;nh l&atilde;nh đạo của Bill George, đặc biệt l&agrave; thời gian &ocirc;ng l&agrave;m tại Medtronic &ndash; một trong những tập đo&agrave;n c&ocirc;ng nghệ y tế h&agrave;ng đầu thế giới &ndash; người đọc được tiếp thu nhiều b&agrave;i học qu&yacute; gi&aacute; về thuật l&atilde;nh đạo đ&iacute;ch thực gi&uacute;p ph&aacute;t triển vai tr&ograve; l&atilde;nh đạo của c&aacute; nh&acirc;n, c&aacute;ch thức để x&acirc;y dựng một c&ocirc;ng ty ph&aacute;t triển bền vững, vượt qua những th&aacute;ch thức thường gặp tr&ecirc;n thương trường. C&oacute; &yacute; kiến cho rằng cuốn s&aacute;ch n&agrave;y xứng đ&aacute;ng l&agrave; một Tuy&ecirc;n ng&ocirc;n về Tinh thần v&agrave; Gi&aacute; trị của Thuật l&atilde;nh đạo, c&oacute; thể gi&uacute;p &iacute;ch cho giới l&atilde;nh đạo n&oacute;i chung, kh&ocirc;ng chỉ với c&aacute;c nh&agrave; l&atilde;nh đạo kinh doanh m&agrave; c&ograve;n với c&aacute;c nh&agrave; l&atilde;nh đạo thuộc mọi loại h&igrave;nh tổ chức kh&aacute;c.</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><strong><span style="line-height: 24px; font-size: 12pt;">Th&ocirc;ng tin t&aacute;c giả</span></strong></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">Bill George hiện l&agrave; Gi&aacute;o sư về khoa học l&atilde;nh đạo v&agrave; kiểm so&aacute;t quản trị tại Trường Kinh doanh Harvard thuộc Đại học Harvard, Trường Quản l&yacute; thuộc Đại học Yale v&agrave; Trường IMD (Lausanne, Thụy Sĩ). &Ocirc;ng cũng tham gia HĐQT c&aacute;c tập đo&agrave;n như Goldman Sachs, Novartis v&agrave; Target Corporation.</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">&Ocirc;ng từng l&agrave; Tổng Gi&aacute;m đốc Điều h&agrave;nh (CEO) của Medtronic, một tập đo&agrave;n c&ocirc;ng nghệ y tế h&agrave;ng đầu thế giới, từ năm 1991 đến 2001, đồng thời l&agrave; Chủ tịch HĐQT Medtronic từ 1996 đến 2002. &Ocirc;ng cũng từng tham gia điều h&agrave;nh tại c&aacute;c c&ocirc;ng ty Honeywell v&agrave; Litton Industries, từng l&agrave;m việc trong Bộ Quốc ph&ograve;ng Mỹ.</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">&Ocirc;ng từng được Học viện Quản trị (Academy of Management) vinh danh l&agrave; &ldquo;Nh&agrave; điều h&agrave;nh của năm &ndash; 2001&rdquo;, Hiệp hội quốc gia c&aacute;c th&agrave;nh vi&ecirc;n HĐQT doanh nghiệp (National Association of Corporate Directors) b&igrave;nh chọn l&agrave; &ldquo;Th&agrave;nh vi&ecirc;n HĐQT của năm &ndash; 2001/2002&rdquo;.</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">Ngo&agrave;i ra, &ocirc;ng cũng l&agrave; th&agrave;nh vi&ecirc;n HĐQT của c&aacute;c tổ chức kh&aacute;c như Hội Chữ thập đỏ Hoa Kỳ, Quỹ Carnegie v&igrave; H&ograve;a b&igrave;nh Quốc tế, Trường Kinh doanh Harvard, Học viện Nghệ thuật Minneapolis, từng l&agrave;m Chủ tịch HĐQT của Allina Health System, Abbott-Northwestern Hospital, United Way of Greater Twin Cities, Advamed. Vợ chồng Bill George c&ugrave;ng nhau th&agrave;nh lập Quỹ Gia đ&igrave;nh George (George Family Foundation).</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">Hiện &ocirc;ng tập trung nghi&ecirc;n cứu về khoa học l&atilde;nh đạo, kiểm so&aacute;t quản trị tập đo&agrave;n, đồng thời tham gia đ&agrave;o tạo thế hệ l&atilde;nh đạo mới. C&aacute;c b&agrave;i viết của Bill George từng được đăng tr&ecirc;n c&aacute;c tạp ch&iacute; Harvard Business Review, Corporate Boards, Directors and Boards, Corporate Director, Wharton Leadership Digest.</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><strong><span style="line-height: 24px; font-size: 12pt;">Những lời nhận x&eacute;t về L&atilde;nh đạo đ&iacute;ch thực</span></strong></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">&ldquo;Bất kỳ ai muốn dẫn dắt một c&ocirc;ng ty đều n&ecirc;n đọc cuốn s&aacute;ch n&agrave;y. T&aacute;c phẩm m&ocirc; tả những g&igrave; nh&agrave; l&atilde;nh đạo phải l&agrave;m v&agrave; phải trở th&agrave;nh &ndash; một qu&aacute; tr&igrave;nh kh&ocirc;ng dễ d&agrave;ng nhưng khi ho&agrave;n th&agrave;nh th&igrave; sẽ khiến bạn v&ocirc; c&ugrave;ng thỏa m&atilde;n! Với việc sử dụng kh&aacute;i niệm Sứ mệnh, Bill George đ&atilde; dẫn dắt c&ocirc;ng ty của &ocirc;ng một c&aacute;ch rất r&otilde; r&agrave;ng v&agrave; cởi mở. Con người n&agrave;y l&agrave; một trong những nh&agrave; l&atilde;nh đạo tốt nhất tr&ecirc;n thế giới. H&atilde;y học hỏi từ &ocirc;ng ấy.&rdquo;</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: right; line-height: 19.5px;" align="right"><span style="font-family: ''Times New Roman'';"><strong><span style="line-height: 24px; font-size: 12pt;">- Earl E. Bakken, Nh&agrave; s&aacute;ng lập v&agrave; Gi&aacute;m đốc danh dự, Medtronic, Inc.</span></strong></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">&ldquo;L&atilde;nh đạo đ&iacute;ch thực cho ta một khu&ocirc;n mẫu ho&agrave;n hảo cho đề t&agrave;i l&atilde;nh đạo trong thế kỷ XXI. Trong tương lai, c&aacute;c c&ocirc;ng ty phải vừa tốt vừa vĩ đại. C&aacute;c c&ocirc;ng ty phải cạnh tranh nhiều hơn trong m&ocirc;i trường to&agrave;n cầu... đồng thời vẫn tạo ra một m&ocirc;i trường hướng đến kh&aacute;ch h&agrave;ng, coi trọng nh&acirc;n vi&ecirc;n, t&igrave;m kiếm sự t&iacute;n nhiệm từ nh&agrave; đầu tư. L&atilde;nh đạo đ&iacute;ch thực mi&ecirc;u tả những thay đổi m&agrave; nh&agrave; l&atilde;nh đạo phải thực hiện trong ho&agrave;n cảnh hiện thời. T&ocirc;i từng c&oacute; dịp học hỏi từ Bill George trong một số c&ocirc;ng việc kinh doanh chung, v&agrave; giờ đ&acirc;y Bill cung cấp cho độc giả một cơ hội để hưởng lợi từ tr&iacute; tuệ của &ocirc;ng.&rdquo;</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: right; line-height: 19.5px;" align="right"><span style="font-family: ''Times New Roman'';"><strong><span style="line-height: 24px; font-size: 12pt;">- Jeffrey Immelt, Chủ tịch v&agrave; CEO, General Electric</span></strong></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">&ldquo;Bất kỳ ai quan t&acirc;m đến c&aacute;ch trở th&agrave;nh nh&agrave; l&atilde;nh đạo hiệu quả đều n&ecirc;n t&igrave;m gặp Bill George, nguy&ecirc;n CEO của một trong những tập đo&agrave;n tốt nhất nước Mỹ &ndash; Medtronic. C&aacute;ch kế tiếp để n&acirc;ng cao kỹ năng l&atilde;nh đạo ch&iacute;nh l&agrave; đọc t&aacute;c phẩm rất mạch lạc, r&otilde; r&agrave;ng m&agrave; h&ugrave;ng hồn n&agrave;y của &ocirc;ng &ndash; L&atilde;nh đạo đ&iacute;ch thực. Đ&acirc;y l&agrave; cuốn cẩm nang tốt về những &yacute; tưởng l&atilde;nh đạo m&agrave; ai cũng c&oacute; thể dễ d&agrave;ng &aacute;p dụng.&rdquo;</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: right; line-height: 19.5px;" align="right"><span style="font-family: ''Times New Roman'';"><strong><span style="line-height: 24px; font-size: 12pt;">- Arthur Levitt, cựu Chủ tịch Ủy ban Chứng kho&aacute;n Mỹ</span></strong></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">&ldquo;L&atilde;nh đạo đ&iacute;ch thực đưa ra một th&ocirc;ng điệp cực kỳ quan trọng về kiểu l&atilde;nh đạo m&agrave; ch&uacute;ng ta cần c&oacute; để t&aacute;i tạo l&ograve;ng tin v&agrave;o giới doanh nghiệp Mỹ. Cuốn s&aacute;ch tr&agrave;n ngập c&aacute;c &yacute; tưởng, dựa tr&ecirc;n sự nghiệp th&agrave;nh c&ocirc;ng của Bill George trong c&aacute;c vai tr&ograve; CEO v&agrave; Chủ tịch Medtronic, th&agrave;nh vi&ecirc;n Hội đồng quản trị của một số tập đo&agrave;n h&agrave;ng đầu kh&aacute;c. Kh&aacute;c với dạng hồi k&yacute; thi&ecirc;n về c&aacute;i t&ocirc;i của một số nh&agrave; l&atilde;nh đạo kinh doanh, s&aacute;ch của Bill George kh&ocirc;ng chỉ cho độc giả biết về c&aacute;c phẩm chất của nh&agrave; l&atilde;nh đạo, m&agrave; c&ograve;n về sự th&ocirc;ng tuệ &ndash; phẩm chất hiếm c&oacute; v&agrave; đ&aacute;ng qu&yacute; nhất. Đ&acirc;y l&agrave; cuốn s&aacute;ch phải đọc cho mọi nh&agrave; l&atilde;nh đạo kinh doanh hiện nay, cũng như những ai c&oacute; kh&aacute;t khao trở th&agrave;nh l&atilde;nh đạo kinh doanh.&rdquo;</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: right; line-height: 19.5px;" align="right"><span style="font-family: ''Times New Roman'';"><strong><span style="line-height: 24px; font-size: 12pt;">- Jay Lorsch, Gi&aacute;o sư Trường Kinh doanh Harvard, t&aacute;c giả cuốn s&aacute;ch Aligning the Stars</span></strong></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">&ldquo;L&atilde;nh đạo đ&iacute;ch thực l&agrave; lời k&ecirc;u gọi đanh th&eacute;p về việc cần c&oacute; sự l&atilde;nh đạo đ&iacute;ch thực v&agrave; đạo đức trong giới doanh nghiệp, được minh chứng qua ch&iacute;nh sự nghiệp xuất sắc của t&aacute;c giả. Cuốn s&aacute;ch n&agrave;y thuyết giảng về ch&iacute;nh những g&igrave; m&agrave; t&aacute;c giả Bill George đ&atilde; thực h&agrave;nh trong suốt sự nghiệp l&acirc;u d&agrave;i v&agrave; th&agrave;nh c&ocirc;ng của &ocirc;ng.&rdquo;</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: right; line-height: 19.5px;" align="right"><span style="font-family: ''Times New Roman'';"><strong><span style="line-height: 24px; font-size: 12pt;">- Walter Mondale, cựu Ph&oacute; Tổng thống Mỹ</span></strong></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><strong><span style="line-height: 24px; font-size: 12pt;">Mời bạn đ&oacute;n đọc.</span></strong></span></p>', '3547lanh-dao-dich-thuc.jpg', '2013-12-17', 0),
(7, 2, 'Thiên Thần Nhỏ Của Tôi', 'Nguyễn Nhật Ánh', 'Trẻ', 25, 38000, 132, 'cứng', '10', '<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><strong><span style="line-height: 26px; color: red; font-size: 13pt;">Thi&ecirc;n Thần Nhỏ Của T&ocirc;i</span></strong></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">Khi dọn về ng&ocirc;i nh&agrave; mới, Kha c&oacute; một c&ocirc; bạn ngh&egrave;o sống trong ng&otilde; hẻm gần đ&oacute;. Khu vườn v&agrave; ng&ocirc;i biệt thự m&agrave; cha mẹ Kha được cấp kh&ocirc;ng ngờ l&agrave; nơi quen thuộc của Hồng Hoa, cứ như c&ocirc; ấy đ&atilde; gắn b&oacute; với n&oacute; từ l&acirc;u rồi. V&igrave; thế, d&ugrave; bị chủ nh&agrave; mới ngăn cấm, c&ocirc; vẫn l&eacute;n l&uacute;t t&igrave;m v&agrave;o khu vườn, để rồi gặp phải tai nạn... V&agrave;&hellip;</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><strong><span style="line-height: 24px; font-size: 12pt;">Mời bạn đ&oacute;n đọc.</span></strong></span></p>', '4059thien-than-nho-cua-toi.jpg', '2013-12-17', 0),
(8, 2, 'Còn Chút Gì Để Nhớ', 'Nguyễn Nhật Ánh', 'Trẻ', 20, 53000, 210, 'mềm', '10', '<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><strong><span style="line-height: 26px; color: red; font-size: 13pt;">C&ograve;n Ch&uacute;t G&igrave; Để Nhớ</span></strong></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">Đ&oacute; l&agrave; những kỷ niệm thời đi học của Chương, l&uacute;c mới bước ch&acirc;n v&agrave;o S&agrave;i G&ograve;n v&agrave; l&agrave;m quen với cuộc sống đ&ocirc; thị. L&agrave; những mối quan hệ bạn b&egrave; tưởng chừng hời hợt thoảng qua nhưng gắn b&oacute; suốt cuộc đời. Cuộc sống đầy biến động đ&atilde; x&ocirc; dạt mỗi người mỗi nơi, nhưng trải qua h&agrave;ng mấy chục năm, những kỷ niệm ấy vẫn lu&ocirc;n n&iacute;u k&eacute;o Chương về với một thời để nhớ.</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><strong><span style="line-height: 24px; font-size: 12pt;">Mời bạn đ&oacute;n đọc.</span></strong></span></p>', '3278con-chut-gi-de-nho.jpg', '2013-12-17', 0),
(9, 2, 'Kỳ Nghỉ Hè Tươi Đẹp', 'Dương Chí Thành', 'Hội Nhà Văn - First News', 40, 50000, 224, 'mềm', '10', '<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: center; line-height: 19.5px;" align="center"><span style="font-family: ''Times New Roman'';"><strong><span style="line-height: 24px; color: #404040; font-size: 12pt;">KỲ NGHỈ H&Egrave; TƯƠI ĐẸP</span></strong></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: center; line-height: 19.5px;" align="center"><span style="font-family: ''Times New Roman'';"><strong><span style="line-height: 24px; color: #404040; font-size: 12pt;">T&aacute;c giả: Dương Ch&iacute; Th&agrave;nh</span></strong></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: center; line-height: 19.5px;" align="center"><span style="font-family: ''Times New Roman'';"><strong><span style="line-height: 24px; color: #404040; font-size: 12pt;">***</span></strong></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><strong><span style="line-height: 24px; font-size: 12pt;">Kỳ Nghỉ H&egrave; Tươi Đẹp</span></strong><span style="line-height: 24px; font-size: 12pt;">&nbsp;l&agrave; l&aacute;t cắt nhỏ về cuộc sống của một c&ocirc; b&eacute; mười hai tuổi nhưng lại chứa đựng những triết l&yacute; nhẹ nh&agrave;ng v&agrave; s&acirc;u sắc. Suy nghĩ v&agrave; h&agrave;nh động của Minh Mẫn sẽ khiến ch&uacute;ng ta nh&igrave;n lại cuộc sống v&agrave; bản th&acirc;n m&igrave;nh.</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">M&ugrave;a h&egrave; về trong tiếng ve r&acirc;m ran, c&aacute;i nắng v&agrave;ng ươm, trong những t&aacute;n l&aacute; xanh v&agrave; trong những cơn mưa đầu m&ugrave;a m&aacute;t l&agrave;nh. M&ugrave;a h&egrave; gợi mở những kh&ocirc;ng gian bao la để ta h&ograve;a m&igrave;nh v&agrave;o thanh &acirc;m v&agrave; sắc m&agrave;u tuyệt vời của cuộc sống.</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">Đối với những c&ocirc; cậu học tr&ograve;, m&ugrave;a h&egrave; bắt đầu khi tiếng trống trường cuối c&ugrave;ng vang l&ecirc;n. M&ugrave;a h&egrave; đầy nắng, đầy gi&oacute;, chứa đựng nhiều dự định v&agrave; hứa hẹn những chuyến đi xa để được kh&aacute;m ph&aacute; bao điều hay ho. Nhưng m&ugrave;a h&egrave; trong bước ch&acirc;n chầm chậm của c&ocirc; b&eacute; Minh M&atilde;n khi bước ra khỏi cổng trường trở th&agrave;nh một nỗi &aacute;m ảnh.</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">M&ugrave;a h&egrave; ư? C&oacute; g&igrave; chờ đợi kh&ocirc;ng?</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">Tưởng chừng m&ugrave;a h&egrave; của c&ocirc; b&eacute; sẽ nhạt nhẽo tr&ocirc;i qua c&ugrave;ng ch&uacute; c&aacute; "Mắt to". Nhưng kh&ocirc;ng, một dịp t&igrave;nh cờ đ&atilde; đưa Minh Mẫn đến với viện dưỡng l&atilde;o Nh&acirc;n Thiện trong vai tr&ograve; của một t&igrave;nh nguyện vi&ecirc;n. Tại đ&acirc;y, c&ocirc; b&eacute; đ&atilde; c&oacute; những th&aacute;ng ng&agrave;y đ&aacute;ng nhớ, được tiếp x&uacute;c với những con người kỳ lạ v&agrave; c&ugrave;ng chia sẻ với họ nhiều cung bậc cảm x&uacute;c. "Triết l&yacute; &ocirc; mai" v&agrave; c&acirc;u chuyện của những cụ gi&agrave; ở viện đ&atilde; gi&uacute;p Minh Mẫn thấu hiểu được nhiều lẽ nh&acirc;n sinh ở đời.</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">Một t&igrave;nh bạn trong s&aacute;ng!</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">Những trải nghiệm kh&oacute; qu&ecirc;n!</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">Những b&agrave;i học s&acirc;u sắc!</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">Tất cả đ&atilde; mang đến cho c&ocirc; b&eacute; một kỳ h&egrave; tươi đẹp v&agrave; trọn vẹn.</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><strong><span style="line-height: 24px; font-size: 12pt;">Kỳ Nghỉ H&egrave; Tươi Đẹp</span></strong><span style="line-height: 24px; font-size: 12pt;">&nbsp;l&agrave; l&aacute;t cắt nhỏ về cuộc sống của một c&ocirc; b&eacute; mười hai tuổi nhưng lại chứa đựng những triết l&yacute; nhẹ nh&agrave;ng v&agrave; s&acirc;u sắc. Suy nghĩ v&agrave; h&agrave;nh động của Minh Mẫn sẽ khiến ch&uacute;ng ta nh&igrave;n lại cuộc sống v&agrave; bản th&acirc;n m&igrave;nh.</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">H&atilde;y đọc v&agrave; thấu cảm!</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">Tr&ecirc;n hết, h&atilde;y tin rằng "triết l&yacute; &ocirc; mai" sẽ theo ta đến suốt cuộc đời!</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><strong><span style="line-height: 24px; font-size: 12pt;">Mời bạn đ&oacute;n đọc.</span></strong></span></p>', '4837ky-nghi-he-tuoi-dep.jpg', '2013-12-17', 0),
(10, 2, 'Nhật Ký Say Nắng Của Cassie', 'Fiona Foden', 'Trẻ', 50, 65000, 224, 'mềm', '10', '<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: center; line-height: 19.5px;" align="center"><span style="font-family: ''Times New Roman'';"><strong><span style="line-height: 24px; font-size: 12pt;">NHẬT K&Yacute; SAY NẮNG CỦA CASSIE</span></strong></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: center; line-height: 19.5px;" align="center"><span style="font-family: ''Times New Roman'';"><strong><span style="line-height: 24px; font-size: 12pt;">T&aacute;c giả: Fiona Foden</span></strong></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: center; line-height: 19.5px;" align="center"><span style="font-family: ''Times New Roman'';"><strong><span style="line-height: 24px; font-size: 12pt;">***</span></strong></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><strong><span style="line-height: 24px; font-size: 12pt;">Nhật K&yacute; Say Nắng Của Cassie&nbsp;</span></strong><span style="line-height: 24px; font-size: 12pt;">l&agrave; c&acirc;u chuyện<strong>&nbsp;</strong>xoay quanh c&ocirc; b&eacute;&nbsp;<strong>Cassie&nbsp;</strong>với những rắc rối về những vấn đề t&acirc;m sinh l&yacute; trong lứa tuổi dậy th&igrave;. C&acirc;u chuyện được thể hiện dưới dạng nhật k&yacute; c&aacute; nh&acirc;n, từ đ&oacute; dễ d&agrave;ng bộc lộ t&acirc;m tư, t&igrave;nh cảm của<strong>Cassie</strong>&nbsp;qua ng&ocirc;n từ ng&acirc;y thơ, nh&iacute; nhảnh, đ&aacute;ng y&ecirc;u.</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">Th&ocirc;ng qua những c&acirc;u chuyện diễn ra h&agrave;ng ng&agrave;y trong cuộc sống như ph&aacute;t hiện cơ thể ph&aacute;t triển bất c&acirc;n xứng, lo lắng về sự thay đổi sinh l&yacute;, c&aacute;c mối quan hệ trong trường lớp, để &yacute; một bạn trai mới chuyển trường, những rung động t&igrave;nh cảm đầu ti&ecirc;n&hellip; t&aacute;c giả lồng v&agrave;o những th&ocirc;ng điệp gi&aacute;o dục đơn giản nhẹ nh&agrave;ng, đ&ocirc;i khi c&aacute;ch xử l&yacute; t&igrave;nh huống rất h&oacute;m hỉnh, vui vẻ, gi&uacute;p c&aacute;c bạn vượt qua độ tuổi nhiều thay đổi một c&aacute;ch thoải m&aacute;i v&agrave; tự tin.</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">Lối dẫn truyện chủ quan, h&agrave;i hước; phong c&aacute;ch viết h&oacute;m hỉnh; ng&ocirc;n từ hiện đại được sử dụng kh&eacute;o l&eacute;o, ph&ugrave; hợp, kh&ocirc;ng g&acirc;y phản cảm hay cứng nhắc về vấn đề dậy th&igrave; ở tuổi mới lớn.</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">S&aacute;ch mang t&iacute;nh giải tr&iacute; cao.</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">S&aacute;ch&nbsp;<strong>Nhật K&yacute; Say Nắng Của Cassie&nbsp;</strong>d&agrave;nh cho bạn đọc nữ trong giai đoạn dậy th&igrave; từ 12 - 17 tuổi, với những hoang mang, lo lắng vu vơ về sức khỏe giới t&iacute;nh v&agrave; t&igrave;nh cảm.</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><strong><span style="line-height: 24px; font-size: 12pt;">Mời bạn đ&oacute;n đọc.</span></strong></span></p>', '1730nhat-ki-say-nang-cua-cassie.jpg', '2013-12-17', 0),
(11, 2, 'Lớp Học Siêu Quậy Lớp Học Siêu Quậy', 'Butchivacucgom', 'Văn học - Văn Việt', 42, 58000, 270, 'mềm', '10', '<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: center; line-height: 19.5px;" align="center"><span style="font-family: ''Times New Roman'';"><strong><span style="line-height: 24px; font-size: 12pt;">LỚP HỌC SI&Ecirc;U QUẬY</span></strong></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: center; line-height: 19.5px;" align="center"><span style="font-family: ''Times New Roman'';"><strong><span style="line-height: 24px; font-size: 12pt;">T&aacute;c giả: Butchivacucgom</span></strong></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: center; line-height: 19.5px;" align="center"><span style="font-family: ''Times New Roman'';"><strong><span style="line-height: 24px; font-size: 12pt;">***</span></strong></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><strong><span style="line-height: 24px; font-size: 12pt;">Lớp Học Si&ecirc;u Quậy&nbsp;</span></strong><span style="line-height: 24px; font-size: 12pt;">l&agrave; một<strong>&nbsp;</strong>c&acirc;u chuyện h&agrave;i hước xung quanh&nbsp;<strong>Minh</strong>&nbsp;&ndash; c&ocirc; nh&oacute;c học sinh từ nước ngo&agrave;i trở về, v&agrave;o học tại &ldquo;lớp học qu&aacute;i dị&rdquo; với t&ecirc;n lớp trưởng c&oacute; biệt danh&nbsp;<strong>Đầu T&ocirc;m</strong>. Mặc d&ugrave; quậy ph&aacute; nổi tiếng to&agrave;n trường nhưng lớp học n&agrave;y lại ho&agrave;n to&agrave;n c&oacute; thể đứng nhất về th&agrave;nh th&iacute;ch học tập (nếu ch&uacute;ng muốn), nhưng để ch&uacute;ng chịu ngồi y&ecirc;n, kh&ocirc;ng trốn tiết bỏ học tập thể l&agrave; cả 1 vấn đề với&nbsp;<strong>Minh</strong>&nbsp;khi c&ocirc; g&aacute;i quyết t&acirc;m lập lại trật tự kỷ cương để gi&uacute;p thầy gi&aacute;o chủ nhiệm cũng l&agrave; cậu ruột của c&ocirc;. C&aacute;c pha nghịch ngợm, chi&ecirc;u tr&ograve; đối ph&oacute; với thầy c&ocirc;, bạn b&egrave; biến lớp học như 1 rạp xiếc, ng&agrave;y n&agrave;o tới trường cũng l&agrave; 1 ng&agrave;y vui với c&aacute;c c&ocirc; cậu nhất quỷ nh&igrave; ma thứ ba học tr&ograve;. B&ecirc;n cạnh l&agrave; mối t&igrave;nh bọ x&iacute;t l&iacute;t nh&iacute;t giữa&nbsp;<strong>Heo</strong>&nbsp;(Minh) v&agrave;&nbsp;<strong>Đầu T&ocirc;m</strong>&nbsp;(Lớp trưởng) cũng rất thu h&uacute;t, h&agrave;i hước, nhiều phen lận đận cười ra nước mắt.</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">Bạn đ&atilde; bao giờ l&agrave;m th&agrave;nh vi&ecirc;n của một lớp l&agrave; chuy&ecirc;n gia cũng những tr&ograve; nghịch ph&aacute; khiến thầy c&ocirc; to&agrave;n trường lắc đầu ngao ng&aacute;n?</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">Bạn đ&atilde; bao giờ c&ugrave;ng cả lớp bắt tay v&agrave;o c&ocirc;ng cuộc gi&uacute;p thầy chủ nhiệm chinh phục tr&aacute;i tim của c&ocirc; gi&aacute;o sẽ l&agrave; vợ thầy chủ nhi&ecirc;m trong lương lai?</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">Bạn đ&atilde; bao giờ tim đập rộn r&atilde; trong l&ograve;ng ngực nhưng ngo&agrave;i mặt vẫn lạnh tanh, miệng th&igrave; đấu khẩu kh&ocirc;ng ngừng với người đ&oacute;?</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">Bạn đ&atilde; bao giờ v&igrave; c&acirc;u n&oacute;i của một người m&agrave; l&agrave;m mặt lạnh, khiến phe con g&aacute;i v&agrave; con trai trong lớp tr&aacute;nh n&eacute; kh&ocirc;ng đ&ugrave;a giỡn với nhau?&hellip;</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">Tất cả c&acirc;u trả lời đều nằm trong cuốn&nbsp;<strong>Lớp Học Si&ecirc;u Quậy&nbsp;</strong>n&agrave;y bạn nh&eacute;!</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">T&ocirc;i c&ograve;n trẻ, nhưng t&ocirc;i đ&atilde; sống hơn thực tại nhiều lần, kh&ocirc;ng d&aacute;m nhận l&agrave; từng trải, nhưng t&ocirc;i tin nếu buồn bạn sẽ kh&oacute;c, nếu vui bạn sẽ cười, khi nghe những c&acirc;u chuyện n&agrave;y.</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><strong><span style="line-height: 24px; font-size: 12pt;">Mời bạn đ&oacute;n đọc.</span></strong></span></p>', '8616lop-hoc-sieu-quay.jpg', '2013-12-17', 0),
(12, 2, 'Chuỗi Hạt Azoth', 'Phan Hồn Nhiên', 'Kim Đồng', 40, 63000, 244, 'mềm', '10', '<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><strong><span style="line-height: 26px; color: red; font-size: 13pt;">Chuỗi Hạt Azoth</span></strong></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">Tiếp nối th&agrave;nh c&ocirc;ng của tiểu thuyết k&igrave; ảo &ldquo;Những đ&ocirc;i mắt lạnh&rdquo;, &ldquo;Chuỗi hạt Azoth&rdquo; l&agrave; cuốn tiếp theo cũng thuộc d&ograve;ng fantasy của Phan Hồn Nhi&ecirc;n vừa ra mắt bạn đọc. Nội dung k&igrave; b&iacute;, c&ugrave;ng h&igrave;nh thức s&aacute;ch đẹp mắt với 32 trang minh họa m&agrave;u đ&iacute;nh k&egrave;m được vẽ rất c&ocirc;ng phu bởi họa sĩ Phan Vũ Linh, sẽ mang đến cho độc giả những trải nghiệm mới mẻ khi đọc một t&aacute;c phẩm d&agrave;nh cho teen.</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">G&acirc;y hồi hộp ngay từ những trang đầu ti&ecirc;n, ng&ograve;i b&uacute;t đi&ecirc;u luyện của t&aacute;c giả đ&atilde; thu h&uacute;t người đọc dấn s&acirc;u v&agrave;o những trạng th&aacute;i t&acirc;m l&iacute; căng thẳng của B&igrave;nh Nguy&ecirc;n, c&ocirc; g&aacute;i c&oacute; vẻ ngo&agrave;i nhỏ b&eacute; nh&uacute;t nh&aacute;t vừa gia nhập một ng&ocirc;i trường nội tr&uacute; đặc biệt d&agrave;nh cho giới qu&yacute; tộc, để rồi từng bước kh&aacute;m ph&aacute; v&ocirc; số b&iacute; mật nguy hiểm khuất sau c&aacute;nh cổng gothic ấy, kể cả những tội &aacute;c gh&ecirc; khiếp b&agrave;y ra bởi những nh&acirc;n dạng c&oacute; vẻ ngo&agrave;i xinh đẹp.</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">Kh&ocirc;ng chỉ vậy, &ldquo;Chuỗi hạt Azoth&rdquo; c&ograve;n gửi đến bạn những khoảnh khắc rung động tinh tế của mối t&igrave;nh đầu giữa hai nh&acirc;n vật B&igrave;nh Nguy&ecirc;n v&agrave; Danny, ch&agrave;ng trai b&iacute; ẩn đang phải giằng co giữa 2 phần thiện-&aacute;c trong ch&iacute;nh bản th&acirc;n m&igrave;nh.</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">Bằng h&agrave;nh tr&igrave;nh đi t&igrave;m chuỗi hạt quyền lực - Azoth, cuộc tranh gi&agrave;nh quyết liệt để chiếm đoạt sức mạnh tối thượng của n&oacute;, đ&atilde; cuốn hầu hết nh&acirc;n vật v&agrave;o một thế giới tăm tối, khiến họ ng&agrave;y một phải đối diện với nỗi sợ h&atilde;i, th&oacute;i tham lam cuồng bạo, cũng như bộc lộ to&agrave;n bộ phẩm chất hoặc tham vọng ch&iacute;nh m&igrave;nh. Để rồi, khi t&agrave;n cuộc, họ biết đằng sau chuỗi hạt ấy, l&agrave; ẩn &yacute; về cuộc sống, t&igrave;nh bạn v&agrave; t&igrave;nh y&ecirc;u qu&yacute; gi&aacute; thế n&agrave;o, thứ m&agrave; kh&ocirc;ng &iacute;t người trong số họ đ&atilde; ho&agrave;n to&agrave;n đ&aacute;nh mất cơ hội chạm tới.</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><strong><span style="line-height: 24px; font-size: 12pt;">Mời bạn đ&oacute;n đọc.</span></strong></span></p>', '8193chuoi-hat-azoth.jpg', '2013-12-17', 0);
INSERT INTO `sach` (`sach_id`, `dmuc_id`, `sach_name`, `tacgia`, `nxb`, `soluong`, `dongia`, `sotrang`, `loaibia`, `giamgia`, `gioithieu`, `hinhanh`, `post_date`, `luot_mua`) VALUES
(13, 2, 'Cô Nàng Pizza', 'Karhryn Williams', 'Phụ Nữ', 52, 82000, 332, 'mềm', '15', '<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><strong><span style="line-height: 26px; color: red; font-size: 13pt;">C&ocirc; N&agrave;ng Pizza</span></strong></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">L&agrave; một cuốn tiểu thuyết d&agrave;nh cho tuổi teen,&nbsp;<strong>Nhật k&iacute; Pizza</strong>&nbsp;hứa hẹn sẽ mang đến cho giới trẻ một l&agrave;n gi&oacute; mới khi từng trang s&aacute;ch ẩn chứa v&ocirc; v&agrave;n b&iacute; k&iacute;p nấu nướng đặc biệt hấp dẫn từ khắp c&aacute;c nền văn ho&aacute; ẩm thực tr&ecirc;n thế giới. V&agrave; tr&ecirc;n hết, với văn phong hiện đại, trẻ trung, h&agrave;i hước lồng v&agrave;o c&aacute;c t&igrave;nh tiết gay cấn v&agrave; l&atilde;ng mạn đến m&ecirc; hồn cuốn s&aacute;ch sẽ khiến c&aacute;c bạn kh&ocirc;ng thể rời mắt đến tận trang cuối c&ugrave;ng.</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: center; line-height: 19.5px;" align="center"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">&hellip;</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">Bầu kh&ocirc;ng kh&iacute; s&ocirc;i động trong căn bếp nh&agrave; h&agrave;ng Taverna Ristorante đ&atilde; thổi b&ugrave;ng l&ecirc;n niềm đam m&ecirc; nấu nướng cho c&ocirc; bạn Sophie Nicolaides từ khi c&ograve;n rất nhỏ. Nhưng đối với Sophie, căn bếp nhỏ bận rộn, c&aacute;c c&ocirc;ng thức nấu ăn loang lổ vết dầu mỡ v&agrave; hương vị quyến rũ của m&oacute;n &Yacute; truyền thống dường như vẫn c&ograve;n qu&aacute; &iacute;t ỏi so với những g&igrave; c&ocirc; mơ ước. Liệu c&ocirc; n&agrave;ng c&oacute; chịu rời bỏ c&ocirc;ng việc bếp n&uacute;c hằng ng&agrave;y để l&agrave;m một điều lớn lao hơn? C&oacute; lẽ Sophie sẽ vẫn l&agrave; người-thừa-kế-ở-th&igrave;-tương-lai của Taverna Ristorante nếu Alex - cậu bạn th&acirc;n từ thời &ldquo;truổng cời&rdquo; kh&ocirc;ng qu&aacute; ng&aacute;n ngẩm c&aacute;i t&iacute;nh ba phải nửa m&ugrave;a của c&ocirc; n&agrave;ng m&agrave; tự &yacute; đăng k&yacute; tham gia chương tr&igrave;nh T&igrave;m kiếm đầu bếp tuổi teen, một chương tr&igrave;nh gameshow đ&igrave;nh đ&aacute;m khắp nước Mĩ.</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">Vượt qua khoảng c&aacute;ch địa l&yacute; xa x&ocirc;i v&agrave; cả những trở ngại t&acirc;m l&yacute; ban đầu, Sophie đ&atilde; đặt ch&acirc;n đến Thung lũng Napa - khu th&aacute;nh địa ẩm thực m&agrave; bất cứ đầu bếp nghiệp dư n&agrave;o cũng mơ một lần được đến, để thể hiện khả năng nấu nướng của bản th&acirc;n, để gặp gỡ c&aacute;c đối thủ ki&ecirc;m những người bạn c&ugrave;ng ch&iacute; hướng&hellip; Nhưng Sophie phải trả gi&aacute; những g&igrave; để trở th&agrave;nh một hot girl? Liệu c&ocirc; n&agrave;ng tuổi teen của ch&uacute;ng ta c&oacute; muốn sống thật với bản th&acirc;n, sống thật với những rung cảm đầu đời, với gia đ&igrave;nh c&ugrave;ng những người bạn thật sự? V&agrave; liệu chương tr&igrave;nh truyền h&igrave;nh thực tế m&agrave; Sophie tham gia c&oacute; thật sự &ldquo;thực tế&rdquo; như t&ecirc;n gọi của n&oacute;?</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><strong><span style="line-height: 24px; font-size: 12pt;">Th&ocirc;ng tin t&aacute;c giả</span></strong></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">Kathryn Williams l&agrave; một nh&agrave; văn trẻ chuy&ecirc;n viết cho tuổi teen với một số t&aacute;c phẩm nổi bật như: The Debutante (C&ocirc; n&agrave;ng mới lớn), The lost Summer (M&ugrave;a h&egrave; đ&atilde; mất), Roomies: Sharing your home with friends, strangers and total freaks (Bạn c&ugrave;ng ph&ograve;ng: C&aacute;ch chung sống với bạn b&egrave;, người lạ v&agrave; những thay đổi), hay Camp Rock: The Junior Novel (Trại Camp Rock: Chuyện t&igrave;nh cuối cấp). Hiện tại, Kathryn Williams đang sống tại Mĩ, nhưng kh&ocirc;ng cố định ở một nơi n&agrave;o: Tennessee, New York, Maine v&agrave; cả Virginia.</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">Ngo&agrave;i nghiệp s&aacute;ng t&aacute;c, t&aacute;c giả người Mĩ n&agrave;y c&ograve;n l&agrave; một bi&ecirc;n tập vi&ecirc;n tự do cho c&ocirc;ng ty truyền th&ocirc;ng Sideshow Media. Kathryn đặt niềm tin kh&aacute; lớn v&agrave;o &ldquo;sức mạnh&rdquo; của bơ, thịt muối... phải chăng đ&acirc;y l&agrave; l&yacute; do khiến c&ocirc; d&agrave;y c&ocirc;ng t&igrave;m hiểu c&aacute;c c&ocirc;ng thức, c&aacute;c m&oacute;n ăn để tạo ra một Nhật k&iacute; Pizza hấp dẫn đến thế.</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><strong><span style="line-height: 24px; font-size: 12pt;">Mời bạn đ&oacute;n đọc.</span></strong></span></p>', '4027co-nang-pizza.jpg', '2013-12-17', 0),
(14, 3, 'Từ Điển Những Bài Viết Tiếng Anh Trình Độ Nâng Cao Và Đại Học', 'Nguyễn Thành Yến', 'Tổng hợp TP. HCM - NTV', 36, 148000, 796, 'mềm', '15', '<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 18px; text-align: justify;"><span style="line-height: 18.399999618530273px; font-size: 12pt;"><span style="font-family: ''Times New Roman'';">Viết b&agrave;i tiểu luận tiếng Anh l&agrave; một m&ocirc;n thi bắt buộc đối với c&aacute;c sinh vi&ecirc;n khoa tiếng Anh, th&iacute; sinh dự thi TOEFL, những người thi tuyển v&agrave;o hệ cao học... Đ&acirc;y cũng l&agrave; m&ocirc;n thi khiến nhiều người đau đầu.</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 18px; text-align: justify;"><span style="line-height: 18.399999618530273px; font-size: 12pt;"><span style="font-family: ''Times New Roman'';">Cuốn s&aacute;ch&nbsp;<strong>Từ Điển Những B&agrave;i Viết Tiếng Anh Tr&igrave;nh Độ N&acirc;ng Cao V&agrave; Đại Học</strong>&nbsp;thu thập những b&agrave;i luận mẫu để bạn tham khảo. Những b&agrave;i luận mẫu thực tế n&agrave;y được thu thập qua c&aacute;c kỳ thi viết luận c&oacute; mức độ ph&ugrave; hợp, nội dung đa dạng, li&ecirc;n quan đến những vấn đề x&atilde; hội chung của tất cả c&aacute;c quốc gia. V&igrave; vậy, khả năng c&aacute;c bạn sẽ gặp những đề b&agrave;i c&oacute; nội dung tương tự trong c&aacute;c kỳ thi l&agrave; rất cao. Cho d&ugrave; c&aacute;c bạn kh&ocirc;ng gặp những đề b&agrave;i quen thuộc, nhưng qua phần gợi &yacute;, nội dung v&agrave; c&aacute;c mẫu c&acirc;u được tr&igrave;nh b&agrave;y trong cuốn s&aacute;ch n&agrave;y, c&aacute;c bạn c&oacute; thể linh hoạt &aacute;p dụng ch&uacute;ng v&agrave;o b&agrave;i luận của m&igrave;nh</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 18px; text-align: justify;"><strong><span style="line-height: 18.399999618530273px; font-size: 12pt;"><span style="font-family: ''Times New Roman'';">Mời bạn đ&oacute;n đọc.</span></span></strong></p>', '3553tu-dien-nhung-bai-viet-tieng-anh.jpg', '2013-12-18', 0),
(15, 3, '900 Mẫu Câu Đàm Thoại Thực Dụng Trong Thương Mại Và Kinh Doanh', 'Lê Huy Lâm', 'Tổng hợp TP. HCM - NTV', 20, 83000, 445, 'cứng', '0', '<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 18px; text-align: justify;"><span style="line-height: 18.399999618530273px; font-size: 12pt;"><span style="font-family: ''Times New Roman'';">Cuốn s&aacute;ch&nbsp;<strong>900 Mẫu C&acirc;u Đ&agrave;m Thoại Thực Dụng Trong Thương Mại V&agrave; Kinh Doanh&nbsp;</strong>sẽ giới thiệu đến bạn đọc những nội dung ch&iacute;nh sau:</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 18px; text-align: justify;"><span style="line-height: 18.399999618530273px; font-size: 12pt;"><span style="font-family: ''Times New Roman'';">Phần 1: Tiếng Anh thương mại</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 18px; text-align: justify;"><span style="line-height: 18.399999618530273px; font-size: 12pt;"><span style="font-family: ''Times New Roman'';">Phần 2: Tiếng Anh kinh doanh căn bản</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 18px; text-align: justify;"><span style="line-height: 18.399999618530273px; font-size: 12pt;"><span style="font-family: ''Times New Roman'';">Phần 3: Tiếng Anh kinh doanh thực dụng</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 18px; text-align: justify;"><span style="line-height: 18.399999618530273px; font-size: 12pt;"><span style="font-family: ''Times New Roman'';">Hy vọng cuốn s&aacute;ch n&agrave;y sẽ gi&uacute;p bạn được nhiều điều trong c&ocirc;ng việc.</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 18px; text-align: justify;"><strong><span style="line-height: 18.399999618530273px; font-size: 12pt;"><span style="font-family: ''Times New Roman'';">Mời bạn đ&oacute;n đọc.</span></span></strong></p>', '900.jpg', '2013-12-18', 0),
