(18, 3, 'Tiếng Anh Dành Cho Người Xuất Cảnh', 'Nguyễn Trung Tánh', 'Tổng hợp TP. HCM - NTV', 55, 57000, 196, 'mềm', '5', '<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 18px; text-align: justify;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 18.399999618530273px; font-size: 12pt;">Cuốn s&aacute;ch&nbsp;</span><span style="line-height: 18.399999618530273px; font-size: 12pt;"><strong>Tiếng Anh D&agrave;nh Cho Người Xuất Cảnh</strong>&nbsp;</span><span style="line-height: 18.399999618530273px; font-size: 12pt;">sẽ gi&uacute;p bạn đọc c&oacute; nhu cầu xuất cảnh chuẩn bị c&aacute;c kỹ năng tiếng Anh cần thiết cho chuyến đi của m&igrave;nh.</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 18px; text-align: justify;"><strong><span style="line-height: 18.399999618530273px; font-size: 12pt;"><span style="font-family: ''Times New Roman'';">Mời bạn đ&oacute;n đọc.</span></span></strong></p>', 'cah.jpg', '2013-12-18', 0),
(19, 4, 'Luật Hấp Dẫn - Bí Mật Tối Cao', 'Som Sujeera', 'Lao động xã hội - Thái Hà', 42, 59000, 216, 'mềm', '10', '<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: center; line-height: 19.5px;" align="center"><span style="font-family: ''Times New Roman'';"><strong><span style="line-height: 24px; font-size: 12pt;">LUẬT HẤP DẪN - B&Iacute; MẬT TỐI CAO</span></strong></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: center; line-height: 19.5px;" align="center"><span style="font-family: ''Times New Roman'';"><strong><span style="line-height: 24px; font-size: 12pt;">T&aacute;c giả: Som Sujeera</span></strong></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: center; line-height: 19.5px;" align="center"><span style="font-family: ''Times New Roman'';"><strong><span style="line-height: 24px; font-size: 12pt;">***</span></strong></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">Sự tiến h&oacute;a của lo&agrave;i người mới chỉ k&eacute;o d&agrave;i hơn hai trăm ngh&igrave;n năm trong khi hai lo&agrave;i chim v&agrave; b&ograve; s&aacute;t đ&atilde; ph&aacute;t triển hơn một triệu năm. So với ch&uacute;ng, lo&agrave;i người chỉ l&agrave; một giống lo&agrave;i non trẻ. Tuy nhi&ecirc;n, lo&agrave;i người đ&atilde; tiến h&oacute;a rất nhanh v&agrave; c&oacute; những bước ph&aacute;t triển vượt trội bởi họ c&oacute; khả năng nhận thức về bản th&acirc;n c&ugrave;ng khả năng ph&aacute;t triển nhận thức v&agrave; th&uacute;c đẩy những hiểu biết r&otilde; r&agrave;ng hơn về bản chất của sự vật v&agrave; hiện tượng xung quanh. Họ lu&ocirc;n cố gắng t&igrave;m kiếm cho m&igrave;nh con đường hướng tới th&agrave;nh c&ocirc;ng v&agrave; hướng tới sự giải tho&aacute;t khỏi mọi nỗi đau.</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><strong><span style="line-height: 24px; font-size: 12pt;">Luật hấp dẫn &ndash; B&iacute; mật tối cao</span></strong><span style="line-height: 24px; font-size: 12pt;">&nbsp;&nbsp;mang lại cho bạn sự giải th&iacute;ch r&otilde; r&agrave;ng về sự t&aacute;c động của&nbsp;<strong>Luật Hấp dẫn</strong>&nbsp;dựa tr&ecirc;n tư tưởng của t&acirc;m l&yacute; học hiện đại v&agrave; sự thấu hiểu b&ecirc;n trong về tinh thần c&oacute; thể đạt được qua những hiểu biết về lời răn của đạo Phật. Th&ocirc;ng qua&nbsp;<strong>Luật Hấp dẫn</strong>, những kết quả nghi&ecirc;n cứu mang t&iacute;nh khoa học v&agrave; bằng c&aacute;ch luyện tập nhận thức khoảnh khắc hiện tại bạn sẽ hiểu tư duy của m&igrave;nh tạo ra thực tại như thế n&agrave;o v&agrave; l&agrave;m sao bạn c&oacute; thể sử dụng sức mạnh của những hiểu biết n&agrave;y để l&agrave;m cho cuộc sống của m&igrave;nh trở n&ecirc;n hạnh ph&uacute;c v&agrave; &yacute; nghĩa hơn. Cụ thể hơn, cuốn s&aacute;ch sẽ nh&igrave;n nhận vai tr&ograve; của cảm x&uacute;c trong việc định h&igrave;nh thực tế. Để đạt được tới th&agrave;nh c&ocirc;ng th&igrave; con người cần trải qua một qu&aacute; tr&igrave;nh s&aacute;ng tạo ho&agrave;n chỉnh gồm ba bước: y&ecirc;u cầu, tin tưởng v&agrave; đ&oacute;n nhận &ndash; gửi th&ocirc;ng điệp tới thế giới về những mong muốn của m&igrave;nh, tin tưởng chắc chắn rằng m&igrave;nh sẽ đạt được mơ ước đ&oacute; v&agrave; đ&oacute;n nhận kết quả tốt đẹp để c&oacute; thể c&oacute; được một cuộc sống tốt đẹp, &yacute; nghĩa hơn v&agrave; tho&aacute;t khỏi mọi nỗi đau.&nbsp;&nbsp;</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><strong><span style="line-height: 24px; font-size: 12pt;">Mời bạn đ&oacute;n đọc.</span></strong></span></p>', 'luat.jpg', '2013-12-18', 0),
(20, 4, 'Xóa Hết Dấu Vết Trước Khi Về Nhà', 'Đức Long', 'Văn học - Limbooks', 48, 75000, 276, 'cứng', '15', '<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: center; line-height: 19.5px;" align="center"><span style="font-family: ''Times New Roman'';"><strong><span style="line-height: 24px; font-size: 12pt;">X&Oacute;A HẾT DẤU VẾT TRƯỚC KHI VỀ NH&Agrave;</span></strong></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: center; line-height: 19.5px;" align="center"><span style="font-family: ''Times New Roman'';"><strong><span style="line-height: 24px; font-size: 12pt;">T&aacute;c giả: Đức Long</span></strong></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: center; line-height: 19.5px;" align="center"><span style="font-family: ''Times New Roman'';"><strong><span style="line-height: 24px; font-size: 12pt;">***</span></strong></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><strong><span style="line-height: 24px; font-size: 12pt;">&ldquo;Tất cả nh&acirc;n vật trong cuốn s&aacute;ch n&agrave;y đều c&oacute; thật, trừ một nh&acirc;n vật được hư cấu. Ch&iacute;nh l&agrave; T&Ocirc;I&rdquo;</span></strong></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: right; line-height: 19.5px;" align="right"><span style="font-family: ''Times New Roman'';"><strong><span style="line-height: 24px; font-size: 12pt;">- Đức Long</span></strong></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><strong><span style="line-height: 24px; font-size: 12pt;">Phụ nữ phải đọc để biết c&aacute;ch cầm cương đ&agrave;n &ocirc;ng</span></strong></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><strong><span style="line-height: 24px; font-size: 12pt;">Đ&agrave;n &ocirc;ng cần đọc để được Tự do</span></strong></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-size: 12pt; line-height: 24px; font-family: ''Times New Roman'';">Ai cũng biết, phụ nữ v&agrave; đ&agrave;n &ocirc;ng c&oacute; đến một vạn t&aacute;m ng&agrave;n điểm kh&aacute;c biệt. Trong đấy c&oacute; đến một vạn bảy ng&agrave;n bảy trăm bảy ch&iacute;n điểm rơi v&agrave;o hệ thần kinh.</span>&nbsp;</p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">Thật ra chỉ cần một điểm ở hệ thần kinh kh&aacute;c nhau đ&atilde; đủ để tạo ra sự kh&aacute;c biệt về c&aacute;ch h&agrave;nh xử, nữa l&agrave; hơn một vạn bảy điểm. Thế n&ecirc;n người ta mới bảo, phụ nữ v&agrave; đ&agrave;n &ocirc;ng d&ugrave; c&oacute; sống với nhau đến hai ng&agrave;n năm cũng kh&ocirc;ng thể n&agrave;o hiểu được nhau ho&agrave;n to&agrave;n.</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">Nhưng c&oacute; khi, ch&iacute;nh v&igrave; kh&ocirc;ng hiểu nổi nhau n&ecirc;n họ mới sống được c&ugrave;ng nhau hai ng&agrave;n năm. Ngược lại chỉ hai năm l&agrave; ch&aacute;n ph&egrave;.</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">Cuốn s&aacute;ch n&agrave;y ch&iacute;nh l&agrave; n&oacute;i về c&aacute;i sự kh&ocirc;ng hiểu (hoặc kh&ocirc;ng muốn hiểu) ấy.</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">&ldquo;<strong>X&oacute;a hết dấu vết trước khi về nh&agrave;</strong>&rdquo; ho&agrave;n to&agrave;n kh&ocirc;ng phải l&agrave; một cuốn s&aacute;ch t&acirc;m l&yacute; về đ&agrave;n &ocirc;ng v&agrave; phụ nữ. C&aacute;c b&agrave;i viết trong đ&oacute; b&agrave;y tỏ g&oacute;c nh&igrave;n h&agrave;i hước của t&aacute;c giả về c&aacute;ch h&agrave;nh xử của phụ nữ v&agrave; giải th&iacute;ch theo c&aacute;ch của ri&ecirc;ng m&igrave;nh. Tất cả những th&oacute;i quen cư xử của phụ nữ được viết qua c&aacute;c c&acirc;u chuyện d&iacute; dỏm, kh&ocirc;ng triết l&yacute; nhưng kh&aacute; nhiều th&ocirc;ng điệp khiến bạn đọc t&acirc;m đắc. Hoặc về những điều &ldquo;kh&oacute; chịu một c&aacute;ch đ&aacute;ng y&ecirc;u&rdquo; của phụ nữ:</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">&ldquo;Phụ nữ t&ograve; m&ograve; muồn biết anh người y&ecirc;u của m&igrave;nh c&oacute; y&ecirc;u m&igrave;nh thật kh&ocirc;ng, y&ecirc;u m&igrave;nh như thế n&agrave;o, y&ecirc;u s&acirc;u đậm hay hời hợt, c&oacute; y&ecirc;u mọi l&uacute;c mọi nơi kh&ocirc;ng, c&oacute; y&ecirc;u đủ để vượt qua cả l&uacute;c bận bịu kh&ocirc;ng, c&oacute; y&ecirc;u đủ để vẫn ngọt ng&agrave;o với m&igrave;nh ngay cả khi anh ta vừa bị sếp đ&igrave; kh&ocirc;ng. Hơn nữa, c&oacute; đủ y&ecirc;u để vượt qua t&igrave;nh huống rất dễ mất ki&ecirc;n nhẫn v&igrave; bị hỏi đi hỏi lại một c&acirc;u cũ m&egrave;m kh&ocirc;ng. V&igrave; l&agrave; c&oacute; thế n&agrave;y m&agrave; đ&atilde; c&aacute;u, kh&ocirc;ng biết chừng lấy nhau rồi th&igrave; ăn đ&aacute;. Bởi một khi đ&atilde; kết h&ocirc;n, phụ nữ c&ograve;n t&ograve; m&ograve; hơn nữa, hỏi nhiều hơn nữa&rdquo;. (Anh c&oacute; y&ecirc;u em kh&ocirc;ng?)</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">&ldquo;N&ecirc;n hai tiếng &ldquo;Đừng anh!&rdquo; sẽ vẫn l&agrave; một thứ vũ kh&iacute; tốt trong giai đoạn t&igrave;nh y&ecirc;u l&ecirc;n đến cao tr&agrave;o. N&oacute; gi&uacute;p phụ nữ bớt ngượng ng&ugrave;ng, lại gi&uacute;p đ&agrave;n &ocirc;ng tiến dần theo từng bước. N&oacute; giống như một account đ&atilde; để ngỏ password, ho&agrave;n to&agrave;n chỉ c&ograve;n cần dựa v&agrave;o hơi thở để nhấn n&uacute;t login&rdquo;. (L&rsquo;amour platonique v&agrave; triết l&iacute; &ldquo;Đừng anh&rdquo;)</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">Hoặc khẳng định bản chất tử tế của đ&agrave;n &ocirc;ng:</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">&ldquo;Đ&agrave;n &ocirc;ng lu&ocirc;n th&iacute;ch c&aacute;c c&ocirc; g&aacute;i sống tho&aacute;ng m&agrave; kh&ocirc;ng dễ d&atilde;i, v&igrave; nhờ thế họ c&oacute; mục đ&iacute;ch để theo đuổi. D&ugrave; g&igrave;, tuyệt đại đa số đ&agrave;n &ocirc;ng cũng l&agrave; những sinh vật tử tế, họ mơ ước &ocirc;m ấp c&aacute;i đ&iacute;ch ấy, nhưng kh&ocirc;ng bao giờ đ&aacute;nh đổi bằng mọi gi&aacute;.&rdquo; (Đường đến chiếc giường)</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">&ldquo;<strong>X&oacute;a hết dấu vết trước khi về nh&agrave;</strong>&rdquo; sẽ l&agrave; một m&oacute;n qu&agrave; th&uacute; vị, n&oacute; gi&uacute;p bạn l&agrave;m tăng th&ecirc;m sự...kh&ocirc;ng hiểu nhau giữa hai phe (để c&oacute; thể sống c&ugrave;ng nhau đến hai ng&agrave;n năm), nhưng ngược lại, cuốn s&aacute;ch n&agrave;y cũng gi&uacute;p bạn biết rằng c&aacute;i sự kh&ocirc;ng hiểu nhau ấy ch&iacute;nh l&agrave; cục nam ch&acirc;m tr&aacute;i dấu, kết nối bền chặt hai tr&aacute;i tim lại với nhau.</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">V&igrave; thế, h&atilde;y lu&ocirc;n vui vẻ tận hưởng. Cho d&ugrave; c&oacute; ai đ&oacute; phải &ldquo;<strong>x&oacute;a hết dấu vết trước khi về nh&agrave;</strong>&rdquo;.</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><strong><span style="line-height: 24px; font-size: 12pt;">Mời bạn đ&oacute;n đọc.</span></strong></span></p>', 'da.jpg', '2013-12-18', 0),
(21, 4, 'Điều Vĩ Đại Đời Thường', 'Robin Sharma', 'Trẻ', 44, 73000, 430, 'mềm', '15', '<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: center; line-height: 19.5px;" align="center"><span style="font-family: ''Times New Roman'';"><strong><span style="line-height: 24px; font-size: 12pt;">ĐIỀU VĨ ĐẠI ĐỜI THƯỜNG</span></strong></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: center; line-height: 19.5px;" align="center"><span style="font-family: ''Times New Roman'';"><strong><span style="line-height: 24px; font-size: 12pt;">T&aacute;c giả: Robin Sharma</span></strong></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: center; line-height: 19.5px;" align="center"><span style="font-family: ''Times New Roman'';"><strong><span style="line-height: 24px; font-size: 12pt;">***</span></strong></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">H&agrave;nh tr&igrave;nh trở n&ecirc;n vĩ đại khởi đầu từ những điều thật giản dị v&agrave; gần gũi, cũng giống như h&agrave;nh tr&igrave;nh vạn dặm khởi đầu từ một bước ch&acirc;n giản đơn. 101 c&acirc;u chuyện v&agrave; &yacute; tưởng của t&aacute;c giả trong quyển s&aacute;ch n&agrave;y ch&iacute;nh l&agrave; 101 b&agrave;i học minh chứng cho triết l&yacute; đơn sơ ấy.</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">Kh&ocirc;ng c&oacute; cuộc sống n&agrave;o l&agrave; ho&agrave;n hảo, tất cả ch&uacute;ng ta phải đối mặt với nhiều thử th&aacute;ch, từ đơn giản đến nghi&ecirc;m trọng&hellip; Nhưng ch&uacute;ng ta đều c&oacute; quyền lựa chọn vượt l&ecirc;n mọi nghịch cảnh để vươn tới vị tr&iacute; cao nhất v&agrave; tốt nhất của cuộc sống.</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><strong><span style="line-height: 24px; font-size: 12pt;">Mời bạn đ&oacute;n đọc.</span></strong></span></p>', 'dar.jpg', '2013-12-18', 0),
(22, 4, 'Sống Sáng Suốt', 'Dean Cunningham', 'Trẻ', 30, 65000, 442, 'mềm', '10', '<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: center; line-height: 19.5px;" align="center"><span style="font-family: ''Times New Roman'';"><strong><span style="line-height: 24px; font-size: 12pt;">SỐNG S&Aacute;NG SU&Ocirc;T</span></strong></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: center; line-height: 19.5px;" align="center"><span style="font-family: ''Times New Roman'';"><strong><span style="line-height: 24px; font-size: 12pt;">T&aacute;c giả: Dean Cunningham</span></strong></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: center; line-height: 19.5px;" align="center"><span style="font-family: ''Times New Roman'';"><strong><span style="line-height: 24px; font-size: 12pt;">***</span></strong></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">Hầu hết ai cũng muốn &ldquo;c&oacute;&rdquo; một thứ g&igrave; đ&oacute; nhiều hơn (nhiều tiền hơn, danh vọng cao hơn, nhiều quyền lực hơn, địa vị tốt hơn), để &lsquo;l&agrave;m&rsquo; một điều g&igrave; đ&oacute; (mua xe xịn hơn, nh&agrave; to hơn, th&ecirc;m một ng&ocirc;i nh&agrave; nữa), nhờ đ&oacute; họ c&oacute; thể &lsquo;l&agrave;&rsquo; một thứ g&igrave; đ&oacute; (hạnh ph&uacute;c, th&agrave;nh đạt, được y&ecirc;u). Nhưng để sống tốt, thật ra &lsquo;bạn l&agrave; g&igrave;&rdquo; quan trọng hơn &lsquo;bạn l&agrave;m g&igrave;.&rsquo;</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">Đ&oacute; l&agrave; l&yacute; do cuốn s&aacute;ch n&agrave;y được chia l&agrave;m ba phần. (Th&aacute;i Độ Đ&uacute;ng, H&agrave;nh Xử Đ&uacute;ng v&agrave; Hiểu Biết Đ&uacute;ng), mỗi phần bao gồm hai mươi chương về th&aacute;i độ, t&iacute;nh c&aacute;ch v&agrave; h&agrave;nh vi cần thiết để chuyển h&oacute;a cuộc sống h&agrave;ng ng&agrave;y.</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">Cuốn s&aacute;ch đơn sơ nhưng s&acirc;u sắc n&agrave;y gi&uacute;p ta nh&igrave;n cuộc đời theo chiều hướng kh&aacute;c. N&oacute; cho ta thấy những sự việc vốn thường bị xem nhẹ lại l&agrave; những điều tạo ra kh&aacute;c biệt trong sự trải nghiệm thế giới, v&agrave; n&oacute; sử dụng c&aacute;c kiến thức đ&oacute; để gi&uacute;p ch&uacute;ng ta sống một cuộc đời tốt đẹp hơn v&agrave; mỹ m&atilde;n hơn.</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><strong><span style="line-height: 24px; font-size: 12pt;">Mời bạn đ&oacute;n đọc.</span></strong></span></p>', '5233song-sang-suot.jpg', '2013-12-18', 0);
INSERT INTO `sach` (`sach_id`, `dmuc_id`, `sach_name`, `tacgia`, `nxb`, `soluong`, `dongia`, `sotrang`, `loaibia`, `giamgia`, `gioithieu`, `hinhanh`, `post_date`, `luot_mua`) VALUES
(23, 4, 'Không Bao Giờ Thất Bại', 'W. Clement Stone', 'Lao động - Thái Hà', 50, 85000, 334, 'mềm', '15', '<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><strong><span style="line-height: 26px; color: #ff3300; font-size: 13pt;">Kh&ocirc;ng Bao Giờ Thất Bại</span></strong></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">C&oacute; thực sự c&oacute; một hệ thống th&agrave;nh c&ocirc;ng?</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">Nh&agrave; văn Bryon, trong cuốn Đ&ocirc;ng-Gioăng, đ&atilde; viết &ldquo;Một giọt mực nhỏ rơi xuống tạo th&agrave;nh h&agrave;ng ng&agrave;n, c&oacute; thể h&agrave;ng triệu&hellip; suy nghĩ&rdquo;. V&agrave; những suy nghĩ quan trọng nhất trong số h&agrave;ng triệu suy nghĩ n&agrave;y l&agrave; (những suy nghĩ) t&igrave;m kiếm th&agrave;nh c&ocirc;ng trong cuộc sống ri&ecirc;ng tư, cuộc sống gia đ&igrave;nh v&agrave; cuộc sống sự nghiệp.Tại thời điểm n&agrave;y, trong bất cứ ng&oacute;c ng&aacute;ch n&agrave;o của thế giới, c&oacute; những người thắc mắc họ c&oacute; thể l&agrave;m g&igrave; để tiến gần hơn tới mục ti&ecirc;u đang tỏa s&aacute;ng, v&agrave; để cải thiện bản th&acirc;n họ.</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">Nhiều người sẽ nắm lấy b&iacute; mật từ trong s&acirc;u thẳm tr&aacute;i tim v&agrave; linh hồn họ để c&oacute; thể đạt được th&agrave;nh t&iacute;ch cao. Nhưng hầu hết mọi người đều tiếp tục thắc mắc, mơ tưởng v&agrave; mong ước.Rồi một ng&agrave;y, họ sẽ ngỡ ng&agrave;ng bừng tỉnh ph&aacute;t hiện ra m&igrave;nh vẫn đang dậm ch&acirc;n tại ch&iacute;nh nơi họ đ&atilde; từng mơ đến khi c&ograve;n trẻ. Chỉ l&agrave; họ đ&atilde; đ&aacute;nh mất giấc mơ của m&igrave;nh v&agrave; họ băn khoăn tại sao lại thế.</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">Một t&agrave;i liệu tuyệt vời đ&atilde; tiết lộ bản sơ đồ để t&igrave;m thấy những c&acirc;u trả lời n&agrave;y. Hệ thống th&agrave;nh c&ocirc;ng kh&ocirc;ng bao giờ thất bại của W. Clement Stone l&agrave; cuốn s&aacute;ch hướng dẫn nắm giữ ch&igrave;a kh&oacute;a cho rất nhiều c&acirc;u hỏi bạn t&igrave;m kiếm trong qu&aacute; tr&igrave;nh theo đuổi th&agrave;nh c&ocirc;ng. Stone truyền cảm hứng cho bạn kh&aacute;m ph&aacute; bản th&acirc;n, ph&aacute;t triển th&aacute;i độ tinh thần đ&uacute;ng đắn, l&agrave;m điều đ&uacute;ng đắn v&igrave; đ&oacute; l&agrave; điều đ&uacute;ng đắn n&ecirc;n l&agrave;m, nhận biết rằng chẳng c&oacute; g&igrave; đ&aacute;ng sợ ngo&agrave;i bản th&acirc;n nỗi sợ. &Ocirc;ng đ&atilde; th&aacute;ch thức mỗi một người v&agrave; tất cả ch&uacute;ng ta theo đuổi điều ch&uacute;ng ta muốn trong đời v&agrave; kh&ocirc;ng bao giờ quay đầu lại cho tới khi ch&uacute;ng ta đạt được điều đ&oacute;. To&agrave;n bộ cuốn s&aacute;ch đ&atilde; định hướng, thay đổi v&agrave; thường xuy&ecirc;n khiến bạn phải suy nghĩ về cuộc sống v&agrave; c&ocirc;ng việc của bạn như thế n&agrave;o. Đ&acirc;y l&agrave; một th&aacute;ch thức m&agrave; bạn sẽ c&ograve;n gặp phải tại một thời điểm n&agrave;o đ&oacute; trong tương lai gần.</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><strong><span style="line-height: 24px; font-size: 12pt;">Mục lục:</span></strong></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">Phần I :&nbsp;&nbsp;Cuộc t&igrave;m kiếm bắt đầu</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">Chương 1:&nbsp;&nbsp;&nbsp;Cậu b&eacute; bắt đầu t&igrave;m kiếm</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">Chương 2 : Sẵn s&agrave;ng cho ng&agrave;y mai</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">Chương 3 :&nbsp;&nbsp;H&atilde;y l&agrave; người tự g&acirc;y dựng</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">Chương 4 :&nbsp;&nbsp;Đừng để tương lai ở sau bạn</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">Phần II : T&ocirc;i t&igrave;m bản đồ kho b&aacute;u</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">Chương 5:&nbsp;&nbsp;Chỉ phải l&agrave;m &iacute;t hơn để th&agrave;nh c&ocirc;ng so với để thất bại</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">Chương 6:&nbsp;&nbsp;Đi đ&uacute;ng lộ tr&igrave;nh</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">Chương 7: Sức mạnh tiến l&ecirc;n</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">Phần III : Cuộc h&agrave;nh tr&igrave;nh c&oacute; nhiều sự kiện quan trọng</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">Chương 8 :&nbsp;&nbsp;T&ocirc;i đ&atilde; chọn một đội giỏi</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">Chương 9 :&nbsp;&nbsp;&nbsp;Ch&uacute;ng ta đ&atilde; vượt qua cơn b&atilde;o</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">Chương 10 : Thật dễ nếu bạn biết c&aacute;ch</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">Chương 11 : Nguồn sức mạnh thần kỳ</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">Chương 12 : The way of all flesh</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">Chương 13 : Vươn tới đỉnh cao từ xuất ph&aacute;t điểm hiện tại</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">Phần IV: Sự thịnh vượng&hellip; v&agrave; t&agrave;i sản thực sự trong đời</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">Chương 14 : Sự thịnh vượng v&agrave; cơ hội</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">Chương 15 : L&agrave;m c&aacute;ch n&agrave;o để nh&oacute;m l&ecirc;n ngọn lửa tham vọng</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">Chương 16 : Thi&ecirc;n t&agrave;i được đ&agrave;o tạo&hellip; kh&ocirc;ng phải bẩm sinh</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">Chương 17 : Sức mạnh thay đổi số phận</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">Chương 18 : T&agrave;i sản đ&iacute;ch thực trong đời</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">Phần V: Cuộc t&igrave;m kiếm kết th&uacute;c</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">Chương 19 : Vật b&aacute;o hiệu th&agrave;nh c&ocirc;ng đem lại th&agrave;nh c&ocirc;ng</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">Chương 20 : T&aacute;c giả tổng kết c&ocirc;ng việc của ch&iacute;nh bản th&acirc;n</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><strong><span style="line-height: 24px; font-size: 12pt;">Th&ocirc;ng tin t&aacute;c giả</span></strong></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><strong><span style="line-height: 24px; font-size: 12pt;">W. Clement Stone</span></strong><span style="line-height: 24px; font-size: 12pt;">&nbsp;bắt đầu sự nghiệp b&aacute;n h&agrave;ng phi thường của &ocirc;ng từ khi c&ograve;n l&agrave; một đứa trẻ b&aacute;n b&aacute;o ở th&agrave;nh phố qu&ecirc; nh&agrave;, Chicago. Ở tuổi 20, &ocirc;ng đ&atilde; h&igrave;nh th&agrave;nh hệ thống b&aacute;n h&agrave;ng bảo hiểm y tế v&agrave; tai nạn của ri&ecirc;ng m&igrave;nh. &Ocirc;ng th&agrave;nh c&ocirc;ng trong lĩnh vực n&agrave;y tới mức &ocirc;ng đ&atilde; quyết định gạt tham vọng h&agrave;nh nghề luật ban đầu của m&igrave;nh sang một b&ecirc;n, v&agrave; tập trung v&agrave;o mở rộng kinh doanh. Hiện nay, ngo&agrave;i những hoạt động kh&aacute;c, &ocirc;ng l&agrave; Chủ tịch của C&acirc;u lạc bộ c&aacute;c ch&agrave;ng trai Chicago, l&agrave; th&agrave;nh vi&ecirc;n Ban Gi&aacute;m đốc của C&acirc;u lạc bộ c&aacute;c ch&agrave;ng trai Mỹ. &Ocirc;ng cũng l&agrave; ủy vi&ecirc;n Trại h&egrave; &Acirc;m nhạc Quốc gia v&agrave; Học viện nghệ thuật Interlochen. Stone l&agrave; Chủ tịch Quỹ t&ocirc;n gi&aacute;o v&agrave; t&acirc;m l&yacute; Mỹ, Quỹ nghi&ecirc;n cứu chu tr&igrave;nh.</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><strong><span style="line-height: 24px; font-size: 12pt;">W. Clement Stone</span></strong><span style="line-height: 24px; font-size: 12pt;">&nbsp;l&agrave; Chủ tịch C&ocirc;ng ty bảo hiểm Combined, First National Casualty của Mỹ v&agrave; Hearthstone của Massachusetts. &Ocirc;ng cũng l&agrave; bi&ecirc;n tập v&agrave; nh&agrave; xuất bản của tạp ch&iacute; Success Unlimited (Th&agrave;nh c&ocirc;ng kh&ocirc;ng giới hạn), v&agrave; l&agrave; gi&aacute;m đốc của c&ocirc;ng ty Alberto-Culver.&Ocirc;ng l&agrave; đồng t&aacute;c giả với Napoleon Hill viết cuốn s&aacute;ch Th&agrave;nh c&ocirc;ng th&ocirc;ng qu&aacute; th&aacute;i độ tinh thần t&iacute;ch cực.</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><strong><span style="line-height: 24px; font-size: 12pt;">Mời bạn đ&oacute;n đọc.</span></strong></span></p>', 'KHONG.jpg', '2013-12-18', 0),
(24, 5, 'Hồi Ký Chính Trị', 'Mahathir Mohamad', 'Thế giới - Alphabooks', 42, 339000, 976, 'cứng', '15', '<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><strong><span style="line-height: 26px; color: red; font-size: 13pt;">Hồi K&yacute; Ch&iacute;nh Trị</span></strong></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">Hồi k&yacute; ch&iacute;nh trị của nguy&ecirc;n Thủ tướng Malaysia&nbsp;<strong>Tun Dr Mahathir Mohamad</strong>&nbsp;đ&atilde; liệt k&ecirc; đầy đủ di sản ch&iacute;nh trị &ocirc;ng để lại cho đất nước trong 22 năm cầm quyền (1981 - 2003) v&agrave; tự h&agrave;o bảo vệ những đường lối ch&iacute;nh s&aacute;ch l&atilde;nh đạo đất nước. Với sự ch&iacute;nh x&aacute;c của một nh&agrave; giải phẫu, Dr Mahathir đ&atilde; đưa ra những ph&acirc;n t&iacute;ch s&acirc;u sắc về lịch sử ph&aacute;t triển d&acirc;n tộc v&agrave; vai tr&ograve; của &ocirc;ng trong c&ocirc;ng cuộc định h&igrave;nh một nước Malaysia hiện đại trong cuốn s&aacute;ch&nbsp;<strong>Hồi k&yacute; ch&iacute;nh trị</strong>.</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">Kể từ khi gi&agrave;nh được độc lập năm 1957 đến nay, Malaysia đ&atilde; trải qua nhiều đời thủ tướng, trong đ&oacute; ri&ecirc;ng &ocirc;ng&nbsp;<strong>Mahathir Mohamad</strong>&nbsp;c&oacute; thời gian cầm quyền kh&aacute; l&acirc;u d&agrave;i v&agrave; đ&atilde; để lại dấu ấn kh&oacute; phai mờ về một người đ&agrave;n &ocirc;ng đầy quyền lực trong lịch sử ph&aacute;t triển của Malaysia. Nh&agrave; l&atilde;nh đạo hơn 2 thập kỷ của Malaysia được nh&igrave;n nhận như một nh&acirc;n vật vừa thực t&ecirc;́ vừa vi&ecirc;̉n v&ocirc;ng nhưng đến c&aacute;c chuy&ecirc;n gia kh&oacute; t&iacute;nh nhất cũng phải thừa nhận, &ocirc;ng đã thực hi&ecirc;̣n th&agrave;nh c&ocirc;ng vi&ecirc;̣c ki&ecirc;̉m soát chặt chẽ n&ecirc;̀n kinh t&ecirc;́ đất nước. Thủ tướng Mahathir cũng l&agrave; người được coi l&agrave; c&oacute; c&ocirc;ng đầu mang lại sự &ocirc;̉n định v&ecirc;̀ chính trị, hàn gắn các chia rẽ v&ecirc;̀ sắc t&ocirc;̣c tại Malaysia cũng như tạo ra nhiều đổi thay t&iacute;ch cực kh&aacute;c cho đất nước. Trong cuốn hồi k&yacute; g&acirc;y x&ocirc;n xao n&agrave;y, với sự ch&iacute;nh x&aacute;c của một nh&agrave; giải phẫu, cựu thủ tướng Mahathir đ&atilde; đưa ra những ph&acirc;n t&iacute;ch s&acirc;u sắc về lịch sử đất nước Malaysia v&agrave; vai tr&ograve; của &ocirc;ng trong c&ocirc;ng cuộc x&acirc;y dựng v&agrave; ph&aacute;t triển một nước Malaysia hiện đại. Tất cả đều rất ch&acirc;n thực, mạnh mẽ, quyết liệt v&agrave; hấp dẫn.</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">Trong cuốn hồi k&yacute; của m&igrave;nh, &ocirc;ng Mahathir đ&atilde; liệt k&ecirc; đầy đủ di sản ch&iacute;nh trị &ocirc;ng để lại cho đất nước trong 22 năm cầm quyền (1981 - 2003) v&agrave; tự h&agrave;o bảo vệ những đường lối ch&iacute;nh s&aacute;ch l&atilde;nh đạo đất nước.&nbsp;<em>&ldquo;T&ocirc;i đ&atilde; cố hết sức l&agrave;m việc d&ugrave; kh&ocirc;ng thể l&agrave; một quan t&ograve;a ph&aacute;n quyết c&ocirc;ng việc của bản th&acirc;n. H&atilde;y để cho nh&acirc;n d&acirc;n h&ocirc;m nay v&agrave; mai sau c&oacute; sự ph&aacute;n quyết n&agrave;y&rdquo;</em>&nbsp;- &ocirc;ng chia sẻ. &Ocirc;ng Mahathir đ&atilde; kể rất chi tiết từng giai đoạn trưởng th&agrave;nh của m&igrave;nh, kể từ những năm th&aacute;ng tuổi thơ tại v&ugrave;ng Đ&ocirc;ng Bắc Malaysia ngh&egrave;o kh&oacute; với những khu nh&agrave; ổ chuột dưới thời cai trị của thực d&acirc;n Anh. Cha &ocirc;ng l&agrave; nh&agrave; gi&aacute;o đ&atilde; dẫn dắt &ocirc;ng theo học ng&agrave;nh y để trở th&agrave;nh b&aacute;c sĩ trước khi bước v&agrave;o ch&iacute;nh trường, trở th&agrave;nh nghị sĩ năm 1964.</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">&Ocirc;ng Mahathir đ&atilde; d&agrave;nh nhiều chương trong cuốn s&aacute;ch giới thiệu những ch&iacute;nh s&aacute;ch kinh tế của &ocirc;ng đ&atilde; biến Malaysia từ một nền kinh tế khai th&aacute;c mỏ kẽm v&agrave; trồng c&acirc;y cao su trở th&agrave;nh một trong những nước c&ocirc;ng nghiệp ph&aacute;t triển nhất của ch&acirc;u &Aacute;. &Ocirc;ng đặc biệt tự h&agrave;o về c&ocirc;ng tr&igrave;nh kiến tr&uacute;c t&ograve;a th&aacute;p đ&ocirc;i Petronas cao nhất thế giới được x&acirc;y dựng cuối thập kỷ 90 thế kỷ trước.</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">B&ecirc;n cạnh đ&oacute;, để bảo vệ th&agrave;nh t&iacute;ch của m&igrave;nh, &ocirc;ng Mahathir biện minh kh&ocirc;ng c&oacute; sự lựa chọn n&agrave;o kh&aacute;c trong thời kỳ khủng hoảng ch&iacute;nh trị những năm 80 thế kỷ trước đ&atilde; g&acirc;y hậu quả khi bắt giam kh&ocirc;ng x&eacute;t xử h&agrave;ng chục nh&acirc;n vật bất đồng ch&iacute;nh kiến, đ&oacute;ng cửa h&agrave;ng loạt tờ b&aacute;o v&agrave; kh&ocirc;ng bảo đảm t&iacute;nh độc lập của ng&agrave;nh tư ph&aacute;p. &Ocirc;ng viết:&nbsp;<em>&ldquo;Thật kh&oacute; biết v&agrave;o giai đoạn đ&oacute; điều g&igrave; l&agrave; hợp t&igrave;nh hợp l&yacute;, đối s&aacute;ch n&agrave;o l&agrave; kh&ocirc;ng ph&ugrave; hợp v&agrave; cực đoan! Ch&uacute;ng ta chỉ biết h&agrave;nh động v&agrave; h&agrave;nh động!&rdquo;</em>&nbsp;&Ocirc;ng Mahathir c&ograve;n d&agrave;nh hẳn một chương viết về số phận đau buồn của cựu ph&oacute; thủ tướng Anwar Ibrahim, bị &ocirc;ng c&aacute;ch chức năm 1998 về tội d&acirc;m dục đồng t&iacute;nh v&agrave; bị kết &aacute;n 6 năm t&ugrave;...</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><strong><span style="line-height: 24px; font-size: 12pt;">Th&ocirc;ng tin t&aacute;c giả</span></strong></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><strong><span style="line-height: 24px; font-size: 12pt;">Mahathir Mohamad</span></strong><span style="line-height: 24px; font-size: 12pt;">&nbsp;l&agrave; cựu Chủ tịch Đảng UMNO, nguy&ecirc;n Thủ tướng thứ tư của Malaysia giai đoạn 1981 - 2003. &Ocirc;ng cũng từng đảm nhiệm vị tr&iacute; Bộ trưởng Gi&aacute;o dục; Bộ trưởng C&ocirc;ng nghiệp &amp; Thương mại Quốc tế. Với những ph&acirc;n t&iacute;ch s&acirc;u sắc về lịch sử ph&aacute;t triển d&acirc;n tộc, phương T&acirc;y gọi &ocirc;ng l&agrave; kẻ cứng đầu, ph&acirc;n biệt chủng tộc v&agrave; ngạo mạn. Ngược lại, c&aacute;c nước đang ph&aacute;t triển lại vinh danh nguy&ecirc;n Thủ tướng Malaysia l&agrave; một l&atilde;nh tụ nh&igrave;n xa tr&ocirc;ng rộng, một nh&agrave; l&atilde;nh đạo hiếm thấy, người đ&atilde; đem lại cho nh&acirc;n d&acirc;n c&aacute;c nước thế giới thứ ba l&yacute; do để tự h&agrave;o về d&acirc;n tộc m&igrave;nh. Ngay cả những người chỉ tr&iacute;ch gay gắt nhất cũng kh&ocirc;ng thể chối bỏ sự thật rằng &ocirc;ng đ&atilde; đem lại dũng kh&iacute; cho những nước &iacute;t được ch&uacute; &yacute; đến nhất, chỉ cho họ con đường đến tương lai tương s&aacute;ng hơn.</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">Trong sự nghiệp ch&iacute;nh trị của m&igrave;nh, &ocirc;ng&nbsp;<strong>Mahathir Mohamad</strong>&nbsp;kh&ocirc;ng phải l&agrave; kh&ocirc;ng đối mặt những phản đối, tranh c&atilde;i. Trong 22 năm l&atilde;nh đạo đất nước, t&ecirc;n tuổi &ocirc;ng gắn liền với hai cụm từ: Độc t&agrave;i v&agrave; Nh&agrave; l&atilde;nh đạo truyền cảm hứng. Rất &iacute;t nh&agrave; l&atilde;nh đạo c&oacute; khả năng chuyển đổi một đất nước m&agrave; nền kinh tế chủ yếu dựa v&agrave;o n&ocirc;ng nghiệp th&agrave;nh một cường quốc c&ocirc;ng nghiệp, c&agrave;ng &iacute;t người c&oacute; khả năng th&agrave;nh đạt được th&agrave;nh tựu n&agrave;y chỉ trong v&ograve;ng hai thập kỷ ngắn ngủi.</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><strong><span style="line-height: 24px; font-size: 12pt;">Mời bạn đ&oacute;n đọc.</span></strong></span></p>', '7400hoi-ky-chinh-tri.jpg', '2013-12-18', 0),
(25, 5, 'Những Bà Mẹ Của Người Nổi Tiếng', 'Trương Lương Khoa', 'Văn hóa thông tin - Văn Lang', 30, 75000, 320, 'mềm', '0', '<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: center; line-height: 19.5px;" align="center"><span style="font-family: ''Times New Roman'';"><strong><span style="line-height: 24px; font-size: 12pt;">NHỮNG B&Agrave; MẸ CỦA NGƯỜI NỔI TIẾNG</span></strong></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: center; line-height: 19.5px;" align="center"><span style="font-family: ''Times New Roman'';"><strong><span style="line-height: 24px; font-size: 12pt;">T&aacute;c giả: Trương Lương Khoa</span></strong></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: center; line-height: 19.5px;" align="center"><span style="font-family: ''Times New Roman'';"><strong><span style="line-height: 24px; font-size: 12pt;">***</span></strong></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">Cuốn s&aacute;ch&nbsp;<strong>Những b&agrave; mẹ của người nổi tiếng</strong>&nbsp;sẽ mang đến cho bạn những b&agrave;i học gi&aacute;o dục bổ &iacute;ch th&ocirc;ng qua 26 tấm gương người mẹ của 26 nh&acirc;n vật kiệt xuất. Từng trang s&aacute;ch sẽ đưa bạn đến với những phương ph&aacute;p dạy con mang đậm t&iacute;nh nh&acirc;n văn s&acirc;u sắc. Rất nhiều mẩu chuyện th&uacute; vị đi k&egrave;m đang chờ bạn kh&aacute;m ph&aacute;!</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">L&agrave; cha mẹ, chắc bạn cũng mong con m&igrave;nh sau n&agrave;y lớn l&ecirc;n trở th&agrave;nh một người giỏi giang, cũng gặt h&aacute;i được những th&agrave;nh c&ocirc;ng như c&aacute;c nh&acirc;n vật trong s&aacute;ch. Bạn cũng c&oacute; thể l&agrave;m một người mẹ như mẹ của những nh&acirc;n vật n&agrave;y. H&atilde;y đọc v&agrave; r&uacute;t ra phương ph&aacute;p gi&aacute;o dục con của ri&ecirc;ng m&igrave;nh, bạn nh&eacute;!</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><strong><span style="line-height: 24px; font-size: 12pt;">Mời bạn đ&oacute;n đọc.</span></strong></span></p>', 'me.jpg', '2013-12-18', 0);
INSERT INTO `sach` (`sach_id`, `dmuc_id`, `sach_name`, `tacgia`, `nxb`, `soluong`, `dongia`, `sotrang`, `loaibia`, `giamgia`, `gioithieu`, `hinhanh`, `post_date`, `luot_mua`) VALUES
(26, 5, 'Điệp Viên Hoàn Hảo X6', 'Larry Berman', 'Hồng Đức - Firstnews', 50, 120000, 392, 'mềm', '0', '<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: center; line-height: 19.5px;" align="center"><span style="font-family: ''Times New Roman'';"><strong><span style="line-height: 24px; font-size: 12pt;">ĐIỆP VI&Ecirc;N HO&Agrave;N HẢO X6</span></strong></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: center; line-height: 19.5px;" align="center"><span style="font-family: ''Times New Roman'';"><strong><span style="line-height: 24px; font-size: 12pt;">T&aacute;c giả: Larry Berman</span></strong></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: center; line-height: 19.5px;" align="center"><span style="font-family: ''Times New Roman'';"><strong><span style="line-height: 24px; font-size: 12pt;">***</span></strong></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><strong><span style="line-height: 24px; font-size: 12pt;">Phạm Xu&acirc;n Ẩn</span></strong><span style="line-height: 24px; font-size: 12pt;">, một c&aacute;i t&ecirc;n chứa biết bao điều b&iacute; ẩn, kh&ocirc;ng chỉ với người Việt Nam, m&agrave; đối với cả rất nhiều ch&iacute;nh kh&aacute;ch v&agrave; b&aacute;o ch&iacute; thế giới, đặc biệt l&agrave; b&aacute;o ch&iacute; Mỹ. Người ta đ&atilde; b&igrave;nh luận, tranh c&atilde;i, phỏng đo&aacute;n v&agrave; viết rất nhiều về &ocirc;ng, thậm ch&iacute; c&ograve;n l&agrave;m phim t&agrave;i liệu nhiều tập về &ocirc;ng, nhưng dường như c&ograve;n c&oacute; qu&aacute; nhiều điều vẫn chưa được l&agrave;m s&aacute;ng tỏ. Cho đến khi một người Mỹ bắt tay v&agrave;o cuộc&hellip; Đ&oacute; ch&iacute;nh l&agrave; Larry Berman, nh&agrave; sử học, gi&aacute;o sư ch&iacute;nh trị học thuộc đại học California, một chuy&ecirc;n gia xuất sắc chuy&ecirc;n nghi&ecirc;n cứu về cuộc chiến tranh x&acirc;m lược Việt Nam của Mỹ. Năm 2007, quyển s&aacute;ch Perfect Spy - The Incredible Double Life of PHAM XUAN AN Time Magazine Reporter &amp; Vietnamese Communist Agent (Điệp vi&ecirc;n ho&agrave;n hảo &ndash; Hai cuộc đời kh&oacute; tin (hay cuộc đời hai mặt theo c&aacute;ch gọi của Phương T&acirc;y về&nbsp;&nbsp;&nbsp;Phạm Xu&acirc;n Ẩn, ph&oacute;ng vi&ecirc;n tạp ch&iacute; Reuters, Time v&agrave; tướng t&igrave;nh b&aacute;o Cộng sản Việt Nam)) của Larry Berman được xuất bản lần đầu ti&ecirc;n tại Mỹ đ&atilde; g&acirc;y chấn động dư luận Mỹ, kiều b&agrave;o Việt Nam ở Mỹ v&agrave; cả ở Việt Nam. V&igrave; trước khi quyển s&aacute;ch n&agrave;y ra đời, rất &iacute;t người biết v&agrave; hiểu về tướng t&igrave;nh b&aacute;o Phạm Xu&acirc;n Ẩn, mặc d&ugrave; &ocirc;ng l&agrave; một nh&acirc;n vật huyền thoại của chiến tranh Việt Nam. C&oacute; lẽ bởi v&igrave; cuộc đời của Phạm Xu&acirc;n Ẩn, cũng giống như t&ecirc;n của &ocirc;ng, chứa đựng nhiều b&iacute; ẩn về con người v&agrave; sự nghiệp của một nh&agrave; t&igrave;nh b&aacute;o vĩ đại, t&agrave;i năng v&agrave; bản lĩnh nhưng lại v&ocirc; c&ugrave;ng khi&ecirc;m nhường, b&igrave;nh dị.</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><strong><span style="line-height: 24px; font-size: 12pt;">Phạm Xu&acirc;n Ẩn</span></strong><span style="line-height: 24px; font-size: 12pt;">&nbsp;(sinh ng&agrave;y 12/9/1927, mất ng&agrave;y 20/9/2006) tham gia hoạt động c&aacute;ch mạng từ đầu thập ni&ecirc;n 1950, năm 1953 được kết nạp Đảng v&agrave; được giao nhiệm vụ hoạt động điệp b&aacute;o. Nhằm tạo vỏ bọc tốt hơn để c&oacute; thể th&acirc;m nhập s&acirc;u hơn v&agrave;o giới chức ch&iacute;nh quyền v&agrave; qu&acirc;n đội S&agrave;i G&ograve;n, năm 1957 &ocirc;ng được cấp tr&ecirc;n bố tr&iacute; cho sang Mỹ học ng&agrave;nh b&aacute;o ch&iacute;. Năm 1959, Phạm Xu&acirc;n Ẩn về nước, l&agrave;m việc cho H&atilde;ng tin Reuters v&agrave; sau đ&oacute; l&agrave; tạp ch&iacute; h&agrave;ng đầu Time, New York Herald Tribune&nbsp;&nbsp;của Mỹ. Với kiến thức uy&ecirc;n b&aacute;c, hiểu biết rộng, cương trực v&agrave; t&agrave;i năng giao tiếp, ngoại giao kh&aacute;c biệt, độc đ&aacute;o theo kiểu l&atilde;ng tử, h&agrave;o hoa, ngang t&agrave;ng, &ldquo;chửi thề như bắp rang&rdquo;, xuất hiện với phong c&aacute;ch thượng lưu, thừa hưởng văn h&oacute;a được đ&agrave;o tạo ch&iacute;nh qui từ Mỹ, &ocirc;ng đ&atilde; th&acirc;m nhập v&agrave; l&agrave; bạn tri kỷ với c&aacute;c tướng lĩnh, tr&ugrave;m an ninh mật vụ cả Mỹ v&agrave; S&agrave;i G&ograve;n, giới b&aacute;o ch&iacute; Việt Nam Cộng H&ograve;a v&agrave; Mỹ cũng như c&aacute;c ch&iacute;nh kh&aacute;ch ch&oacute;p bu của ch&iacute;nh quyền S&agrave;i G&ograve;n để khai th&aacute;c những th&ocirc;ng tin tuyệt mật mang tầm chiến lược cho cuộc đối đầu của Bắc Việt Nam với Mỹ v&agrave; ch&iacute;nh quyền S&agrave;i G&ograve;n. Cuộc đời hoạt động t&igrave;nh b&aacute;o đầy ly kỳ hấp dẫn nhưng cũng v&ocirc; c&ugrave;ng hiểm nguy của Phạm Xu&acirc;n Ẩn với b&iacute; danh X6 thuộc cụm t&igrave;nh b&aacute;o H63 được thể hiện một c&aacute;ch trung thực, sinh động trong cuốn&nbsp;<strong>Điệp vi&ecirc;n ho&agrave;n hảo X6</strong>&nbsp;&nbsp;được bổ sung cập nhật mới.</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">Sau 6 năm, cuốn s&aacute;ch với những th&ocirc;ng tin c&oacute; gi&aacute; trị lịch sử giờ đ&acirc;y lại được ra mắt bạn đọc qua một bản dịch đầy đủ v&agrave; cập nhật nhiều th&ocirc;ng tin chưa từng được c&ocirc;ng bố do ch&iacute;nh Phạm Xu&acirc;n Ẩn tiết lộ với t&aacute;c giả Larry Berman c&oacute; ghi &acirc;m. Với lời hứa chỉ được c&ocirc;ng bố những th&ocirc;ng tin n&agrave;y từng giai đoạn sau khi Phạm Xu&acirc;n Ẩn mất, Larry Berman đ&atilde; mất 6 năm để c&acirc;n nhắc để từng bước c&ocirc;ng bố th&ocirc;ng tin của người đ&atilde; khuất.</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">Trong ấn bản lần n&agrave;y, ở đầu s&aacute;ch c&oacute; lời cảm nhận của nguy&ecirc;n Ph&oacute; Thủ tướng Trương Vĩnh Trọng, với tựa đề &ldquo;Nhớ m&atilde;i một con người&rdquo;, trong đ&oacute; c&oacute; đoạn: &ldquo;Phạm Xu&acirc;n Ẩn l&agrave; nh&agrave; t&igrave;nh b&aacute;o chiến lược cực kỳ quan trọng. Anh đ&atilde; sống một cuộc đời đầy nhiệt huyết giữa hai chiến tuyến, hai l&agrave;n đạn. Một nh&agrave; b&aacute;o Reuters v&agrave; Time được cả ch&iacute;nh kh&aacute;ch Mỹ nể trọng. Một t&igrave;nh b&aacute;o, một người y&ecirc;u nước thật sự đ&atilde; đ&oacute;ng g&oacute;p rất nhiều cho sự nghiệp giải ph&oacute;ng d&acirc;n tộc với một nh&acirc;n c&aacute;ch đặc biệt, một tấm l&ograve;ng của một người Việt Nam ch&acirc;n ch&iacute;nh. Hiếm c&oacute; một nh&agrave; t&igrave;nh b&aacute;o n&agrave;o tr&ecirc;n thế giới để lại những b&iacute; ẩn v&agrave; t&igrave;nh cảm tốt đẹp cho mọi người từ cả hai ph&iacute;a như nh&agrave; t&igrave;nh b&aacute;o Phạm Xu&acirc;n Ẩn&rdquo;.</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">Trong b&agrave;i Một phần của h&igrave;nh h&agrave;i Tổ quốc viết cho ấn bản mới cuốn Điệp vi&ecirc;n ho&agrave;n hảo, &ocirc;ng Đ&agrave;o Văn Lừng, Vụ trưởng, Trưởng cơ quan Thường trực Ban Tuy&ecirc;n gi&aacute;o Trung ương tại Th&agrave;nh phố Hồ Ch&iacute; Minh khẳng định: &ldquo;Thiếu tướng-Anh h&ugrave;ng LLVT - đ&oacute; l&agrave; sự vinh danh của Tổ quốc d&agrave;nh cho nh&agrave; t&igrave;nh b&aacute;o chiến lược Phạm Xu&acirc;n Ẩn. Nhưng t&ecirc;n tuổi của &Ocirc;ng c&ograve;n đi xa hơn thế, đặc biệt với những người b&ecirc;n kia chiến tuyến. Điều lạ l&ugrave;ng l&agrave; ngay cả khi nhiệm vụ thực sự của &Ocirc;ng được c&ocirc;ng bố, Phạm Xu&acirc;n Ẩn vẫn gi&agrave;nh được sự k&iacute;nh trọng v&agrave; ngưỡng mộ tuyệt đối từ những đối thủ của m&igrave;nh! H&agrave;ng loạt c&aacute;c ch&iacute;nh kh&aacute;ch, nh&agrave; ngoại giao, nh&agrave; nghi&ecirc;n cứu&hellip; từ Mỹ nva2 tr&ecirc;n thế giới khi đến Việt Nam đều mong muốn được diện kiến &ocirc;ng l&agrave; một minh chứng! Đặc biệt gi&aacute;o sư, nh&agrave; sử học Mỹ Larry Berman đ&atilde; d&agrave;nh nhiều năm với gần 30 lần bay từ Mỹ đến Việt Nam để gặp v&agrave; phỏng vấn &ldquo;đối thủ&rdquo;lớn của ng&agrave;nh t&igrave;nh b&aacute;o Mỹ sừng sỏ cho thấy sự hấp dẫn của Phạm Xu&acirc;n Ẩn &ndash; một người do ch&iacute;nh nước Mỹ g&oacute;p phần &ldquo;đ&agrave;o tạo&rdquo; n&ecirc;n!&rdquo;</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">Ấn bản mới n&agrave;y c&oacute; bổ sung Lời giới thiệu của t&aacute;c giả cho lần t&aacute;i bản 2013 &ldquo;S&aacute;u năm sau: hồi tưởng về Điệp Vi&ecirc;n Ho&agrave;n Hảo&rdquo;. Larry Berman viết: &ldquo;&hellip;cuộc đời của Ẩn kh&ocirc;ng chỉ l&agrave; một c&acirc;u chuyện chiến tranh m&agrave; c&ograve;n l&agrave; c&acirc;u chuyện về h&agrave;n gắn, về l&ograve;ng trung th&agrave;nh với đất nước v&agrave; bạn b&egrave;. Trong ấn bản mới, t&ocirc;i đ&atilde; th&ecirc;m v&agrave;o những c&acirc;u chuyện v&agrave; t&igrave;nh tiết mới m&agrave; hồi năm 2007 chưa thể kể ra. T&ocirc;i cũng phản &aacute;nh lại việc một số độc giả d&acirc;n sự v&agrave; qu&acirc;n sự Mỹ đ&atilde; phản ứng về cuốn s&aacute;ch của t&ocirc;i cũng như về nh&acirc;n vật/con người Phạm Xu&acirc;n Ẩn như thế n&agrave;o. Tuy nhi&ecirc;n, điều l&agrave;m cho ấn bản mới n&agrave;y trở n&ecirc;n rất quan trọng đ&oacute; l&agrave; bản dịch mới. Độc giả Việt Nam sẽ lần đầu ti&ecirc;n được đọc c&acirc;u chuyện về cuộc đời phi thường của Phạm Xu&acirc;n Ẩn như ch&iacute;nh những g&igrave; m&agrave; &ocirc;ng đ&atilde; kể với t&ocirc;i, một người Mỹ viết hồi k&yacute; cho &ocirc;ng. Điều n&agrave;y l&agrave;m cho ấn bản của First News - Tr&iacute; Việt vừa rất đặc biệt, vừa l&agrave; một cuốn s&aacute;ch mới chứa đựng rất nhiều th&ocirc;ng tin lần đầu ti&ecirc;n c&ocirc;ng bố&hellip;&rdquo;.</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">Đặc biệt trong lần in mới n&agrave;y c&ocirc;ng bố bức thư x&uacute;c động của b&agrave; Thu Nh&agrave;n, vợ thiếu tướng Phạm Xu&acirc;n Ẩn, gửi t&aacute;c giả Larry Berman: &ldquo;Do mắt k&eacute;m, t&ocirc;i phải đọc cuốn s&aacute;ch của &ocirc;ng ba lần trong suốt ba ng&agrave;y, d&ugrave; t&ocirc;i muốn đọc xong ngay lập tức: ba ng&agrave;y đầy cảm x&uacute;c, ba ng&agrave;y đầy nước mắt nhớ thương, đầy t&igrave;nh y&ecirc;u v&agrave; sự tiếc nuối&hellip; V&agrave; giờ đ&acirc;y, mỗi lần đọc lại l&agrave; t&ocirc;i kh&ocirc;ng cầm được nước mắt! Một &iacute;t người bạn của t&ocirc;i cũng c&oacute; cảm x&uacute;c như vậy&hellip; Nhưng t&ocirc;i muốn đọc lại nhiều lần để nhớ về chồng t&ocirc;i.</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">Đ&ocirc;i l&uacute;c, t&ocirc;i cảm thấy như anh Ẩn c&ograve;n sống, nhưng thật đau đớn, t&ocirc;i m&atilde;i m&atilde;i kh&ocirc;ng thể gặp anh ấy ở bất cứ nơi n&agrave;o trong cuộc đời thực n&agrave;y nữa. Một nửa th&acirc;n thể t&ocirc;i đ&atilde; chết! C&agrave;ng th&ecirc;m tuổi, t&ocirc;i c&agrave;ng thấy c&ocirc; đơn khi kh&ocirc;ng c&ograve;n anh ấy. Đ&oacute; c&oacute; phải l&agrave; định mệnh khắc nghiệt m&agrave; tất cả ch&uacute;ng ta đều phải chịu đựng trong cuộc đời n&agrave;y?!&rdquo;.</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">Ngo&agrave;i ra, ấn bản mới c&ograve;n c&oacute; lời giới thiệu &ldquo;H&atilde;y Viết Sự Thật&rdquo; của nh&agrave; sử học Dương Trung Quốc: &ldquo;&hellip;Chắc chắn, một người Mỹ viết về Phạm Xu&acirc;n Ẩn sẽ c&oacute; lợi thế m&agrave; c&aacute;c t&aacute;c giả Việt Nam kh&oacute; c&oacute; được. Kh&ocirc;ng phải chỉ l&agrave; vấn đề tư liệu. Gi&aacute;o sư Lary Berman c&oacute; thể khai th&aacute;c c&aacute;c kho lưu trữ ở Mỹ, tiếp cận c&aacute;c nh&acirc;n chứng người Mỹ, c&aacute;c cựu tướng lĩnh, ch&iacute;nh kh&aacute;ch Việt Nam Cộng ho&agrave; nay đang định cư ở Mỹ... l&agrave; những nh&acirc;n tố tạo n&ecirc;n m&ocirc;i trường sống v&agrave; hoạt động của Phạm Xu&acirc;n Ẩn với tư c&aacute;ch l&agrave; một ph&oacute;ng vi&ecirc;n của Tạp ch&iacute; Time v&agrave; một nh&agrave; t&igrave;nh b&aacute;o luồn s&acirc;u v&agrave;o nội bộ đối phương để chống lại cuộc chiến tranh m&agrave; nước Mỹ tiến h&agrave;nh tr&ecirc;n đất nước Việt Nam ở những thập kỷ 60,70 ở thế kỷ trước.</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">Nhưng, c&aacute;i lợi thế ấy kh&ocirc;ng mấy quan trọng bằng c&aacute;ch suy nghĩ của một c&ocirc;ng d&acirc;n, một nh&agrave; sử học Mỹ về một nh&agrave; t&igrave;nh b&aacute;o đứng ở chiến tuyến b&ecirc;n kia của một cuộc chiến khốc liệt. N&oacute;i c&aacute;ch kh&aacute;c, chỉ một t&aacute;c giả người Mỹ như Larry Berman mới l&yacute; giải được v&igrave; sao Phạm Xu&acirc;n Ẩn kh&ocirc;ng chỉ được những người đồng b&agrave;o, đồng ch&iacute; Việt Nam của m&igrave;nh kh&acirc;m phục, vinh danh như một người anh h&ugrave;ng m&agrave; nhiều người Mỹ đ&atilde; từng quen biết vẫn giữ được sự k&iacute;nh trọng v&agrave; chia sẻ những việc Phạm Xu&acirc;n Ẩn đ&atilde; h&agrave;nh xử với tư c&aacute;ch một người Việt Nam y&ecirc;u nước...&rdquo;.</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">Thay cho lời bạt l&agrave; những d&ograve;ng hồi tưởng v&ocirc; c&ugrave;ng x&uacute;c động của l&atilde;nh đạo v&agrave; c&aacute;c th&agrave;nh vi&ecirc;n cụm t&igrave;nh b&aacute;o H.63 về người đồng đội quả cảm, t&agrave;i năng v&agrave; qu&yacute; mến của m&igrave;nh &ndash; nh&agrave; t&igrave;nh b&aacute;o anh h&ugrave;ng Phạm Xu&acirc;n Ẩn.</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">&Ocirc;ng Trần Quốc Hương (Mười Hương), nguy&ecirc;n B&iacute; thư BCH Trung ương Đảng, Chỉ huy Mạng lưới t&igrave;nh b&aacute;o chiến lược, Trưởng ban Nội ch&iacute;nh Trung ương: &ldquo;Phạm Xu&acirc;n Ẩn l&agrave; một điệp vi&ecirc;n c&oacute; tầm nh&igrave;n chiến lược, s&aacute;ng tạo, c&oacute; &oacute;c h&agrave;i hước v&agrave; t&agrave;i ngoại giao đặc biệt. Với t&agrave;i năng thu phục nh&acirc;n t&acirc;m v&agrave; khả năng khai th&aacute;c th&ocirc;ng tin xuất ch&uacute;ngđ&atilde; kh&aacute;m ph&aacute; ra những kế hoạch v&ocirc; c&ugrave;ng quan trọng ở cấp cao nhất của qu&acirc;n đội, t&igrave;nh b&aacute;o Mỹ v&agrave; ch&iacute;nh quyền Việt Nam Cộng H&ograve;a. T&ocirc;i từng được b&aacute;o c&aacute;o lại sau khi đọc c&aacute;c t&agrave;i liệu mật của Ẩn gửi về, B&aacute;c Hồ v&agrave; Đại tướng V&otilde; Nguy&ecirc;n Gi&aacute;p c&ugrave;ng c&aacute;c tướng lĩnh đ&atilde; x&uacute;c động: &ldquo;Đọc b&aacute;o c&aacute;o của Hai Trung m&agrave; c&oacute; cảm tưởng như đang ở ngay trong đầu n&atilde;o chỉ huy t&aacute;c chiến của Mỹ&rdquo;.</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">Đại t&aacute; Nguyễn Văn T&agrave;u (Tư Cang), Cụm trưởng t&igrave;nh b&aacute;o H.63: &ldquo;Phạm Xu&acirc;n Ẩn l&agrave; một nh&agrave; t&igrave;nh b&aacute;o đặc biệt, c&oacute; một tr&aacute;i tim lớn v&agrave; l&ograve;ng trắc ẩn. Trưa 29/4/1975, khi Đại sứ qu&aacute;n Mỹ đ&atilde; chật k&iacute;n v&agrave; v&ocirc; c&ugrave;ng hỗn loạn, kh&ocirc;ng một ai c&oacute; thể v&agrave;o được, ch&iacute;nh Phạm Xu&acirc;n Ẩn l&agrave; người l&aacute;i xe đưa b&aacute;c sĩ Trần Kim Tuyến &ndash; tr&ugrave;m mật vụ S&agrave;i G&ograve;n từ kh&aacute;ch sạn Continental đến t&ograve;a nh&agrave; 22 đường Gia Long khi c&aacute;nh cửa sắt lớn đang từ từ hạ xuống v&agrave; chiếc trực thăng cuối c&ugrave;ng tr&ecirc;n s&acirc;n thượng chật n&iacute;ch người sắp cất c&aacute;nh. Anh thắng gấp xe nhảy xuống d&ugrave;ng một tay giữ cửa, tay kia k&eacute;o Tuyến chui v&agrave;o kịp khi c&aacute;nh cửa c&ograve;n c&aacute;ch mặt đất chỉ v&agrave;i tấc. Kh&ocirc;ng kịp từ biệt &ndash; &ldquo;Chạy mau đi!&rdquo; &ndash; anh qu&aacute;t lớn trong nước mắt. Trần Kim Tuyến bật kh&oacute;c v&agrave; chỉ kịp thốt l&ecirc;n: &ldquo;T&ocirc;i sẽ kh&ocirc;ng bao giờ qu&ecirc;n anh!&rdquo;.</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">Đại t&aacute; Nguyễn Xu&acirc;n Mạnh (Mười Nho), Trưởng ph&ograve;ng điệp b&aacute;o 73, Cục T&igrave;nh b&aacute;o Bộ Tổng tham mưu: Sau 30/4/1975, nhận thấy t&agrave;i năng t&igrave;nh b&aacute;o bậc thầy thi&ecirc;n ph&uacute; v&agrave; xuất ch&uacute;ng của Ẩn, kh&ocirc;ng chỉ c&oacute; tầm nh&igrave;n chiến lược với Mỹ m&agrave; c&ograve;n với Trung Quốc v&agrave; c&aacute;c nước kh&aacute;c, t&ocirc;i đ&atilde; ch&iacute;nh thức đề xuất l&ecirc;n Tổng cục t&igrave;nh b&aacute;o Trung ương v&agrave; cấp tr&ecirc;n, đề nghị Phạm Xu&acirc;n Ẩn l&agrave; Viện trưởng Viện Đ&agrave;o tạo nghiệp vụ t&igrave;nh b&aacute;o cho QĐNDVN sẽ v&ocirc; c&ugrave;ng hiệu quả&rdquo;.</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">B&agrave; Nguyễn Thị Mỹ Nhung, giao li&ecirc;n của điệp vi&ecirc;n X6 trong Cụm H.63: &ldquo;Kh&ocirc;ng chỉ l&agrave; bậc thầy trong nghề t&igrave;nh b&aacute;o, Phạm Xu&acirc;n Ẩn c&ograve;n l&agrave; một người anh qu&yacute; mến, với những đức t&iacute;nh m&agrave; t&ocirc;i đ&atilde; học hỏi v&agrave; trưởng th&agrave;nh. Anh lu&ocirc;n quan t&acirc;m, như t&igrave;nh cảm của một người anh d&agrave;nh cho người em g&aacute;i. Suốt đời t&ocirc;i kh&ocirc;ng bao giờ qu&ecirc;n được &aacute;nh mắt của anh, như nguồn kh&iacute;ch lệ, động vi&ecirc;n t&ocirc;i vượt qua những kh&oacute; khăn, thử th&aacute;ch&rdquo;.</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">Đồng nghiệp b&aacute;o ch&iacute; v&agrave; những người bạn Mỹ cũng tỏ ra rất ngưỡng mộ v&agrave; cảm k&iacute;ch cuộc đời l&agrave;m b&aacute;o v&agrave; cuộc đời hoạt động t&igrave;nh b&aacute;o phi thường của Phạm Xu&acirc;n Ẩn. Trả lời phỏng vấn của dịch giả Đỗ H&ugrave;ng, b&agrave; Germaine Lộc Swanson, một đồng nghiệp cũ của Phạm Xu&acirc;n Ẩn ở h&atilde;ng tin Reuters ph&aacute;t biểu: &ldquo;Sự thực th&igrave; khi biết tin anh Ẩn l&agrave;m t&igrave;nh b&aacute;o, t&ocirc;i cũng kh&ocirc;ng c&oacute; suy nghĩ g&igrave; đặc biệt, kh&ocirc;ng hề sốc. Anh ấy vẫn l&agrave; một người anh, một người bạn th&acirc;n, một cựu đồng nghiệp của t&ocirc;i. Trước kia t&ocirc;i tr&acirc;n trọng, qu&yacute; trọng anh ấy thế n&agrave;o th&igrave; giờ đ&acirc;y vẫn vậy. Trong suy nghĩ của t&ocirc;i, anh Ẩn l&agrave; một người y&ecirc;u nước, một người c&oacute; tinh thần d&acirc;n tộc. Anh ấy l&agrave;m việc đ&oacute; v&igrave; anh ấy nghĩ sẽ gi&uacute;p &iacute;ch cho đất nước. T&ocirc;i y&ecirc;u nước, anh ấy cũng y&ecirc;u nước, nhưng ho&agrave;n cảnh xuất th&acirc;n mỗi người mỗi kh&aacute;c n&ecirc;n con đường sau đ&oacute; cũng kh&aacute;c nhau. Gia đ&igrave;nh lớn của t&ocirc;i c&oacute; người theo Việt Minh, c&oacute; người l&agrave;m cho Ph&aacute;p. Ho&agrave;n cảnh lịch sử đặc biệt của đất nước m&igrave;nh khiến kh&ocirc;ng &iacute;t những người bạn th&acirc;n, những th&agrave;nh vi&ecirc;n trong c&aacute;c gia đ&igrave;nh đứng về c&aacute;c ph&iacute;a chiến tuyến kh&aacute;c nhau. Đất nước khi chiến tranh l&agrave; vậy, đến thời b&igrave;nh th&igrave; c&aacute;c th&agrave;nh vi&ecirc;n gia đ&igrave;nh lại về với nhau. T&igrave;nh cảm giữa t&ocirc;i với Phạm Xu&acirc;n Ẩn v&agrave; gia đ&igrave;nh anh ấy cũng tương tự, kh&ocirc;ng g&igrave; thay đổi được&rdquo;.</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">Sau khi đọc cuốn Điệp vi&ecirc;n ho&agrave;n hảo, John le Carre, nh&agrave; văn Anh chuy&ecirc;n viết tiểu thuyết t&igrave;nh b&aacute;o đ&atilde; thốt l&ecirc;n: &ldquo;C&uacute; sốc về điệp vi&ecirc;n hai mang kh&ocirc;ng sao l&agrave;m t&ocirc;i qu&ecirc;n được. V&agrave; sự nhẹ dạ của kẻ x&acirc;m lược ngạo mạn cũng thế".</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">Robert Sam Anson, t&aacute;c giả cuốn War News: A Young Reporter in Indochina:&nbsp;&nbsp;&ldquo;Phạm Xu&acirc;n Ẩn l&agrave; đồng nghiệp, l&agrave; người bạn, người thầy của t&ocirc;i - v&agrave; l&agrave; người đ&atilde; cứu mạng sống của t&ocirc;i trong cuộc chiến Việt Nam. T&ocirc;i nghĩ t&ocirc;i đ&atilde; hiểu Phạm Xu&acirc;n Ẩn như bất kỳ người Mỹ n&agrave;o kh&aacute;c, cho đến khi t&ocirc;i đọc cuốn s&aacute;ch của Larry Berman. C&oacute; nhiều bất ngờ đến với t&ocirc;i tr&ecirc;n từng trang s&aacute;ch.&rdquo;</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">David Lamb, t&aacute;c giả cuốn Việt Nam, B&acirc;y giờ: Một ph&oacute;ng vi&ecirc;n trở lại: &ldquo;Đọc tư liệu được bi&ecirc;n soạn kỹ lưỡng của Larry Berman, bạn sẽ hiểu tại sao chiến thắng Việt Nam chưa bao giờ nằm trong tầm với của nước Mỹ.&rdquo;</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">Stanley Karnow, t&aacute;c giả cuốn Việt Nam: Một thi&ecirc;n lịch sử v&agrave; chủ nh&acirc;n giải Pulitzer 1991 về đề t&agrave;i lịch sử: &ldquo;Một trong những nh&acirc;n vật quyến rũ nhất của Việt Nam trong suốt cuộc chiến l&agrave; Phạm Xu&acirc;n Ẩn. Larry Berman đ&atilde; v&eacute;n bức m&agrave;n b&iacute; mật trong cuộc đời hai mặt lạ l&ugrave;ng của con người đặc biệt n&agrave;y th&ocirc;ng qua c&aacute;ch kể chuyện l&ocirc;i cuốn&rdquo;.</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">Dan Southerland, Tổng thư k&yacute; Đ&agrave;i ph&aacute;t thanh Ch&acirc;u &Aacute; Tự do, cựu ph&oacute;ng vi&ecirc;n Christian Science Monitor : &ldquo;Kh&ocirc;ng ai c&oacute; thể biết được bao nhi&ecirc;u người l&iacute;nh đ&atilde; chết như l&agrave; hệ quả từ những hoạt động b&iacute; mật của Phạm Xu&acirc;n Ẩn . Nhưng Larry Berman đ&atilde; thực hiện một sứ mệnh ngoại hạng trong việc giải th&iacute;ch sự h&igrave;nh th&agrave;nh một điệp vi&ecirc;n bậc thầy ngay tại S&agrave;i G&ograve;n. Ở đ&acirc;y c&oacute; rất nhiều điều để cả những người ủng hộ lẫn chống đối phải suy ngẫm&rdquo;.</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">Robert Dallek, t&aacute;c giả cuốn Nixon and Kissinger: Partners in Power: &ldquo;Perfect Spy l&agrave; một minh chứng sống động rằng sự thật c&ograve;n kỳ lạ hơn chuyện viễn tưởng. Larry Berman đ&atilde; l&agrave;m tỏa s&aacute;ng hai cuộc đời của Phạm Xu&acirc;n Ẩn. Cuốn s&aacute;ch đầy ắp th&ocirc;ng tin lịch sử với những đặc t&iacute;nh của c&acirc;u chuyện t&igrave;nh b&aacute;o thời Chiến tranh Lạnh đặc biệt ly kỳ v&agrave; l&ocirc;i cuốn&rdquo;.</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">Zalin Granti, t&aacute;c giả cuốn Những người sống s&oacute;t: C&aacute;c t&ugrave; binh chiến tranh Việt Nam kể lại chuyện m&igrave;nh: &ldquo;Sau&nbsp;&nbsp;khi Perfect Spy xuất bản tại Mỹ v&agrave; tiết lộ Phạm Xu&acirc;n Ẩn l&agrave; điệp vi&ecirc;n Cộng sản, rất nhiều nh&agrave; b&aacute;o Mỹ đ&atilde; ra sức đứng ra bảo vệ Phạm Xu&acirc;n Ẩn. Họ tuy&ecirc;n bố v&agrave; đưa ra c&aacute;c bằng chứng rằng &ocirc;ng chưa bao giờ l&agrave;m sai lệch c&aacute;c b&agrave;i b&aacute;o, rằng &ocirc;ng lu&ocirc;n cung cấp cho họ những ph&acirc;n t&iacute;ch rất kh&aacute;ch quan. &Ocirc;ng ấy tranh luận với họ về c&aacute;c h&agrave;nh động ch&iacute;nh trị v&agrave; qu&acirc;n sự của Mỹ, S&agrave;i G&ograve;n v&agrave; H&agrave; Nội một c&aacute;ch c&ocirc;ng bằng v&agrave; ch&iacute;nh trực nhất&rdquo;.</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">Jean - Clause Pomonti, t&aacute;c giả cuốn Một người Việt Nam thầm lặng: &ldquo;Tin Phạm Xu&acirc;n Ẩn l&agrave;m t&igrave;nh b&aacute;o cho Bắc Việt đ&atilde; khiến nhiều quan chức cao cấp CIA phải hổ thẹn v&igrave; họ đ&atilde; từng dựa tr&ecirc;n những ph&acirc;n t&iacute;ch của Ẩn để ra những quyết định quan trọng".</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">Cuốn&nbsp;<strong>Điệp vi&ecirc;n ho&agrave;n hảo</strong>&nbsp;của Larry Berman được người đọc Việt Nam v&agrave; Mỹ cũng như giới sử học, giới b&aacute;o ch&iacute; v&agrave; c&aacute;c nh&agrave; t&igrave;nh b&aacute;o của cả hai nước đ&aacute;nh gi&aacute; cao.</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><strong><span style="line-height: 24px; font-size: 12pt;">Th&ocirc;ng tin t&aacute;c giả&nbsp;</span></strong></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">Larry Berman nh&agrave; sử học, gi&aacute;o sư khoa học ch&iacute;nh trị tại Đại học California đồng thời l&agrave; một người chuy&ecirc;n t&acirc;m nghi&ecirc;n cứu lịch sử cuộc chiến tranh của Mỹ ở Việt Nam. &Ocirc;ng cũng l&agrave; t&aacute;c giả của cuốn s&aacute;ch nổi tiếng Kh&ocirc;ng h&ograve;a b&igrave;nh, Kh&ocirc;ng danh dự: Nixon, Kissinger v&agrave; Sự phản bội ở Việt Nam. Đ&acirc;y l&agrave; cuốn s&aacute;ch rất c&oacute; gi&aacute; trị lịch sử về giai đoạn cuối cuộc chiến tranh do Mỹ g&acirc;y ra ở Việt Nam.</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><strong><span style="line-height: 24px; font-size: 12pt;">Mời bạn đ&oacute;n đọc.</span></strong></span></p>', 'dv.jpg', '2013-12-18', 0),
(27, 6, 'CoreIDraw X5 - Dành Cho Người Tự Học', 'Đỗ Lê Thuận - Võ Duy Thanh Tâm - Phạm Quang Huy', 'Giao thông vận tải - TTH', 60, 60000, 272, 'mềm', '0', '<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 18px; text-align: justify;"><span style="line-height: 18.399999618530273px; font-size: 12pt;"><span style="font-family: ''Times New Roman'';">Coreldraw X5 d&agrave;nh cho người tự học được bi&ecirc;n soạn cho người mới bắt đầu sử dụng coreldraw trong thiết kế, học tập v&agrave; nhiều hơn nữa. Quyển s&aacute;ch n&agrave;y được minh họa bằng h&igrave;nh ảnh cả l&yacute; thuyết lẫn thực h&agrave;nh sẽ gi&uacute;p bạn dễ d&agrave;ng t&igrave;m hiểu v&agrave; kh&aacute;m ph&aacute; c&aacute;c đặc t&iacute;nh của Coreldraw x5 để ứng dụng trong c&ocirc;ng việc thực tế v&agrave; hiệu quả nhất.</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 18px; text-align: justify;"><strong><span style="line-height: 18.399999618530273px; font-size: 12pt;"><span style="font-family: ''Times New Roman'';">Nội dung s&aacute;ch được chia l&agrave;m hai phần.</span></span></strong></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 18px; text-align: justify;"><span style="font-family: ''Times New Roman'';"><strong><span style="line-height: 18.399999618530273px; font-size: 12pt;">Phần 1:&nbsp;</span></strong><span style="line-height: 18.399999618530273px; font-size: 12pt;">Từ chương 1 đến chương 5, chủ yếu giới thiệu về l&yacute; thuyết. Trong mỗi chương l&yacute; thuyết c&oacute; lồng v&agrave;o một số b&agrave;i tập nhỏ để bạn dễ d&agrave;ng h&igrave;nh dung v&agrave; hiểu r&otilde; c&aacute;ch sử dụng c&aacute;c c&ocirc;ng cụ, lệnh trong Corel.</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 18px; text-align: justify;"><span style="font-family: ''Times New Roman'';"><strong><span style="line-height: 18.399999618530273px; font-size: 12pt;">Phần 2:</span></strong><span style="line-height: 18.399999618530273px; font-size: 12pt;">&nbsp;Bao gồm 3 b&agrave;i tập tổng hợp trong chương 6.</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 18px; text-align: justify;"><strong><span style="line-height: 18.399999618530273px; font-size: 12pt;"><span style="font-family: ''Times New Roman'';">Chương 1: Giao diện coreldraw x5.</span></span></strong></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 18px; text-align: justify;"><span style="line-height: 18.399999618530273px; font-size: 12pt;"><span style="font-family: ''Times New Roman'';">Trước khi đi v&agrave;o t&igrave;m hiểu c&aacute;c c&ocirc;ng cụ v&agrave; lệnh, cũng như c&aacute;c thao t&aacute;c vẽ v&agrave; thiết kế trong CorelDRAW X5, bạn đọc sẽ được giới thiệu sơ qua về giao diện của chương tr&igrave;nh.</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 18px; text-align: justify;"><strong><span style="line-height: 18.399999618530273px; font-size: 12pt;"><span style="font-family: ''Times New Roman'';">Chương 2: Thanh c&ocirc;ng cụ.</span></span></strong></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 18px; text-align: justify;"><span style="line-height: 18.399999618530273px; font-size: 12pt;"><span style="font-family: ''Times New Roman'';">Chương n&agrave;y giới thiệu đến bạn đọc t&iacute;nh năng cũng như c&aacute;ch sử dụng c&aacute;c c&ocirc;ng cụ trong CorelDraw, bao gồm 19 nh&oacute;m c&ocirc;ng cụ ch&iacute;nh.</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 18px; text-align: justify;"><strong><span style="line-height: 18.399999618530273px; font-size: 12pt;"><span style="font-family: ''Times New Roman'';">Chương 3: Tr&igrave;nh đơn effects.</span></span></strong></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 18px; text-align: justify;"><span style="line-height: 18.399999618530273px; font-size: 12pt;"><span style="font-family: ''Times New Roman'';">Chương 3 sẽ hướng dẫn bạn sử dụng một số hiệu ứng của CorelDraw X5 trong tr&igrave;nh đơn Effects.</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 18px; text-align: justify;"><strong><span style="line-height: 18.399999618530273px; font-size: 12pt;"><span style="font-family: ''Times New Roman'';">Chương 4: Tr&igrave;nh đơn arrange.</span></span></strong></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 18px; text-align: justify;"><span style="line-height: 18.399999618530273px; font-size: 12pt;"><span style="font-family: ''Times New Roman'';">C&aacute;c lệnh trong tr&igrave;nh đơn Arrange li&ecirc;n quan trực tiếp đến thao t&aacute;c v&agrave; &aacute;p dụng những hiệu ứng đặc biệt cho c&aacute;c đối tượng. So với c&aacute;c phi&ecirc;n bản trước, CorelDRAW X5 c&oacute; th&ecirc;m một số lệnh v&agrave; cửa sổ kỹ xảo mới, đồng thời c&oacute; cải tiến c&aacute;c lệnh cũ. Trong chương n&agrave;y, ta sẽ lần lượt t&igrave;m hiểu ch&uacute;ng.</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 18px; text-align: justify;"><strong><span style="line-height: 18.399999618530273px; font-size: 12pt;"><span style="font-family: ''Times New Roman'';">Chương 5: Tr&igrave;nh đơn text.</span></span></strong></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 18px; text-align: justify;"><strong><span style="line-height: 18.399999618530273px; font-size: 12pt;"><span style="font-family: ''Times New Roman'';">Chương 6: B&agrave;i Tập Tổng Hợp</span></span></strong></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 18px; text-align: justify;"><span style="line-height: 18.399999618530273px; font-size: 12pt;"><span style="font-family: ''Times New Roman'';">Chương 6 tập trung giới thiệu 3 b&agrave;i tập thực h&agrave;nh với phần hướng dẫn đầy đủ v&agrave; chi tiết. Ngo&agrave;i việc gi&uacute;p bạn &ocirc;n tập những kiến thức đ&atilde; học ở những chương trước, trong qu&aacute; tr&igrave;nh thực h&agrave;nh, bạn sẽ l&agrave;m quen một số c&ocirc;ng cụ v&agrave; lệnh mới gi&uacute;p việc học của bạn nhanh tiến bộ hơn.</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 18px; text-align: justify;"><span style="line-height: 18.399999618530273px; font-size: 12pt;"><span style="font-family: ''Times New Roman'';">Mỗi b&agrave;i tập đều tr&igrave;nh b&agrave;y với c&aacute;c hướng dẫn từng bước, được minh họa bằng c&aacute;c h&igrave;nh ảnh được cắt trực tiếp từ m&aacute;y t&iacute;nh gi&uacute;p bạn dễ theo d&otilde;i khi thực h&agrave;nh.</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 18px; text-align: justify;"><strong><span style="line-height: 18.399999618530273px; font-size: 12pt;"><span style="font-family: ''Times New Roman'';">B&agrave;i Tập 1: Vẽ Đĩa Phim</span></span></strong></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 18px; text-align: justify;"><span style="line-height: 18.399999618530273px; font-size: 12pt;"><span style="font-family: ''Times New Roman'';">B&agrave;i tập hướng dẫn vẽ đĩa phim với c&aacute;c c&ocirc;ng cụ t&ocirc; m&agrave;u Fountain Fill Dialog v&agrave; c&aacute;c lệnh Combine, Bitmaps đưa về dạng ảnh.</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 18px; text-align: justify;"><strong><span style="line-height: 18.399999618530273px; font-size: 12pt;"><span style="font-family: ''Times New Roman'';">B&agrave;i Tập 2: Vẽ Ghế Gỗ</span></span></strong></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 18px; text-align: justify;"><span style="line-height: 18.399999618530273px; font-size: 12pt;"><span style="font-family: ''Times New Roman'';">Với kỹ thuật phối m&agrave;u của Fountain Fill Dialog, c&ocirc;ng cụ cơ bản như: B&eacute;zier Tool, Freehand&hellip; kết hợp với hiệu ứng Bitmaps vẽ chiếc ghế gỗ như h&igrave;nh b&ecirc;n, một vật dụng quen thuộc trong cuộc sống h&agrave;ng ng&agrave;y.</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 18px; text-align: justify;"><strong><span style="line-height: 18.399999618530273px; font-size: 12pt;"><span style="font-family: ''Times New Roman'';">B&agrave;i Tập 3: Vẽ Lon Sữa</span></span></strong></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 18px; text-align: justify;"><span style="line-height: 18.399999618530273px; font-size: 12pt;"><span style="font-family: ''Times New Roman'';">B&agrave;i tập hướng dẫn bạn sử dụng những c&ocirc;ng cụ vẽ cơ bản, đồng thời phối hợp với c&aacute;c c&ocirc;ng cụ t&ocirc; m&agrave;u như Fountain Fill Dialog, Interactive Blend Tool. Đặc biệt sử dụng lệnh Bitmaps đưa đối tượng về dạng bitmap để vẽ được lon sữa như h&igrave;nh.</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 18px; text-align: justify;"><strong><span style="line-height: 18.399999618530273px; font-size: 12pt;"><span style="font-family: ''Times New Roman'';">Mời bạn đ&oacute;n đọc</span></span></strong></p>', '2982corel-x5-cho-nguoi-tu-hoc.jpg', '2013-12-18', 0);
INSERT INTO `sach` (`sach_id`, `dmuc_id`, `sach_name`, `tacgia`, `nxb`, `soluong`, `dongia`, `sotrang`, `loaibia`, `giamgia`, `gioithieu`, `hinhanh`, `post_date`, `luot_mua`) VALUES
(28, 6, 'Microsoft Office 2010 - Dành Cho Người Tự Học', 'Lê Thuận -Thanh Tâm - Quang Huy', 'Thông tin truyền thông - TTH', 30, 62000, 272, 'mềm', '0', '<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 18px; text-align: justify;"><span style="line-height: 18.399999618530273px; font-size: 12pt;"><span style="font-family: ''Times New Roman'';">Trong lĩnh vực tin học văn ph&ograve;ng, Microsoft Word, Excel, PowerPoint, Visio l&agrave; những chương tr&igrave;nh thuộc bộ Microsoft Office đ&atilde; v&agrave; đang được ứng dụng rộng r&atilde;i trong nhiều lĩnh vực: kinh tế, kế to&aacute;n, thương mại, quản l&yacute;, vẽ kỹ thuật v&agrave; nhiều hơn nữa. Với Office 2010, bạn c&oacute; trong tay đầy đủ c&aacute;c c&ocirc;ng cụ v&agrave; lệnh để thực hiện c&aacute;c tiện &iacute;ch trong diện to&aacute;n văn ph&ograve;ng .</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 18px; text-align: justify;"><strong><span style="line-height: 18.399999618530273px; font-size: 12pt;"><span style="font-family: ''Times New Roman'';">Cuốn s&aacute;ch gồm 3 phần :</span></span></strong></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 18px; text-align: justify;"><strong><span style="line-height: 18.399999618530273px; font-size: 12pt;"><span style="font-family: ''Times New Roman'';">Phần 1: GIỚI THIỆU VỀ MICROSOFT WORD</span></span></strong></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 18px; text-align: justify;"><span style="line-height: 18.399999618530273px; font-size: 12pt;"><span style="font-family: ''Times New Roman'';">B&agrave;i tập 1: Định dạng văn bản</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 18px; text-align: justify;"><span style="line-height: 18.399999618530273px; font-size: 12pt;"><span style="font-family: ''Times New Roman'';">B&agrave;i tập 2: Một số hiệu chỉnh.....</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 18px; text-align: justify;"><strong><span style="line-height: 18.399999618530273px; font-size: 12pt;"><span style="font-family: ''Times New Roman'';">Phần 2 : GIỚI THIỆU VỀ MICROSOFT POWERPOINT</span></span></strong></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 18px; text-align: justify;"><span style="line-height: 18.399999618530273px; font-size: 12pt;"><span style="font-family: ''Times New Roman'';">B&agrave;i tập 7: L&agrave;m quen với PowerPoint</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 18px; text-align: justify;"><span style="line-height: 18.399999618530273px; font-size: 12pt;"><span style="font-family: ''Times New Roman'';">B&agrave;i tập 8: Hướng dẫn thiết kế b&agrave;i giảng h&oacute;a học.....</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 18px; text-align: justify;"><strong><span style="line-height: 18.399999618530273px; font-size: 12pt;"><span style="font-family: ''Times New Roman'';">Phần 3: GIỚI THIỆU VỀ MICROSOFT EXCEL</span></span></strong></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 18px; text-align: justify;"><span style="line-height: 18.399999618530273px; font-size: 12pt;"><span style="font-family: ''Times New Roman'';">B&agrave;i tập 10: C&aacute;c thao t&aacute;c cơ bản</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 18px; text-align: justify;"><span style="line-height: 18.399999618530273px; font-size: 12pt;"><span style="font-family: ''Times New Roman'';">B&agrave;i tập 11: Hiệu chỉnh tr&ecirc;n Excel.....</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 18px; text-align: justify;"><strong><span style="line-height: 18.399999618530273px; font-size: 12pt;"><span style="font-family: ''Times New Roman'';">Mời bạn đ&oacute;n đọc</span></span></strong>&nbsp;</p>', '2010.jpg', '2013-12-18', 0),
(29, 6, 'Giáo Trình Word 2010 Dành Cho Người Tự Học', 'Phạm Quang Huy - Võ Duy Thanh Tâm', 'Thông tin truyền thông - TTH', 20, 56000, 288, 'mềm', '5', '<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 18px; text-align: justify;"><span style="line-height: 18.399999618530273px; font-size: 12pt;"><span style="font-family: ''Times New Roman'';">Nội dung s&aacute;ch tr&igrave;nh b&agrave;y những kiến thức kh&ocirc;ng qu&aacute; s&acirc;u, nhằm phục vụ cho những người mới bắt đầu l&agrave;m quen với việc soạn thảo v&agrave; chỉnh sửa văn bản, đồng thời l&agrave; t&agrave;i liệu tham khảo cho c&aacute;c gi&aacute;o vi&ecirc;n....</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 18px; text-align: justify;"><span style="line-height: 18.399999618530273px; font-size: 12pt;"><span style="font-family: ''Times New Roman'';">S&aacute;ch gồm c&oacute; 14 chương:</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 18px; text-align: justify;"><span style="line-height: 18.399999618530273px; font-size: 12pt;"><span style="font-family: ''Times New Roman'';">Chương 1: L&agrave;m quen với giao diện Word 2010</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 18px; text-align: justify;"><span style="line-height: 18.399999618530273px; font-size: 12pt;"><span style="font-family: ''Times New Roman'';">Chương 2: Nhập v&agrave; soạn thảo văn bản</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 18px; text-align: justify;"><span style="line-height: 18.399999618530273px; font-size: 12pt;"><span style="font-family: ''Times New Roman'';">Chương 3: Định dạng văn bản</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 18px; text-align: justify;"><span style="line-height: 18.399999618530273px; font-size: 12pt;"><span style="font-family: ''Times New Roman'';">Chương 4: Định dạng theo khu vực</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 18px; text-align: justify;"><span style="line-height: 18.399999618530273px; font-size: 12pt;"><span style="font-family: ''Times New Roman'';">Chương 5: C&aacute;c kiểu định dạng</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 18px; text-align: justify;"><span style="line-height: 18.399999618530273px; font-size: 12pt;"><span style="font-family: ''Times New Roman'';">Chương 6: Template (dạng mẫu)</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 18px; text-align: justify;"><span style="line-height: 18.399999618530273px; font-size: 12pt;"><span style="font-family: ''Times New Roman'';">Chương 7: C&aacute;c c&ocirc;ng cụ phụ trợ</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 18px; text-align: justify;"><span style="line-height: 18.399999618530273px; font-size: 12pt;"><span style="font-family: ''Times New Roman'';">Chương 8: C&aacute;c phương thức hiển thị</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 18px; text-align: justify;"><span style="line-height: 18.399999618530273px; font-size: 12pt;"><span style="font-family: ''Times New Roman'';">Chương 9: In trong Word 2010</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 18px; text-align: justify;"><span style="line-height: 18.399999618530273px; font-size: 12pt;"><span style="font-family: ''Times New Roman'';">Chương 10: Khung (Frame)</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 18px; text-align: justify;"><span style="line-height: 18.399999618530273px; font-size: 12pt;"><span style="font-family: ''Times New Roman'';">Chương 11: T&agrave;i liệu d&agrave;i</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 18px; text-align: justify;"><span style="line-height: 18.399999618530273px; font-size: 12pt;"><span style="font-family: ''Times New Roman'';">Chương 12: Autotext v&agrave; Macro</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 18px; text-align: justify;"><span style="line-height: 18.399999618530273px; font-size: 12pt;"><span style="font-family: ''Times New Roman'';">Chương 13: Bảng biểu (Table)</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 18px; text-align: justify;"><span style="line-height: 18.399999618530273px; font-size: 12pt;"><span style="font-family: ''Times New Roman'';">Chương 14: Đồ họa</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 18px; text-align: justify;"><strong><span style="line-height: 18.399999618530273px; font-size: 12pt;"><span style="font-family: ''Times New Roman'';">Mời bạn đ&oacute;n đọc.</span></span></strong></p>', 'tb.jpg', '2013-12-18', 0),
(30, 6, 'Tự Học Microsoft Power Point 2010 Bằng Hình Minh Họa', 'Vn - Guide', 'Thời Đại - Văn Lang', 45, 40000, 143, 'mềm', '5', '<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 18px; text-align: justify;"><span style="line-height: 18.399999618530273px; font-size: 12pt;"><span style="font-family: ''Times New Roman'';">Microsoft Power Point l&agrave; một phần mềm trong bộ phận phần mềm của Microsoft Office. Power Point được d&ugrave;ng để thiết kế thuyết tr&igrave;nh cho c&aacute;c dự &aacute;n trong hội họp, hội thảo, hội nghị&hellip; nhằm tr&igrave;nh b&agrave;y c&aacute;c dự &aacute;n tiện &iacute;ch v&agrave; l&ocirc;i cuốn người xem r&otilde; r&agrave;ng hơn.</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 18px; text-align: justify;"><span style="line-height: 18.399999618530273px; font-size: 12pt;"><span style="font-family: ''Times New Roman'';">Trong phi&ecirc;n bản 2010 n&agrave;y, phần mềm Microsoft PowerPoin đ&atilde; được cải tiến rất nhiều so với c&aacute;c phi&ecirc;n bản trước đ&oacute;. C&aacute;c hiệu ứng động đẹp mắt cũng như c&aacute;c t&iacute;nh năng về tinh chỉnh h&igrave;nh ảnh hay &acirc;m thanh được cải thiện nhiều hơn.</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 18px; text-align: justify;"><span style="line-height: 18.399999618530273px; font-size: 12pt;"><span style="font-family: ''Times New Roman'';">Để c&aacute;c bạn l&agrave;m quen với Power Point phi&ecirc;n bản mới c&aacute;c t&aacute;c giả đ&atilde; bi&ecirc;n soạn n&ecirc;n cuốn &ldquo;Tự học Microsoft PowerPoin 2010 bằng h&igrave;nh minh họa&rdquo;. Cuốn s&aacute;ch bao gồm c&aacute;c nội dung ch&iacute;nh như sau: Giới thiệu Microsoft Power Point 2010, x&aacute;c lập v&agrave; hiệu chỉnh Slide Show, l&agrave;m việc với c&aacute;c đối tượng, tạo hiệu ứng cho Slide, x&aacute;c lập điều khiển c&aacute;c đối tượng, ch&egrave;n c&aacute;c đối tượng đặc biệt, d&ugrave;ng chung t&agrave;i liệu v&agrave; in ấn, c&aacute;c ph&iacute;m tắt Microsoft Power Point.</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 18px; text-align: justify;"><span style="line-height: 18.399999618530273px; font-size: 12pt;"><span style="font-family: ''Times New Roman'';">Điểm đặc biệt của cuốn s&aacute;ch n&agrave;y l&agrave; hướng dẫn người học thực h&agrave;nh chủ yếu th&ocirc;ng qua c&aacute;c h&igrave;nh ảnh minh họa. Với những h&igrave;nh ảnh trực quan, sinh động đ&oacute;, người học sẽ tiếp thu kiến thức một c&aacute;ch thuận tiện, nhanh ch&oacute;ng v&agrave; dễ d&agrave;ng hơn.</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 18px; text-align: justify;"><strong><span style="line-height: 18.399999618530273px; font-size: 12pt;"><span style="font-family: ''Times New Roman'';">Mời bạn đ&oacute;n đọc.</span></span></strong></p>', '8605tu-hoc-ppt-2010.jpg', '2013-12-18', 0),
(31, 7, 'Phục Sức Trung Quốc', 'Hoa Mai', 'Tổng hợp TP. HCM', 20, 72000, 160, 'mềm', '5', '<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: center; line-height: 19.5px;" align="center"><span style="font-family: ''Times New Roman'';"><strong><span style="line-height: 24px; font-size: 12pt;">PHỤC SỨC TRUNG QUỐC</span></strong></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: center; line-height: 19.5px;" align="center"><span style="font-family: ''Times New Roman'';"><strong><span style="line-height: 24px; font-size: 12pt;">T&aacute;c giả: Hoa Mai</span></strong></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: center; line-height: 19.5px;" align="center"><span style="font-family: ''Times New Roman'';"><strong><span style="line-height: 24px; font-size: 12pt;">***</span></strong></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">T&ocirc;i đ&atilde; đọc cuốn&nbsp;<strong>Phục sức Trung Quốc</strong>&nbsp;do ThS. Tống Thị Quỳnh Hoa dịch, t&ocirc;i rất cảm ơn ThS. Quỳnh Hoa v&agrave; Nh&agrave; xuất bản Tổng hợp Th&agrave;nh phố Hồ Ch&iacute; Minh đ&atilde; tin tưởng giao cho t&ocirc;i viết lời giới thiệu cuốn s&aacute;ch n&agrave;y.</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">Trung Quốc l&agrave; một quốc gia c&oacute; tới 56 d&acirc;n tộc, c&oacute; nền văn h&oacute;a đa dạng, phong ph&uacute;, trong đ&oacute; c&oacute; văn h&oacute;a về phục sức. C&ugrave;ng với sự dung h&ograve;a giữa c&aacute;c d&acirc;n tộc; v&agrave; c&ugrave;ng với thời gian, trang phục trang sức đ&atilde; kh&ocirc;ng ngừng thay đổi. Sự thay đổi kh&ocirc;ng chỉ giữa c&aacute;c thời đại kh&aacute;c nhau, m&agrave; trong c&ugrave;ng một thời đại cũng c&oacute; sự kh&aacute;c biệt r&otilde; rệt.</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">Trang phục c&aacute;c d&acirc;n tộc Trung Quốc mang đặc trưng rất ri&ecirc;ng, c&oacute; nguồn gốc từ cuộc sống d&acirc;n gian v&agrave; c&aacute;c phong tục tập qu&aacute;n, mang đậm bản sắc qu&ecirc; hương, c&oacute; sức sống m&atilde;nh liệt, nhiều trang phục được lưu truyền từ cổ xưa cho đến ng&agrave;y nay. Những yếu tố như m&ocirc;i trường sinh hoạt, phương thức sản xuất, phong tục tập qu&aacute;n v&agrave; khiếu thẩm mỹ m&agrave;u sắc, kiểu d&aacute;ng, họa tiết trang tr&iacute; đều thể hiện tr&ecirc;n từng trang phục.</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">Một đất nước c&oacute; bề d&agrave;y lịch sử như Trung Quốc, lịch sử ph&aacute;t triển phục sức được ghi lại trong rất nhiều tư liệu khảo cổ, trong c&aacute;c t&aacute;c phẩm văn học c&oacute; nội dung li&ecirc;n quan như thần thoại, thơ ca, tiểu thuyết&hellip; Để gi&uacute;p cho độc giả c&oacute; th&ecirc;m t&agrave;i liệu tham khảo, học tập v&agrave; nghi&ecirc;n cứu về phục sức Trung Quốc, ch&uacute;ng t&ocirc;i xin giới thiệu cuốn Phục sức Trung Quốc do ThS. Quỳnh Hoa dịch. Bản dịch đ&atilde; giữ được nguy&ecirc;n vẹn gi&aacute; trị của t&aacute;c phẩm. Cuốn Phục sức Trung Quốc giới thiệu một c&aacute;ch c&oacute; hệ thống về qu&aacute; tr&igrave;nh h&igrave;nh th&agrave;nh v&agrave; ph&aacute;t triển phục sức Trung Quốc qua c&aacute;c thời k&igrave; lịch sử, l&agrave;m cho t&acirc;m tr&iacute; người đọc c&oacute; thể quay trở lại đất nước Trung Quốc cổ xưa, t&igrave;m hiểu về chất liệu, kĩ thuật chế t&aacute;c, kiểu d&aacute;ng, hoa văn trang tr&iacute; v&agrave; c&aacute;c phụ kiện k&egrave;m theo trang phục từ thời cổ đại cho đến ng&agrave;y nay. Ngo&agrave;i ra, trong cuốn s&aacute;ch c&ograve;n c&oacute; những h&igrave;nh ảnh minh họa sinh động, giới thiệu về từng bộ trang phục, từng m&oacute;n phụ kiện đặc trưng của từng thời k&igrave;.</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">Dưới g&oacute;c độ lịch sử văn h&oacute;a, cuốn s&aacute;ch mang nội dung phong ph&uacute;, đa dạng, cụ thể v&agrave; sinh động, đem đến cho người đọc nhiều th&ocirc;ng tin c&oacute; gi&aacute; trị trong việc t&igrave;m hiểu về lịch sử phục sức Trung Quốc v&agrave; cũng l&agrave; nguồn tư liệu cho s&aacute;ng t&aacute;c thời trang vừa mang t&iacute;nh thời đại vừa giữ được bản sắc truyền thống của phục sức Trung Quốc.</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">Xin tr&acirc;n trọng giới thiệu c&ugrave;ng độc giả.</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: right; line-height: 19.5px;" align="right"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">TP. Hồ Ch&iacute; Minh ng&agrave;y 16 th&aacute;ng 11 năm 2012</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: right; line-height: 19.5px;" align="right"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">Người giới thiệu</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: right; line-height: 19.5px;" align="right"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">NGND. GS.TS Ng&ocirc; Văn Lệ</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: right; line-height: 19.5px;" align="right"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">Nguy&ecirc;n Hiệu trưởng Trường ĐHKHXH &amp; NV TP. HCM</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><strong><span style="line-height: 24px; font-size: 12pt;">Mời bạn đ&oacute;n đọc.</span></strong></span></p>', 'phucsuc.jpg', '2013-12-18', 0),
(32, 7, 'Hí Kịch Trung Quốc', 'Phó Cẩn', 'Tổng hợp TP. HCM', 47, 62000, 144, 'mềm', '10', '<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: center; line-height: 19.5px;" align="center"><span style="font-family: ''Times New Roman'';"><strong><span style="line-height: 24px; font-size: 12pt;">H&Iacute; KỊCH TRUNG QUỐC</span></strong></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: center; line-height: 19.5px;" align="center"><span style="font-family: ''Times New Roman'';"><strong><span style="line-height: 24px; font-size: 12pt;">T&aacute;c giả: Ph&oacute; Cẩn</span></strong></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: center; line-height: 19.5px;" align="center"><span style="font-family: ''Times New Roman'';"><strong><span style="line-height: 24px; font-size: 12pt;">***</span></strong></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">Trung Quốc l&agrave; đất nước rộng lớn, nơi c&aacute;c h&igrave;nh th&aacute;i hoạt động của h&iacute; kịch v&ocirc; c&ugrave;ng phong ph&uacute;, đa dạng v&agrave; được ph&acirc;n bố khắp c&aacute;c v&ugrave;ng l&atilde;nh thổ.</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">H&igrave;nh thức h&iacute; kịch thường gặp nhất ở Trung Quốc b&acirc;y giờ l&agrave; sự kết hợp giữa ca h&aacute;t v&agrave; biểu diễn. Đ&acirc;y l&agrave; một hoạt động nghệ thuật mang t&iacute;nh đại diện v&agrave; độc đ&aacute;o của loại h&igrave;nh nghệ thuật h&iacute; kịch Trung Quốc. H&iacute; kịch c&oacute; nguồn gốc từ thế kỉ thứ XII, xuất hiện muộn hơn so với kịch cổ Hi Lạp v&agrave; Ấn Độ. Trong thời gian d&agrave;i tr&ecirc;n 800 năm, h&iacute; kịch Trung Quốc vẫn giữ được những h&igrave;nh th&aacute;i cơ bản của n&oacute;. Do vậy, trong c&aacute;c loại h&igrave;nh h&iacute; kịch được lưu truyền rộng r&atilde;i hiện nay tr&ecirc;n thế giới, h&iacute; kịch Trung Quốc c&oacute; thể vẫn l&agrave; một trong những loại h&igrave;nh kịch c&oacute; lịch sử l&acirc;u đời nhất.</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">Với t&igrave;nh tiết phong ph&uacute;, cộng th&ecirc;m h&igrave;nh thức biểu diễn gi&agrave;u t&iacute;nh nghệ thuật, h&iacute; kịch được nhiều thế hệ người d&acirc;n Trung Quốc y&ecirc;u mến. Trong biểu diễn h&iacute; kịch, h&igrave;nh thức hư cấu được sử dụng nhiều, điệu h&aacute;t được qui định bởi &acirc;m luật rất đặc biệt, lời thoại của nh&acirc;n vật tu&acirc;n thủ theo ni&ecirc;m luật thơ, trong vũ kịch c&ograve;n sử dụng những thế v&otilde; trong v&otilde; thuật để tạo kịch t&iacute;nh khi biểu diễn.</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">Bốn h&igrave;nh thức biểu diễn cơ bản của h&iacute; kịch Trung Quốc l&agrave; &ldquo;xướng, niệm, t&aacute;c, đả&rdquo;. Những h&igrave;nh thức biểu diễn n&agrave;y dựa tr&ecirc;n nguy&ecirc;n tắc hư cấu (như l&ecirc;n lầu, diễn vi&ecirc;n chỉ cần l&agrave;m động t&aacute;c v&eacute;n &aacute;o, nhấc ch&acirc;n; đ&oacute;ng mở cửa, tr&ecirc;n s&acirc;n khấu kh&ocirc;ng c&oacute; cửa, th&igrave; căn cứ theo động t&aacute;c thể hiện của tay; hoặc tay diễn vi&ecirc;n thay thế cho đạo cụ l&agrave; cương ngựa hay m&aacute;i ch&egrave;o, phối hợp với c&aacute;c động t&aacute;c cơ thể, c&oacute; thể biểu diễn động t&aacute;c cưỡi ngựa hoặc ch&egrave;o thuyền), do vậy tr&ecirc;n cơ sở tả thực với sự biến h&oacute;a v&agrave; chọn lọc, l&agrave;m cho c&aacute;c động t&aacute;c của diễn vi&ecirc;n tr&ecirc;n s&acirc;n khấu th&ecirc;m mĩ miều, động t&aacute;c tay, &aacute;nh mắt, cơ thể của diễn vi&ecirc;n đều c&oacute; &yacute; nghĩa rất phong ph&uacute;, tức l&agrave; c&oacute; t&iacute;nh tường thuật rất cao, thể hiện sinh động trạng th&aacute;i t&acirc;m l&iacute; vui buồn y&ecirc;u gh&eacute;t của nội t&acirc;m nh&acirc;n vật. Giọng h&aacute;t v&agrave; lời thoại mang t&iacute;nh nhạc cao đ&atilde; nhấn mạnh chức năng trữ t&igrave;nh của h&iacute; kịch. Điều n&agrave;y l&agrave;m cho h&iacute; kịch Trung Quốc c&oacute; ưu thế đặc biệt trong việc xử l&iacute; v&agrave; thể hiện t&acirc;m l&iacute; uyển chuyển, tinh tế của nh&acirc;n vật trong những trạng th&aacute;i phức tạp. Trong kết cấu chỉnh thể của h&iacute; kịch Trung Quốc, nh&acirc;n vật ch&iacute;nh ho&agrave;n th&agrave;nh vai diễn của m&igrave;nh th&ocirc;ng qua l&agrave;n điệu h&aacute;t, đặc biệt ở những ph&acirc;n kh&uacute;c ch&iacute;nh sẽ c&oacute; l&agrave;n điệu tiết tấu d&agrave;i v&agrave; sự thay đổi t&acirc;m l&iacute; để lại ấn tượng s&acirc;u sắc cho người xem. Nghệ sĩ h&iacute; kịch xuất sắc thường được người xem y&ecirc;u th&iacute;ch bởi khả năng ca h&aacute;t với &acirc;m giọng đặc biệt. H&iacute; kịch cũng c&oacute; nhiều trường ph&aacute;i kh&aacute;c nhau được ph&acirc;n biệt bởi &acirc;m nhạc, chất giọng đặc trưng v&agrave; khả năng biểu diễn tr&ecirc;n s&acirc;n khấu.</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">X&eacute;t tr&ecirc;n nguy&ecirc;n tắc mĩ học nghệ thuật biểu diễn, s&acirc;n khấu h&iacute; kịch truyền thống Trung Quốc cũng c&oacute; t&iacute;nh hư cấu rất cao. Chỉ với một c&aacute;i b&agrave;n, hai c&aacute;i ghế v&agrave; một v&agrave;i đạo cụ đơn giản, nhưng kh&ocirc;ng gian v&agrave; hoạt cảnh của kịch truyện đều được giới thiệu đến kh&aacute;n giả th&ocirc;ng qua nghệ thuật hư cấu trong biểu diễn cũng như ca từ, lời thoại của diễn vi&ecirc;n. Do vậy, h&iacute; kịch c&oacute; t&iacute;nh linh hoạt cao, c&oacute; thể thay đổi hoặc di chuyển tự do. V&iacute; dụ diễn vi&ecirc;n đi một v&ograve;ng tr&ograve;n tr&ecirc;n s&acirc;n khấu l&agrave; đ&atilde; c&oacute; thể biểu thị việc b&ocirc;n ba trăm n&uacute;i ng&agrave;n s&ocirc;ng.</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">Sự h&oacute;a trang của c&aacute;c nh&acirc;n vật h&iacute; kịch lu&ocirc;n tu&acirc;n thủ theo một quy tắc nhất định. Trang phục của nh&acirc;n vật lịch sử được thay đổi dựa tr&ecirc;n cơ sở trang phục đời nh&agrave; Minh (1368 - 1644). Điều n&agrave;y đ&atilde; th&agrave;nh th&ocirc;ng lệ v&agrave; d&ugrave;ng trong hầu như to&agrave;n bộ c&aacute;c nh&acirc;n vật h&iacute; kịch trong c&aacute;c thời đại. Nh&acirc;n vật h&iacute; kịch h&oacute;a trang rất đậm, đặc biệt một bộ phận nh&acirc;n vật nam c&oacute; gương mặt h&oacute;a trang cố định, tạo h&igrave;nh hơi khuyếch trương nhưng độc đ&aacute;o, thường c&oacute; ngụ &yacute; ri&ecirc;ng. V&iacute; dụ như mặt đỏ biểu thị l&ograve;ng trung dũng ch&iacute;nh nghĩa, mặt đen chỉ người th&ocirc; m&atilde;ng bộc trực, mặt to v&agrave; trắng biểu trưng kẻ nham hiểm, xảo tr&aacute;.</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">H&iacute; kịch đều lấy &ldquo;xướng, niệm, t&aacute;c, đả&rdquo; l&agrave;m thủ ph&aacute;p biểu diễn cơ bản, nhưng h&iacute; kịch c&aacute;c v&ugrave;ng lại ph&acirc;n th&agrave;nh c&aacute;c loại h&igrave;nh kịch kh&aacute;c nhau. Ranh giới của c&aacute;c loại h&igrave;nh kịch, đầu ti&ecirc;n l&agrave; &acirc;m nhạc kh&aacute;c nhau, như kh&aacute;c nhau về tiết tấu, l&agrave;n điệu, nhạc cụ đệm. M&agrave; những sự kh&aacute;c nhau n&agrave;y lại c&oacute; li&ecirc;n quan đến n&eacute;t dị biệt về thanh điệu ở phương ng&ocirc;n. Diện t&iacute;ch l&atilde;nh thổ Trung Quốc rộng lớn, ng&ocirc;n ngữ c&aacute;c v&ugrave;ng kh&aacute;c nhau rất nhiều, n&ecirc;n để hiểu được tự sự v&agrave; trữ t&igrave;nh của c&aacute;c nh&acirc;n vật h&iacute; kịch th&igrave; người xem phải biết được phương ng&ocirc;n của c&aacute;c v&ugrave;ng. Ngược lại khi diễn xướng, diễn vi&ecirc;n phải sử dụng phương ng&ocirc;n của từng v&ugrave;ng khi diễn xướng ở nơi đ&oacute;. Thanh điệu v&agrave; vần của phương ng&ocirc;n sẽ ảnh hưởng tới tiết tấu &acirc;m nhạc ở một mức độ n&agrave;o đ&oacute;. Trong lịch sử Trung Quốc đ&atilde; từng c&oacute; hơn 300 loại h&igrave;nh kịch kh&aacute;c nhau của c&aacute;c v&ugrave;ng. Hiện vẫn c&ograve;n khoảng 200 loại đang được lưu truyền. Điều n&agrave;y đ&atilde; thể hiện được sự phong ph&uacute; v&agrave; sự đa dạng phong c&aacute;ch của h&iacute; kịch Trung Quốc.</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">Từ đầu thế kỉ XX, do ảnh hưởng của nghệ thuật biểu diễn phương T&acirc;y, n&ecirc;n ở Trung Quốc cũng xuất hiện &ldquo;kịch n&oacute;i&rdquo; kh&ocirc;ng d&ugrave;ng thủ thuật h&aacute;t. Một trăm năm trở lại đ&acirc;y, kịch n&oacute;i đ&atilde; cắm rễ s&acirc;u v&agrave; dung h&ograve;a với văn h&oacute;a Trung Quốc, v&agrave; đ&acirc;y l&agrave; một trong những loại h&igrave;nh kịch c&oacute; ảnh hưởng nhiều nhất ở Trung Quốc. Sự ph&aacute;t triển v&agrave; phồn vinh của kịch n&oacute;i tăng th&ecirc;m một bức tranh mới cho h&iacute; kịch Trung Quốc, l&agrave;m cho nền nghệ thuật biểu diễn của Trung Quốc th&ecirc;m phong ph&uacute;, đa dạng ở g&oacute;c nh&igrave;n to&agrave;n cảnh.</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><strong><span style="line-height: 24px; font-size: 12pt;">Mời bạn đ&oacute;n đọc.</span></strong></span></p>', '5043hi-kich-trung-quoc.jpg', '2013-12-18', 0),
(33, 7, 'Văn Hóa Nam Bộ Qua Cái Nhìn Của Sơn Nam', 'Võ Văn Thành', 'Trẻ', 60, 45000, 115, 'mềm', '0', '<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: center; line-height: 19.5px;" align="center"><span style="font-family: ''Times New Roman'';"><strong><span style="line-height: 24px; font-size: 12pt;">VĂN H&Oacute;A NAM BỘ QUA C&Aacute;I NH&Igrave;N CỦA SƠN NAM</span></strong></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: center; line-height: 19.5px;" align="center"><span style="font-family: ''Times New Roman'';"><strong><span style="line-height: 24px; font-size: 12pt;">T&aacute;c giả: V&otilde; Văn Th&agrave;nh</span></strong></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: center; line-height: 19.5px;" align="center"><span style="font-family: ''Times New Roman'';"><strong><span style="line-height: 24px; font-size: 12pt;">***</span></strong></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><strong><span style="line-height: 24px; font-size: 12pt;">Văn h&oacute;a Nam Bộ qua c&aacute;i nh&igrave;n của Sơn Nam</span></strong><span style="line-height: 24px; font-size: 12pt;">&nbsp;l&agrave; tập s&aacute;ch bi&ecirc;n khảo về văn h&oacute;a học của một v&ugrave;ng miền Nam Bộ qua c&aacute;c t&aacute;c phẩm của nh&agrave; văn Sơn Nam.</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">Từ một luận văn thạc sĩ, t&aacute;c giả đ&atilde; xử l&yacute; lại tr&ecirc;n tinh thần khoa học để gi&uacute;p bạn đọc cảm nhận được c&aacute;c tầng văn h&oacute;a c&ugrave;ng miền đặc trưng Nam Bộ trong c&aacute;c s&aacute;ng t&aacute;c v&agrave; bi&ecirc;n khảo của Sơn Nam.</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">T&aacute;c giả đưa người đọc đi từ những cảm nhận về văn h&oacute;a vật thể như văn h&oacute;a mưu sinh, ảm thực, trang phục, cư tr&uacute;, giao th&ocirc;ng đến những văn h&oacute;a phi vật thể như văn h&oacute;a tổ chức cộng đồng, t&iacute;n ngưỡng, phong tục, lễ hội v&agrave; văn h&oacute;a nghệ thuật để người đọc thấy nh&agrave; văn Sơn Nam đ&atilde; d&agrave;nh suốt cuộc đời m&igrave;nh cho sự nghiệp ghi nhận v&agrave; phổ biến những n&eacute;t văn h&oacute;a cao đẹp của người Nam Bộ l&agrave; một c&ocirc;ng việc v&ocirc; c&ugrave;ng lớn lao v&agrave; nghi&ecirc;m t&uacute;c.</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><strong><span style="line-height: 24px; font-size: 12pt;">Mời bạn đ&oacute;n đọc.</span></strong></span></p>', '4810van-hoa-nam-bo-qua-cai-nhin-cua-son-nam.jpg', '2013-12-18', 0),
(34, 7, 'Salam! Chào Xứ Ba Tư', 'Hồ Anh Thái', 'Trẻ', 45, 110000, 428, 'mềm', '10', '<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: center; line-height: 19.5px;" align="center"><span style="font-family: ''Times New Roman'';"><strong><span style="line-height: 24px; font-size: 12pt;">SALAM! CH&Agrave;O XỨ BA TƯ</span></strong></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: center; line-height: 19.5px;" align="center"><span style="font-family: ''Times New Roman'';"><strong><span style="line-height: 24px; font-size: 12pt;">T&aacute;c giả: Hồ Anh Th&aacute;i</span></strong></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: center; line-height: 19.5px;" align="center"><span style="font-family: ''Times New Roman'';"><strong><span style="line-height: 24px; font-size: 12pt;">***</span></strong></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">C&ugrave;ng với&nbsp;<strong>Namaska! Xin ch&agrave;o Ấn Độ, Salam!</strong>&nbsp;<strong>Ch&agrave;o xứ Ba Tư</strong>&nbsp;l&agrave;m th&agrave;nh một bộ s&aacute;ch về hai nền văn h&oacute;a l&acirc;u đời nhất của nh&acirc;n loại.</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><strong><span style="line-height: 24px; font-size: 12pt;">&ldquo;Salam! Ch&agrave;o Ba Tư&rdquo;</span></strong><span style="line-height: 24px; font-size: 12pt;">&nbsp;giới thiệu với bạn đọc xứ sở Ba Tư xưa - nơi được biết đến từ những c&acirc;u chuyện trong Ngh&igrave;n lẻ một đ&ecirc;m v&agrave; Ngh&igrave;n lẻ một ng&agrave;y. Chỉ l&agrave; qua những d&ograve;ng chữ th&ocirc;i, nhưng bạn sẽ hiểu được tại sao xứ n&agrave;y lại nổi tiếng như thế, biết về &yacute; nghĩa những h&igrave;nh ảnh, hoa văn dệt tr&ecirc;n những bức thảm len từ l&ocirc;ng cừu tuyệt mỹ, sẽ đắm ch&igrave;m trong kiến tr&uacute;c đền đ&agrave;i cung điện gi&aacute;o đường khảm gốm sứ tr&ecirc;n m&aacute;i v&ograve;m v&agrave; trang tr&iacute; tường nh&agrave; tuyệt hảo, đặc biệt ghi dấu l&agrave; nghệ thuật tranh tiểu họa, nghệ thuật thư ph&aacute;p tinh xảo trong những bức tranh ho&agrave;nh tr&aacute;ng.</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">&ldquo;Tất cả đều nhằm đem lại cho người đọc một ấn tượng gợi mở về Iran.&rdquo; Cuốn s&aacute;ch ph&ugrave; hợp với đ&ocirc;ng đảo bạn đọc, đặc biệt l&agrave; những người th&iacute;ch du lịch v&agrave; l&agrave;m về văn h&oacute;a.</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><strong><span style="line-height: 24px; font-size: 12pt;">Mời bạn đ&oacute;n đọc.</span></strong></span></p>', '5271salam-chao-xu-ba-tu.jpg', '2013-12-18', 0),
(35, 8, 'Dưới Ánh Sáng Của Thiền', 'Mike George', 'Hồng Đức - First News', 55, 48000, 197, 'mềm', '10', '<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 18px; text-align: justify;"><strong><span style="line-height: 18.399999618530273px; font-size: 12pt;"><span style="font-family: ''Times New Roman'';">Dưới &Aacute;nh S&aacute;ng Của Thiền &ndash; Nghệ Thuật Gi&uacute;p Kh&aacute;m Ph&aacute; V&agrave; Ph&aacute;t Huy Những Tố Chất Trong T&acirc;m Hồn.</span></span></strong></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 18px; text-align: justify;"><span style="line-height: 18.399999618530273px; font-size: 12pt;"><span style="font-family: ''Times New Roman'';">Thiền định l&agrave; con đường dẫn đến sự th&ocirc;ng th&aacute;i, gi&uacute;p kh&aacute;m ph&aacute; vẻ đẹp của t&acirc;m hồn; từ đ&oacute; khơi dậy nguồn sức mạnh nội t&acirc;m v&agrave; nhận thức r&otilde; mục đ&iacute;ch sống thật sự. Trong bầu kh&ocirc;ng kh&iacute; thế giới ng&agrave;y c&agrave;ng nặng nề như hiện nay, khi m&agrave; stress đ&atilde; trở th&agrave;nh "căn bệnh thời thượng" th&igrave; thiền ch&iacute;nh l&agrave; c&aacute;ch r&egrave;n luyện t&acirc;m tr&iacute; nhẹ nh&agrave;ng bằng nghệ thuật tập trung, cho ta cơ hội lắng s&acirc;u v&agrave;o suối nguồn b&igrave;nh y&ecirc;n nội t&acirc;m m&agrave; vẫn c&oacute; thể tập trung ho&agrave;n th&agrave;nh tốt c&ocirc;ng việc thường nhật.</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 18px; text-align: justify;"><span style="line-height: 18.399999618530273px; font-size: 12pt;"><span style="font-family: ''Times New Roman'';">Thực h&agrave;nh thiền mở mắt theo phương ph&aacute;p thiền Raja Yoga sẽ gi&uacute;p bạn dễ d&agrave;ng tạo trạng th&aacute;i &yacute; thức đ&uacute;ng trong khi vẫn nhận thức về sự việc diễn ra xung quanh; vẫn giữ được b&igrave;nh an nội t&acirc;m trong khi mọi người xung quanh đang hoảng loạn, sợ h&atilde;i; cho ta khả năng nh&igrave;n r&otilde; đ&acirc;u l&agrave; sự thật v&agrave; đ&acirc;u l&agrave; giả dối; đồng thời gi&uacute;p ta đưa ra những quyết định đ&uacute;ng, kh&ocirc;ng bị ảnh hưởng từ người kh&aacute;c hoặc từ những cảm x&uacute;c "ph&aacute; bĩnh" của ch&iacute;nh m&igrave;nh.</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 18px; text-align: justify;"><span style="line-height: 18.399999618530273px; font-size: 12pt;"><span style="font-family: ''Times New Roman'';">Mọi người đến với thiền v&igrave; nhiều l&yacute; do. Phần đ&ocirc;ng mong muốn c&oacute; được b&igrave;nh an nội t&acirc;m, một số người xem thiền l&agrave; một phần trong h&agrave;nh tr&igrave;nh t&igrave;m kiếm ch&acirc;n l&yacute;, c&ograve;n số kh&aacute;c lại ấp ủ niềm hy vọng kh&aacute;m ph&aacute; kho b&aacute;u hạnh ph&uacute;c đ&iacute;ch thực. Nh&igrave;n chung, thiền c&oacute; thể đ&aacute;p ứng tất cả những ước mong đ&oacute;. Thế nhưng ch&uacute;ng ta thường mắc phải sai lầm khi cho rằng c&oacute; thể học thiền đơn giản bằng c&aacute;ch nghe giảng qua băng, đĩa hay l&agrave; đọc s&aacute;ch về thiền. Bằng l&yacute; tr&iacute;, ta c&oacute; thể hiểu được c&aacute;c bước thực h&agrave;nh v&agrave; lợi &iacute;ch của thiền, tuy nhi&ecirc;n những lợi &iacute;ch đ&oacute; chỉ ph&aacute;t huy gi&aacute; trị khi ta luyện tập v&agrave; trực tiếp trải nghiệm đều đặn mỗi ng&agrave;y. "Dưới d&aacute;nh s&aacute;ng của Thiền" được soạn thảo v&agrave; tr&igrave;nh b&agrave;y dưới dạng một kh&oacute;a học thiền với mục đ&iacute;ch gi&uacute;p bạn dễ d&agrave;ng đạt được trải nghiệm b&igrave;nh an, hạnh ph&uacute;c&hellip; như mong muốn.</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 18px; text-align: justify;"><span style="line-height: 18.399999618530273px; font-size: 12pt;"><span style="font-family: ''Times New Roman'';">...</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 18px; text-align: justify;"><strong><span style="line-height: 18.399999618530273px; font-size: 12pt;"><span style="font-family: ''Times New Roman'';">Mời bạn đ&oacute;n đọc.</span></span></strong></p>', '7253duoi-anh-sang-cua-thien.jpg', '2013-12-18', 0);
INSERT INTO `sach` (`sach_id`, `dmuc_id`, `sach_name`, `tacgia`, `nxb`, `soluong`, `dongia`, `sotrang`, `loaibia`, `giamgia`, `gioithieu`, `hinhanh`, `post_date`, `luot_mua`) VALUES
(36, 8, 'Barca - Đường Đến Vinh Quang', 'Graham Hunter', 'Thể dục thể thao - Alphabooks', 50, 149000, 612, 'mềm', '10', '<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: center; line-height: 19.5px;" align="center"><span style="font-family: ''Times New Roman'';"><strong><span style="line-height: 24px; font-size: 12pt;">BARCA - ĐƯỜNG ĐẾN VINH QUANG</span></strong></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: center; line-height: 19.5px;" align="center"><span style="font-family: ''Times New Roman'';"><strong><span style="line-height: 24px; font-size: 12pt;">T&aacute;c giả: Graham Hunter</span></strong></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: center; line-height: 19.5px;" align="center"><span style="font-family: ''Times New Roman'';"><strong><span style="line-height: 24px; font-size: 12pt;">***</span></strong></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><strong><span style="line-height: 24px; font-size: 12pt;">Barcelona</span></strong><span style="line-height: 24px; font-size: 12pt;">&nbsp;- c&acirc;u lạc bộ b&oacute;ng đ&aacute; xuất sắc nhất thế giới hiện nay, với những cầu thủ vĩ đại nhất trong thế hệ của họ v&agrave; c&oacute; thể sẽ l&agrave; vĩ đại nhất mọi thời đại. Cuốn s&aacute;ch&nbsp;<strong>Barca - Đường Đến Vinh Quang</strong>&nbsp;l&agrave; những c&acirc;u chuyện chưa kể về h&agrave;nh tr&igrave;nh m&agrave; đội b&oacute;ng xuất sắc nhất v&agrave; được y&ecirc;u th&iacute;ch nhất thế giới định nghĩa lại lối chơi m&ocirc;n thể thao vua, tạo n&ecirc;n một phong c&aacute;ch b&oacute;ng đ&aacute; l&agrave;m nức l&ograve;ng kh&aacute;n giả v&agrave; l&ocirc;i cuốn h&agrave;ng triệu t&iacute;n đồ t&uacute;c cầu gi&aacute;o.</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">Cuốn&nbsp;<strong>Barca - Đường Đến Vinh Quang</strong>&nbsp;mở đầu bằng trận Chung kết C1 năm 2011 tại Wembley, trận cầu l&agrave; tiền đề cho cuộc tranh c&atilde;i về việc liệu Barca c&oacute; phải l&agrave; đội b&oacute;ng vĩ đại nhất thế giới ở thời điểm hiện tại v&agrave; rồi lại tiếp tục đi t&igrave;m c&acirc;u trả lời cho c&acirc;u hỏi: Họ c&oacute; phải l&agrave; đội b&oacute;ng vĩ đại nhất mọi thời đại kh&ocirc;ng? Trong h&agrave;nh tr&igrave;nh kiếm t&igrave;m đ&oacute;, ch&uacute;ng ta cũng sẽ được kh&aacute;m ph&aacute; những b&iacute; mật trong ph&ograve;ng thay đồ, t&igrave;m hiểu những điều ẩn chứa trong chiến lược ph&aacute;t triển đội b&oacute;ng của huấn luyện vi&ecirc;n Pep Guardiola cũng như c&acirc;u chuyện v&ocirc; c&ugrave;ng th&uacute; vị về những con người vĩ đại đ&atilde; l&agrave;m n&ecirc;n một phần lịch sử của Barca:&nbsp;<strong>Xavi</strong>,&nbsp;<strong>Andres Iniesta</strong>,&nbsp;<strong>Puyol</strong>,&nbsp;<strong>Pique</strong>&hellip;. v&agrave; &ldquo;huyền thoại tuổi 24&rdquo;&nbsp;<strong>Lionel Messi</strong></span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><strong><span style="line-height: 24px; font-size: 12pt;">Những lời khen d&agrave;nh cho cuốn s&aacute;ch:</span></strong></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">&ldquo;Chưa ai khiến ch&uacute;ng t&ocirc;i phải vất vả đến thế, nhưng&nbsp;<strong>Bar&ccedil;a</strong>&nbsp;xứng đ&aacute;ng với danh hiệu đ&oacute;. Trong suốt sự nghiệp cầm qu&acirc;n của t&ocirc;i, họ l&agrave; đối thủ xuất sắc nhất m&agrave; ch&uacute;ng t&ocirc;i từng đối mặt.&rdquo;&nbsp;<strong>&ndash; Sir Alex Ferguson</strong></span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">&ldquo;Điều vượt qua mọi l&yacute; lẽ h&ugrave;ng hồn nhất, thậm ch&iacute; đối với cả những đội b&oacute;ng từng l&agrave; nạn nh&acirc;n của đo&agrave;n qu&acirc;n Guardiola, đ&oacute; l&agrave; Bar&ccedil;a đ&atilde; mang đến ngọn lửa hừng hực nhiệt huyết cho nền b&oacute;ng đ&aacute;. V&agrave; chỉ ri&ecirc;ng điều đ&oacute; th&ocirc;i cũng đủ khiến t&ocirc;i tin tưởng v&agrave; d&agrave;nh t&acirc;m huyết cho cuốn s&aacute;ch n&agrave;y.&rdquo;&nbsp;<strong>- Graham Hunter</strong></span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><strong><span style="line-height: 24px; font-size: 12pt;">V&agrave;i n&eacute;t về t&aacute;c giả:</span></strong></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><strong><span style="line-height: 24px; font-size: 12pt;">Graham Hunter&nbsp;</span></strong><span style="line-height: 24px; font-size: 12pt;">l&agrave; nh&agrave; b&aacute;o chuy&ecirc;n viết về những t&ecirc;n tuổi h&agrave;ng đầu quốc tế, bao qu&aacute;t tất cả c&aacute;c vấn đề về b&oacute;ng đ&aacute; T&acirc;y Ban Nha cũng như FC Barcelona cho h&atilde;ng truyền h&igrave;nh Sky Sports, BBC v&agrave; c&aacute;c tờ b&aacute;o, tạp ch&iacute; tr&ecirc;n to&agrave;n thế giới.</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">Mối li&ecirc;n kết của &ocirc;ng với đội b&oacute;ng v&ocirc; c&ugrave;ng lớn v&agrave; &ocirc;ng l&agrave; ph&oacute;ng vi&ecirc;n n&oacute;i tiếng Anh duy nhất được phỏng vấn Pep Guardiola trong suốt thời kỳ Guardiola đảm nhiệm vị tr&iacute; huấn luyện vi&ecirc;n đội b&oacute;ng n&agrave;y.</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><strong><span style="line-height: 24px; font-size: 12pt;">Mời bạn đ&oacute;n đọc.</span></strong></span></p>', 'bar.jpg', '2013-12-18', 0),
(37, 8, 'Arsènal - Cuộc Lột Xác Ngoạn Ngục Của Một Siêu Cường Bóng Đá Đương Đại', 'Alex Fynn', 'Thể dục thể thao - Alphabooks', 42, 12000, 440, 'mềm', '10', '<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: center; line-height: 19.5px;" align="center"><span style="font-family: ''Times New Roman'';"><strong><span style="line-height: 24px; font-size: 12pt;">ARS&Egrave;NAL - CUỘC LỘT X&Aacute;C NGOẠN NGỤC CỦA MỘT SI&Ecirc;U CƯỜNG B&Oacute;NG Đ&Aacute; ĐƯƠNG ĐẠI</span></strong></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: center; line-height: 19.5px;" align="center"><span style="font-family: ''Times New Roman'';"><strong><span style="line-height: 24px; font-size: 12pt;">T&aacute;c giả: Alex Fynn</span></strong></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: center; line-height: 19.5px;" align="center"><span style="font-family: ''Times New Roman'';"><strong><span style="line-height: 24px; font-size: 12pt;">***</span></strong></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><strong><span style="line-height: 24px; font-size: 12pt;">C&Acirc;U CHUYỆN VỀ MỘT ĐỘI B&Oacute;NG, MỘT DOANH NGHIỆP, MỘT CON NGƯỜI</span></strong></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">Tr&ecirc;n thế giới, trong v&agrave;i năm trở lại đ&acirc;y, ng&ocirc;i vị giải đấu hấp dẫn nhất h&agrave;nh tinh đang bị đe dọa bởi giải T&acirc;y Ban Nha v&agrave; đặc biệt l&agrave; Đức, nơi cũng c&oacute; những trận đấu s&ocirc;i nổi, quyết liệt v&agrave; tốc độ cao như giải ngoại hạng Anh, v&agrave; m&agrave;u sắc, h&igrave;nh ảnh của đ&aacute;m đ&ocirc;ng cổ động vi&ecirc;n thậm ch&iacute; c&ograve;n hấp dẫn hơn cả Premier League. Tuy vậy, điều đ&oacute; gần như kh&ocirc;ng ảnh hưởng đến quan niệm ở Việt Nam. Nơi đ&acirc;y, b&oacute;ng đ&aacute; Anh vẫn l&agrave; số một kh&ocirc;ng chỉ v&igrave; bản chất s&ocirc;i động của n&oacute; m&agrave; c&ograve;n v&igrave; n&oacute; l&agrave; giải đấu b&oacute;ng đ&aacute; h&agrave;ng đầu thế giới đầu ti&ecirc;n được ph&aacute;t s&oacute;ng trực tiếp ở Việt Nam v&agrave; diễn ra v&agrave;o thời điểm dễ xem nhất. Trong những năm đầu ti&ecirc;n, c&oacute; lẽ người Việt Nam xem b&oacute;ng đ&aacute; chỉ chia l&agrave;m hai phe: phe&nbsp;<strong>Manchester United</strong>&nbsp;v&agrave; phe&nbsp;<strong>Arsenal</strong>, v&agrave; đ&acirc;y cũng l&agrave; hai đội b&oacute;ng huyền thoại của giải ngoại hạng Anh cho đến ng&agrave;y nay. Sau đ&acirc;y l&agrave; một c&acirc;u chuyện về&nbsp;<strong>Arsenal</strong>, chứa đựng cả những điều chưa từng kể hay đ&atilde; bị l&atilde;ng qu&ecirc;n, một sợi chỉ xuy&ecirc;n suốt lịch sử đội b&oacute;ng, đặc biệt người đọc sẽ thấy r&otilde; những m&acirc;u thuẫn nội bộ b&ecirc;n trong c&ugrave;ng những bước ngoặt b&ecirc;n ngo&agrave;i của giải b&oacute;ng đ&aacute; ngoại hạng Anh đ&atilde; g&oacute;p phần định h&igrave;nh n&ecirc;n một&nbsp;<strong>Arsenal</strong>&nbsp;ng&agrave;y nay như thế n&agrave;o.</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">Ngo&agrave;i ra, cuốn s&aacute;ch c&ograve;n l&agrave; những ghi ch&eacute;p hết sức ch&acirc;n thực xoay quanh yếu tố cốt l&otilde;i của bất kỳ một tổ chức n&agrave;o, đ&oacute; l&agrave; con người. Tỉ mỉ, chi tiết, t&aacute;c giả đ&atilde; đề cập đến mọi nh&acirc;n vật xuất hiện v&agrave; vai tr&ograve; của họ trong lịch sử của đội b&oacute;ng, từ c&aacute;c cổ động vi&ecirc;n, ban l&atilde;nh đạo, hội đồng quản trị v&agrave; cuối c&ugrave;ng l&agrave; huấn luyện vi&ecirc;n&nbsp;<strong>Arsene Wenger</strong>. Kh&ocirc;ng biết tại sao t&ecirc;n &ocirc;ng lại được đặt gần giống t&ecirc;n c&acirc;u lạc bộ nhưng dường như người đ&agrave;n &ocirc;ng c&oacute; biệt danh &ldquo;Gi&aacute;o sư&rdquo; n&agrave;y (v&igrave; &ocirc;ng giống một gi&aacute;o sư hơn một huấn luyện vi&ecirc;n b&oacute;ng đ&aacute;) sinh ra l&agrave; để d&agrave;nh cho&nbsp;<strong>Arsenal</strong>. Cũng như&nbsp;<strong>Sir Alex Ferguson</strong>&nbsp;của&nbsp;<strong>Manchester United</strong>, đối với&nbsp;<strong>Arsene Wenger</strong>,&nbsp;<strong>Arsenal</strong>&nbsp;l&agrave; cuộc sống v&agrave; cũng l&agrave; c&ocirc;ng tr&igrave;nh vĩ đại nhất đời &ocirc;ng. Trong cuốn s&aacute;ch n&agrave;y, độc giả sẽ thấy được vị tr&iacute; độc t&ocirc;n cũng như những triết l&yacute; b&oacute;ng đ&aacute; của&nbsp;<strong>Arsene Wenger</strong>&nbsp;được phản &aacute;nh trong những m&agrave;n tr&igrave;nh diễn, những chiến t&iacute;ch tr&ecirc;n s&acirc;n b&oacute;ng, những cuộc đấu khẩu ngo&agrave;i s&acirc;n cỏ v&agrave; những phi vụ chuyển nhượng đầy kh&ocirc;n ngoan như thế n&agrave;o.</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><strong><span style="line-height: 24px; font-size: 12pt;">Arsenal</span></strong><span style="line-height: 24px; font-size: 12pt;">&nbsp;kh&ocirc;ng chỉ l&agrave; một c&acirc;u lạc bộ b&oacute;ng đ&aacute; m&agrave; c&ograve;n l&agrave; một doanh nghiệp rất th&agrave;nh c&ocirc;ng với triết l&yacute; huyền thoại&nbsp;<strong>&ldquo;mua rẻ, b&aacute;n đắt&rdquo;.</strong>&nbsp;Cuốn s&aacute;ch cũng tr&agrave;n đầy những b&agrave;i học kinh doanh v&agrave; t&agrave;i ch&iacute;nh xoay quanh c&aacute;c sự kiện như x&acirc;y s&acirc;n&nbsp;<strong>Emirates</strong>, th&acirc;u t&oacute;m c&acirc;u lạc bộ, chuyển nhượng cầu thủ v.v... m&agrave; từ đ&oacute; c&oacute; thể &aacute;p dụng v&agrave;o bất cứ doanh nghiệp n&agrave;o. Qua đ&oacute;, ch&uacute;ng ta sẽ thấy được&nbsp;<strong>Arsenal</strong>&nbsp;tuy trước đ&acirc;y phải chắt chiu từng đồng để trả nợ v&agrave; x&acirc;y s&acirc;n vận động nhưng ng&agrave;y nay kh&ocirc;ng phải l&agrave; kh&ocirc;ng c&oacute; tiền, vậy tại sao họ chỉ b&aacute;n m&agrave; &iacute;t khi mua?</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">M&ugrave;a h&egrave; n&agrave;y sẽ l&agrave; cột mốc với&nbsp;<strong>Arsenal</strong>. Những tin đồn&nbsp;<strong>Rooney</strong>&nbsp;rồi&nbsp;<strong>Higuain</strong>&nbsp;về&nbsp;<strong>Arsenal</strong>&nbsp;liệu c&oacute; th&agrave;nh sự thực v&agrave; liệu c&oacute; đem lại thay đổi n&agrave;o sau 8 năm trắng tay? Trong bối cảnh&nbsp;<strong>Manchester United</strong>&nbsp;ng&agrave;y c&agrave;ng rực rỡ,&nbsp;<strong>Chelsea</strong>&nbsp;c&oacute; sự trở lại của &ldquo;Người đặc biệt&rdquo;&nbsp;<strong>Mourinho</strong>,&nbsp;<strong>Manchester City</strong>&nbsp;v&agrave;&nbsp;<strong>Tottenham</strong>&nbsp;mạnh l&ecirc;n tr&ocirc;ng thấy , cuộc chiến Big4 sẽ diễn ra như thế n&agrave;o? Mời c&aacute;c bạn đọc những trang s&aacute;ch n&agrave;y để c&oacute; cảm nhận r&otilde; hơn về điều đ&oacute;.</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><strong><span style="line-height: 24px; font-size: 12pt;">Mời bạn đ&oacute;n đọc.</span></strong></span></p>', '9422Arsenal.jpg', '2013-12-18', 0),
(38, 8, 'Bản Đồ Thân Tâm Trí', 'Paul Hougham', 'TĐBK - Văn Lang', 40, 12000, 472, 'mềm', '0', '<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: center; line-height: 19.5px;" align="center"><span style="font-family: ''Times New Roman'';"><strong><span style="line-height: 24px; font-size: 12pt;">BẢN ĐỒ TH&Acirc;N T&Acirc;M TR&Iacute;</span></strong></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: center; line-height: 19.5px;" align="center"><span style="font-family: ''Times New Roman'';"><strong><span style="line-height: 24px; font-size: 12pt;">T&aacute;c giả: Paul Hougham</span></strong></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: center; line-height: 19.5px;" align="center"><span style="font-family: ''Times New Roman'';"><strong><span style="line-height: 24px; font-size: 12pt;">***</span></strong></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">Với nội dung phong ph&uacute;, c&aacute;ch diễn đạt dễ hiểu, cuốn s&aacute;ch&nbsp;<strong>Bản Đồ Th&acirc;n T&acirc;m Tr&iacute;</strong>&nbsp;so s&aacute;nh nhận thức của người phương T&acirc;y về mối tương quan của&nbsp;<strong>th&acirc;n - t&acirc;m - tr&iacute;</strong>&nbsp;trong một thể thống nhất, với những quan niệm của c&aacute;c nền văn h&oacute;a cổ xưa tr&ecirc;n khắp thế giới. Đ&oacute; l&agrave; những bản đồ cơ thể, bao gồm c&aacute;c lu&acirc;n xa (chakra) - trung t&acirc;m năng lực tinh thần - của người Ấn Độ, sơ đồ huyệt đạo của người Trung Hoa, C&acirc;y Sinh Mệnh (Kabbalah) của người Do Th&aacute;i; c&aacute;c t&iacute;n ngưỡng bản địa ở ch&acirc;u Phi, ch&acirc;u Mỹ Latin. Ch&uacute;ng kết nối cuộc sống con người với vũ trụ v&agrave; được h&igrave;nh th&agrave;nh theo những đặc trưng văn h&oacute;a của c&aacute;c d&acirc;n tộc.</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">Tuy nhi&ecirc;n, tất cả những quan niệm kh&aacute;c biệt đều được thể hiện như những mạng lưới năng lực của con người trải qua qu&aacute; tr&igrave;nh tiến h&oacute;a. Vượt qua kh&aacute;c biệt, tất cả ch&uacute;ng ta đều đang tham gia v&agrave;o cuộc t&igrave;m kiếm c&aacute;c giải ph&aacute;p nhằm đạt đến sự h&ograve;a hợp&nbsp;<strong>th&acirc;n - t&acirc;m - tr&iacute;</strong>&nbsp;tr&ecirc;n phạm vi to&agrave;n nh&acirc;n loại.</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><strong><span style="line-height: 24px; font-size: 12pt;">Mời bạn đ&oacute;n đọc.</span></strong></span></p>', '7012ban-do-than-tam-tri.jpg', '2013-12-18', 0),
(39, 8, 'Messi - Từ El Pulga Đến Một Huyền Thoại', 'Luca Caioli', 'Thể dục thể thao - Alphabooks', 20, 12000, 410, 'mềm', '10', '<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 18px; text-align: center;" align="center"><span style="font-family: ''Times New Roman'';"><strong><span style="line-height: 18.399999618530273px; font-size: 12pt;">MESSI - TỪ EL PULGA ĐẾN MỘT HUYỀN THOẠI</span></strong></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: center; line-height: 19.5px;" align="center"><span style="font-family: ''Times New Roman'';"><strong><span style="line-height: 24px; font-size: 12pt;">T&aacute;c giả: Luca Caioli</span></strong></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: center; line-height: 19.5px;" align="center"><span style="font-family: ''Times New Roman'';"><strong><span style="line-height: 24px; font-size: 12pt;">***</span></strong></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">Cuốn s&aacute;ch&nbsp;<strong>Messi - Từ El Pulga Đến Một Huyền Thoại</strong>&nbsp;thực sự l&agrave; một bức tranh sống động v&agrave; đầy chi tiết th&uacute; vị, h&eacute; lộ những th&ocirc;ng tin &iacute;t người biết đến về cầu thủ tuyệt vời n&agrave;y. Đ&acirc;y thực sự l&agrave; cuốn s&aacute;ch đ&aacute;ng đọc với tất cả những ai quan t&acirc;m đến b&oacute;ng đ&aacute;, đặc biệt l&agrave; những fan ruột của m&ocirc;n thể thao vua n&oacute;i chung, của&nbsp;<strong>Barca&nbsp;</strong>v&agrave;&nbsp;<strong>Messi</strong>&nbsp;n&oacute;i ri&ecirc;ng.</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: center; line-height: 19.5px;" align="center"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">&hellip;</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">Cậu b&eacute; nhỏ con ng&agrave;y n&agrave;o giờ đ&atilde; trở thần tượng đương thời của b&oacute;ng đ&aacute; thế giới. Chỉ mới 24 tuổi, thậm ch&iacute;&nbsp;<strong>Messi</strong>&nbsp;c&ograve;n cả một tương lai trước mặt để trở th&agrave;nh một huyền thoại - cậu thủ xuất sắc nhất mọi thời đại,vượt qua bậc tiền bối đồng hương<strong>Diego Maradona</strong>.</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">Ở&nbsp;<strong>Messi</strong>, t&iacute;n đồ t&uacute;c cầu gi&aacute;o c&oacute; thể t&igrave;m thấy mọi phẩm chất của một cầu thủ vĩ đại:&nbsp;<strong>kỹ thuật</strong>,&nbsp;<strong>tốc độ</strong>,&nbsp;<strong>niềm đam m&ecirc;</strong>&nbsp;v&agrave;<strong>kh&aacute;t khao cống hiến</strong>.</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">Ngo&agrave;i những danh hi&ecirc;u tập thể gi&agrave;nh được c&ugrave;ng Barcelona, Messi c&ograve;n li&ecirc;n tiếp gi&agrave;nh được danh hiệu c&aacute; nh&acirc;n như &ldquo;Cầu thủ xuất sắc nhất thế giới&rdquo; năm 2009 v&agrave; 2010 do FIFA b&igrave;nh chọn.</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><strong><span style="line-height: 24px; font-size: 12pt;">V&agrave;i n&eacute;t về t&aacute;c giả:</span></strong></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><strong><span style="line-height: 24px; font-size: 12pt;">Luca Caioli</span></strong><span style="line-height: 24px; font-size: 12pt;">, một trong những ph&oacute;ng vi&ecirc;n thể thao h&agrave;ng đầu ch&acirc;u &Acirc;u đ&atilde; dựa tr&ecirc;n những cứ liệu độc đ&aacute;o v&agrave; ch&acirc;n thực để kể về những dấu ấn trong những năm th&aacute;ng đầu đời v&agrave; bước đường sự nghiệp của&nbsp;<strong>Messi</strong>. Đ&oacute; l&agrave; những c&acirc;u chuyện về cha mẹ, những người th&acirc;n trong gia đ&igrave;nh; c&aacute;c huấn luyện vi&ecirc;n của anh ở Grandoli v&agrave; Newell&rsquo;s Old Boys; những tiếp x&uacute;c với HLV Rjikaard v&agrave; Pep Guardiola khi ở Barcelona; mối quan hệ với Jorge Valdano v&agrave; rất nhiều c&aacute;c đồng đội kh&aacute;c ở đội tuyển Argentina v&agrave; Barca. T&aacute;c giả d&agrave;nh hẳn cả chương cuối để người h&ugrave;ng s&acirc;n Camp Nou tự chia sẻ về cuộc đời, những băn khoằn, suy tư từ khi c&ograve;n l&agrave; một cậu b&eacute; cho tới khi đ&atilde; nổi tiếng tr&ecirc;n to&agrave;n thế giới.</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><strong><span style="line-height: 24px; font-size: 12pt;">Mời bạn đ&oacute;n đọc.</span></strong></span></p>', '9748messi-tu-el-pulga-den-mot-huyen-thoai.jpg', '2013-12-18', 0),
(40, 9, 'Luật 101 - Mọi Điều Bạn Cần Biết Về Pháp Luật Hoa Kỳ', 'Jay M. Feinman', 'Hồng Đức - Alphabooks', 50, 119000, 552, 'cứng', '10', '<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: center; line-height: 19.5px;" align="center"><span style="font-family: ''Times New Roman'';"><strong><span style="line-height: 24px; color: #595959; font-size: 12pt;">LUẬT 101 - MỌI ĐIỀU BẠN CẦN BIẾT VỀ PH&Aacute;P LUẬT HOA KỲ</span></strong></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: center; line-height: 19.5px;" align="center"><span style="font-family: ''Times New Roman'';"><strong><span style="line-height: 24px; color: #595959; font-size: 12pt;">T&aacute;c giả: Jay M. Feinman</span></strong></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: center; line-height: 19.5px;" align="center"><span style="font-family: ''Times New Roman'';"><strong><span style="line-height: 24px; color: #595959; font-size: 12pt;">***</span></strong></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">Phi&ecirc;n bản đầu ti&ecirc;n v&agrave; t&aacute;i bản lần thứ hai của cuốn s&aacute;ch b&aacute;n chạy nhất, Law 101, đ&atilde; cung cấp cho độc giả một bức tranh sinh động v&agrave; ch&acirc;n thực về hệ thống ph&aacute;p luật của Hoa Kỳ. Nay, trong lần t&aacute;i bản thứ ba n&agrave;y, Jay M.Feinman đ&atilde; đưa ra c&aacute;i nh&igrave;n tổng qu&aacute;t v&agrave; cập nhật đầy đủ về hệ thống ph&aacute;p luật Hoa Kỳ, bao gồm tư liệu mới về c&aacute;c vụ kiện được T&ograve;a &Aacute;n Tối Cao Hoa Kỳ x&eacute;t xử năm 2009, kh&iacute;a cạnh ph&aacute;p l&yacute; về cuộc chiến chống khủng bố (kể cả những người bị giam giữ ở Guantanamo v&agrave; cổng từ an ninh), v&agrave; những tiến triển gần đ&acirc;y nhất của luật về Internet.</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">Trong một cuốn s&aacute;ch đề cập đến nhiều vấn đề ph&aacute;p l&yacute; nan giải, những mẩu chuyện nhỏ đầy th&uacute; vị, v&agrave; c&aacute;c c&acirc;u hỏi đ&aacute;ng phải suy ngẫm, c&aacute;ch dẫn chứng luật một c&aacute;ch r&otilde; r&agrave;ng của Jay M.Feinman gi&uacute;p ch&uacute;ng ta hiểu biết s&acirc;u sắc về truyền thống ph&aacute;p luật của Mỹ v&agrave; bao qu&aacute;t cả những m&ocirc;n học ch&iacute;nh được dạy ở năm đầu trong trường luật. Độc giả được giới thiệu mọi kh&iacute;a cạnh của hệ thống ph&aacute;p luật, từ luật hiến ph&aacute;p v&agrave; qu&aacute; tr&igrave;nh tranh tụng cho đến luật bất cẩn, luật hợp đồng, luật sở hữu tr&iacute; tuệ, v&agrave; luật h&igrave;nh sự. Mỗi vấn đề đưa ra thảo luận được Feinman dẫn chứng bằng những vụ kiện hấp dẫn, gay gắt, v&agrave; đ&aacute;ng bị h&igrave;nh phạt nghi&ecirc;m khắc, từ những vụ kiện bị bỏng do l&agrave;m đổ ly c&agrave; ph&ecirc; n&oacute;ng m&agrave; đ&atilde; l&agrave;m cho McDonald tốn nửa triệu đ&ocirc; la, cho đến việc x&eacute;t xử vụ &aacute;n giết người g&acirc;y chấn động dư luận ở Victorian London đ&atilde; dẫn đến định nghĩa ph&aacute;p luật về t&igrave;nh trạng mất tr&iacute;, một quyết định mang t&iacute;nh lịch sử ở Marbury v. Madison rằng trao cho T&ograve;a &Aacute;n Tối Cao quyền tuy&ecirc;n bố c&aacute;c đạo luật của c&aacute;c tiểu bang v&agrave; đạo luật li&ecirc;n bang tr&aacute;i với hiến ph&aacute;p. &Ocirc;ng đ&atilde; mở rộng cho độc giả vốn từ vựng trong lĩnh vực ph&aacute;p l&yacute;, l&agrave;m r&otilde; nghĩa mọi điều từ &ldquo;nguy&ecirc;n tắc x&eacute;t xử c&ocirc;ng bằng&rdquo; v&agrave; &ldquo;quyền được bảo vệ b&igrave;nh đẳng trước ph&aacute;p luật&rdquo; trong luật hiến ph&aacute;p cho đến sự kh&aacute;c biệt giữa &ldquo;tội giết người&rdquo; v&agrave; &ldquo;tội ngộ s&aacute;t&rdquo; trong luật h&igrave;nh sự. C&oacute; lẽ quan trọng nhất l&agrave; ch&uacute;ng ta biết được rằng tất cả mọi người đều c&oacute; thể tiếp cận được với ph&aacute;p luật mặc d&ugrave; đ&oacute; l&agrave; một lĩnh vực rất rộng v&agrave; phức tạp.</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><strong><span style="line-height: 24px; font-size: 12pt;">Những lời khen d&agrave;nh cho cuốn s&aacute;ch:</span></strong></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">"Cuốn s&aacute;ch chứa đựng to&agrave;n bộ những điều hữu &iacute;ch tương đương với chương tr&igrave;nh năm học thứ nhất trường đại học luật m&agrave; kh&ocirc;ng khiến bạn cảm thấy nh&agrave;m ch&aacute;n, sợ h&atilde;i v&agrave; buồn ngủ... Những b&agrave;i học về ph&aacute;p luật n&agrave;y được tr&igrave;nh b&agrave;y theo một c&aacute;ch thức hết sức l&ocirc;i cuốn v&agrave; h&agrave;i hước".&nbsp;<strong>- Jurist</strong></span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">"Mặc d&ugrave; cuốn s&aacute;ch nhằm mục đ&iacute;ch d&agrave;nh cho những người kh&ocirc;ng chuy&ecirc;n, nhưng n&oacute; đ&atilde; cung cấp một kh&oacute;a học bồi dưỡng kiến thức cơ bản rất hay để cập nhật th&ocirc;ng tin cho những người h&agrave;nh nghề luật v&agrave; những người trợ gi&uacute;p cho chuy&ecirc;n gia luật trong c&aacute;c lĩnh vực m&agrave; họ kh&ocirc;ng chuy&ecirc;n. N&oacute;i chung, đ&acirc;y l&agrave; một cuốn s&aacute;ch hết sức đ&aacute;ng đọc đối với những người quan t&acirc;m đến luật".&nbsp;<strong>- Booklist</strong></span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">"Luật 101 cố gắng dạy bạn c&aacute;ch tư duy giống như một luật sư - m&agrave; bạn kh&ocirc;ng cần phải mất tới ba năm v&agrave; ti&ecirc;u tốn h&agrave;ng chục ng&agrave;n đ&ocirc;-la cho việc đ&oacute;. Thế c&ograve;n kết quả th&igrave; sao? Một cuốn s&aacute;ch thiết thực v&agrave; th&uacute; vị đ&aacute;ng để đọc".&nbsp;<strong>- The Trenton Times</strong></span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">"Một cuốn s&aacute;ch giới thiệu rất hấp dẫn v&agrave; chi tiết về hầu hết mọi kh&iacute;a cạnh của hệ thống ph&aacute;p luật Hoa Kỳ".&nbsp;<strong>- The American Lawyer</strong></span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><strong><span style="line-height: 24px; font-size: 12pt;">Mời bạn đ&oacute;n đọc.</span></strong></span></p>', '6297luat-101.jpg', '2013-12-18', 0),
(41, 9, 'Những Vấn Đề Về Hiến Pháp Và Sửa Đổi Hiến Pháp', 'Nguyễn Đăng Dung', 'Dân Trí - Alphabooks', 42, 74000, 340, 'cứng', '0', '<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: center; line-height: 19.5px;" align="center"><span style="font-family: ''Times New Roman'';"><strong><span style="line-height: 24px; color: #595959; font-size: 12pt;">NHỮNG VẤN ĐỀ VỀ HIẾN PH&Aacute;P V&Agrave; SỬA ĐỔI HIẾN PH&Aacute;P</span></strong></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: center; line-height: 19.5px;" align="center"><span style="font-family: ''Times New Roman'';"><strong><span style="line-height: 24px; color: #595959; font-size: 12pt;">T&aacute;c giả: Nguyễn Đăng Dung</span></strong></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: center; line-height: 19.5px;" align="center"><span style="font-family: ''Times New Roman'';"><strong><span style="line-height: 24px; color: #595959; font-size: 12pt;">***</span></strong></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">Hiến ph&aacute;p c&oacute; hiệu lực tối cao n&ecirc;n việc sửa đổi lu&ocirc;n l&agrave; điều kh&ocirc;ng đơn giản, v&igrave; t&iacute;nh tối cao của loại văn bản n&agrave;y hầu như đồng nghĩa với việc kh&ocirc;ng sửa đổi. C&agrave;ng c&oacute; sức sống l&acirc;u bền bao nhi&ecirc;u, th&igrave; hiệu lực của Hiến ph&aacute;p c&agrave;ng c&oacute; sức mạnh bấy nhi&ecirc;u. Tuy nhi&ecirc;n, những quy định mang t&iacute;nh nguy&ecirc;n tắc của Hiến ph&aacute;p phải được thực thi trong cuộc sống. Đời sống x&atilde; hội lu&ocirc;n lu&ocirc;n vận động, v&igrave; vậy Hiến ph&aacute;p cũng cần phải thay đổi để đ&aacute;p ứng sự vận động đ&oacute;. Ở nước ta, việc sửa đổi Hiến ph&aacute;p 1992 đ&atilde; được đặt ra v&agrave;o hồi đầu thế kỷ XXI đến nay.</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">H&ograve;a chung v&agrave;o c&ocirc;ng việc trọng đại đ&oacute;, c&aacute;c giảng vi&ecirc;n đam m&ecirc; nghi&ecirc;n cứu v&agrave; giảng dạy Hiến ph&aacute;p, c&ugrave;ng với một số cộng t&aacute;c vi&ecirc;n t&acirc;m huyết đ&atilde; tập hợp c&oacute; chọn lọc những b&agrave;i viết từng được c&ocirc;ng bố tr&ecirc;n c&aacute;c tạp ch&iacute;, c&aacute;c diễn đ&agrave;n, hội nghị, hội thảo th&agrave;nh một chuy&ecirc;n khảo với ti&ecirc;u đề: Những vấn đề về Hiến ph&aacute;p v&agrave; sửa đổi Hiến ph&aacute;p. Xin được giới thiệu đến qu&yacute; độc giả v&agrave; mong nhận được &yacute; kiến đ&oacute;ng g&oacute;p của c&aacute;c bạn!</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><strong><span style="line-height: 24px; font-size: 12pt;">Về t&aacute;c giả:</span></strong></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">Gi&aacute;o sư Nguyễn Đăng Dung hiện đang l&agrave; Chủ nhiệm bộ m&ocirc;n Luật h&agrave;nh ch&iacute;nh &ndash; Hiến ph&aacute;p, Khoa Luật, Đại học Quốc gia H&agrave; Nội. &Ocirc;ng đ&atilde; c&oacute; nhiều đ&oacute;ng g&oacute;p đ&atilde; được ghi nhận trong c&ocirc;ng t&aacute;c giảng dạy v&agrave; Nghi&ecirc;n cứu Khoa học. &Ocirc;ng l&agrave; t&aacute;c giả của Sự giới hạn quyền lực nh&agrave; nước, Nh&agrave; nước v&agrave; tr&aacute;ch nhiệm của nh&agrave; nước; đồng thời l&agrave; Chủ bi&ecirc;n của một số s&aacute;ch kh&aacute;c như Tư tưởng Hồ Ch&iacute; Minh về nh&agrave; nước ph&aacute;p quyền, Lịch sử c&aacute;c học thuyết ch&iacute;nh trị học, Ch&iacute;nh trị học v&agrave; mới đ&acirc;y l&agrave; Những vấn đề về Hiến ph&aacute;p v&agrave; sửa đổi Hiến ph&aacute;p.</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">&Ocirc;ng Nguyễn Cảnh B&igrave;nh l&agrave; Chủ tịch hội đồng quản trị, Gi&aacute;m đốc c&ocirc;ng ty Alphabooks, đồng thời l&agrave; Gi&aacute;m đốc trung t&acirc;m hợp t&aacute;c tr&iacute; tuệ Việt Nam VICC, tổ chức phi ch&iacute;nh phủ (NGO) thuộc trung t&acirc;m nghi&ecirc;n cứu kinh tế Ch&acirc;u &Aacute; - Th&aacute;i B&igrave;nh Dương (VAPEC). Song song với việc điều h&agrave;nh một c&ocirc;ng ty chuy&ecirc;n xuất bản s&aacute;ch về kinh tế, quản trị kinh doanh, s&aacute;ch gi&aacute;o dục, &ocirc;ng Cảnh B&igrave;nh cũng đồng thời nghi&ecirc;n cứu về luật hiến ph&aacute;p v&agrave; c&aacute;c m&ocirc; h&igrave;nh tổ chức nh&agrave; nước. &Ocirc;ng đ&atilde; dịch v&agrave; xuất bản th&agrave;nh c&ocirc;ng cuốn Hiến ph&aacute;p Mỹ được l&agrave;m ra như thế n&agrave;o? Trong Những vấn đề về Hiến ph&aacute;p v&agrave; sửa đổi Hiến ph&aacute;p, &ocirc;ng đ&atilde; c&oacute; đ&oacute;ng g&oacute;p t&iacute;ch cực với 3 b&agrave;i viết.</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">Những vấn đề về Hiến ph&aacute;p v&agrave; Sửa đổi Hiến ph&aacute;p c&ograve;n c&oacute; sự g&oacute;p mặt của c&aacute;c Giảng vi&ecirc;n thuộc Khoa Luật đại học Quốc gia H&agrave; Nội như: TS. V&otilde; Tr&iacute; Hảo, TS. Đặng Minh Tuấn, Ths. L&atilde; Kh&aacute;nh T&ugrave;ng.</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><strong><span style="line-height: 24px; font-size: 12pt;">Mời bạn đ&oacute;n đọc.</span></strong></span></p>', 'hienphap.jpg', '2013-12-18', 0),
(42, 10, 'Tuổi Thơ Dữ Dội', 'Phùng Quán', 'Văn học – Đinh Tị', 50, 145000, 784, 'mềm', '15', '<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><strong><span style="line-height: 24px; color: #ff3300; font-size: 12pt;">Tuổi Thơ Dữ Dội</span></strong></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">"... C&oacute; một vi&ecirc;n ngọc qu&yacute; thời gian d&agrave;nh ri&ecirc;ng để ban tặng con người, đ&oacute; l&agrave; Tuổi thơ. Vi&ecirc;n ngọc m&agrave;u nhiệm, trong s&aacute;ng nhưng qu&aacute; mong manh, kh&ocirc;ng thể t&igrave;m thấy lần thứ hai trong đời. V&agrave; c&oacute; một thế hệ người Việt chưa bao giờ được cầm vi&ecirc;n ngọc tr&ecirc;n tay, Tuổi thơ dữ dội của Ph&ugrave;ng Qu&aacute;n được viết cho thế hệ đ&oacute;. H&atilde;y đọc để nhớ lại, để tự h&agrave;o, v&agrave; để cầu nguyện cho những Tuổi thơ sắp ra đời..."</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: right; line-height: 19.5px;" align="right"><span style="font-family: ''Times New Roman'';"><strong><span style="line-height: 24px; font-size: 12pt;">- Nh&agrave; văn Ho&agrave;ng Phủ Ngọc Tường</span></strong></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">"... T&ocirc;i đọc v&agrave;o năm đầu Tuổi thơ dữ dội thập kỷ 60, từ những trang tư liệu của Ph&ugrave;ng Qu&aacute;n. Suốt ngh&igrave;n trang s&aacute;ch, khắc s&acirc;u v&agrave;o l&ograve;ng t&ocirc;i hai chữ Trung Hiếu. Một nỗi đau x&eacute; l&ograve;ng khi ta đọc đến lới trăng trối của Mừng, nh&acirc;n vật trong truyện, trước l&uacute;c em đi v&agrave;o c&otilde;i vĩnh hằng..."</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: right; line-height: 19.5px;" align="right"><span style="font-family: ''Times New Roman'';"><strong><span style="line-height: 24px; font-size: 12pt;">- Nh&agrave; văn Việt Linh</span></strong></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">"... Tuổi thơ dữ dội kh&ocirc;ng phải l&agrave; một c&acirc;u chuyện cổ t&iacute;ch, m&agrave; l&agrave; một c&acirc;u chuyện c&oacute; thật ở chốn trần gian, ở đ&oacute; những con người tuổi nhỏ đ&atilde; tham gia v&agrave;o cuộc kh&aacute;ng chiến chống x&acirc;m lược bảo vệ Tổ quốc với một chuỗi những chiến c&ocirc;ng đầy ắp ly kỳ v&agrave; hấp dẫn. Đọc Tuổi thơ dữ dội ch&iacute;nh l&agrave; đọc lại một phần lịch sử tuổi thơ Việt, thấm đẫm x&uacute;c động, cảm phục v&agrave; tự h&agrave;o..."</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: right; line-height: 19.5px;" align="right"><span style="font-family: ''Times New Roman'';"><strong><span style="line-height: 24px; font-size: 12pt;">- Nh&agrave; thơ, nhạc sĩ Nguyễn Trọng Tạo</span></strong></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><strong><span style="line-height: 24px; font-size: 12pt;">Mời bạn đ&oacute;n đọc.</span></strong></span></p>', 'dd.jpg', '2013-12-18', 0);
INSERT INTO `sach` (`sach_id`, `dmuc_id`, `sach_name`, `tacgia`, `nxb`, `soluong`, `dongia`, `sotrang`, `loaibia`, `giamgia`, `gioithieu`, `hinhanh`, `post_date`, `luot_mua`) VALUES
(43, 10, 'Phù Thủy Xứ OZ', 'Frank Baum', 'Văn học - Nhã Nam', 50, 50000, 224, 'mềm', '10', '<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: center; line-height: 19.5px;" align="center"><span style="font-family: ''Times New Roman'';"><strong><span style="line-height: 24px; font-size: 12pt;">PH&Ugrave; THỦY XỨ OZ</span></strong></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: center; line-height: 19.5px;" align="center"><span style="font-family: ''Times New Roman'';"><strong><span style="line-height: 24px; font-size: 12pt;">T&aacute;c giả: Frank Baum</span></strong></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: center; line-height: 19.5px;" align="center"><span style="font-family: ''Times New Roman'';"><strong><span style="line-height: 24px; font-size: 12pt;">***</span></strong></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">&ldquo;Dorothy sống giữa v&ugrave;ng đồng cỏ Kansas bao la&hellip;&rdquo; Bắt đầu như thế&nbsp;&nbsp;l&agrave; cuốn truyện thiếu nhi kinh điển của nước Mỹ. Cơn lốc xo&aacute;y đ&atilde; v&ocirc; t&igrave;nh cuốn Dorothy c&ugrave;ng ch&uacute; ch&oacute; nhỏ Toto đến một nơi lạ l&ugrave;ng, t&ecirc;n gọi xứ Oz; v&agrave; c&ocirc; b&eacute; sợ rằng m&igrave;nh chẳng bao giờ được về với Ch&uacute; Henry, D&igrave; Em trong căn nh&agrave; x&aacute;m giữa đồng cỏ bao la.</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">Nhưng c&ocirc; đ&atilde; gặp những người Munchkin, họ chỉ cho c&ocirc; Con đường Gạch v&agrave;ng dẫn tới Th&agrave;nh phố Ngọc Lục bảo, nơi c&oacute; Oz, vị Ph&ugrave; thủy c&oacute; khả năng thỏa m&atilde;n mọi điều ước. Tr&ecirc;n đường đi, c&ocirc; gặp B&ugrave; nh&igrave;n rơm, Thợ rừng Thiếc, Sư tử Nh&aacute;t, tất cả đồng l&ograve;ng c&ugrave;ng c&ocirc; dấn bước v&agrave;o phi&ecirc;u lưu, xuy&ecirc;n qua đồng anh t&uacute;c chết ch&oacute;c, đối mặt c&aacute;c qu&aacute;i th&uacute;, đối mặt ph&ugrave; thủy &Aacute;c&hellip; v&agrave; cả Ph&ugrave; thủy xứ Oz h&ugrave;ng mạnh, để t&igrave;m ra ch&igrave;a kh&oacute;a cho điều họ mong mỏi nhất&hellip;</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><strong><span style="line-height: 24px; font-size: 12pt;">Nhận định</span></strong></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">&ldquo;T&aacute;c giả đ&atilde; tạo ra một thế giới kỳ diệu từ những thứ quen thuộc, như b&ugrave; nh&igrave;n tr&ecirc;n đồng ng&ocirc;, người m&aacute;y đốn củi, v&agrave; một g&atilde; ph&ugrave; thủy lừa với những ng&oacute;n nghề cũ kỹ&hellip; để bộc lộ ra một chủ đề mu&ocirc;n thuở: &lsquo;Chằng c&oacute; nơi n&agrave;o được như qu&ecirc; nh&agrave;&rsquo;. D&ugrave; hơn 100 năm đ&atilde; tr&ocirc;i qua, cuốn truyện vẫn sống động v&agrave; được đọc nhiều hơn bao giờ hết.&rdquo;&nbsp;&nbsp;</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: right; line-height: 19.5px;" align="right"><span style="font-family: ''Times New Roman'';"><strong><span style="line-height: 24px; font-size: 12pt;">- Synopsis</span></strong></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">&ldquo;Cuốn s&aacute;ch của Frank Baum chiếm giữ một vị tr&iacute; độc nhất v&ocirc; nhị, mang trong n&oacute; tinh t&uacute;y của nền văn h&oacute;a Mỹ.&rdquo;</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: right; line-height: 19.5px;" align="right"><span style="font-family: ''Times New Roman'';"><strong><span style="line-height: 24px; font-size: 12pt;">- Booklist</span></strong></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">&nbsp;&ldquo;Suốt hơn 100 năm nay, Ph&ugrave; thủy xứ Oz đ&atilde; l&agrave;m lũ trẻ say m&ecirc; hết thế hệ n&agrave;y đến thế hệ kh&aacute;c.&rdquo;</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: right; line-height: 19.5px;" align="right"><span style="font-family: ''Times New Roman'';"><strong><span style="line-height: 24px; font-size: 12pt;">- Ingram</span></strong></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><strong><span style="line-height: 24px; font-size: 12pt;">Mời bạn đ&oacute;n đọc.</span></strong></span></p>', '3540phu-thuy-xu-oz.jpg', '2013-12-18', 0),
(44, 10, 'Ông Già Khottabych', 'Lazar Laghin', 'Hôi nhà văn - Nhã Nam', 15, 82000, 360, 'mềm', '10', '<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: center; line-height: 19.5px;" align="center"><span style="font-family: ''Times New Roman'';"><strong><span style="line-height: 24px; font-size: 12pt;">&Ocirc;NG GI&Agrave; KHOTTABYCH</span></strong></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: center; line-height: 19.5px;" align="center"><span style="font-family: ''Times New Roman'';"><strong><span style="line-height: 24px; font-size: 12pt;">T&aacute;c giả: Lazar Laghin</span></strong></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: center; line-height: 19.5px;" align="center"><span style="font-family: ''Times New Roman'';"><strong><span style="line-height: 24px; font-size: 12pt;">***</span></strong></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><strong><span style="line-height: 24px; font-size: 12pt;">&Ocirc;ng gi&agrave; Khottabych</span></strong><span style="line-height: 24px; font-size: 12pt;">&nbsp;của t&aacute;c giả Lazar Laghin được mệnh danh l&agrave; truyện Ngh&igrave;n lẻ một đ&ecirc;m của văn học thiếu nhi Nga, rất h&agrave;i hước hấp dẫn, được cực kỳ y&ecirc;u th&iacute;ch của nhiều thế hệ bạn đọc Nga cũng như nước ngo&agrave;i, trong đ&oacute; c&oacute; cả Việt Nam. Xoay quanh c&aacute;c nh&acirc;n vật ch&iacute;nh l&agrave; những mẩu chuyện ngộ nghĩnh, cảm động, đem lại những b&agrave;i học hay về lối sống trung thực, về t&igrave;nh bạn, t&igrave;nh y&ecirc;u thương gia đ&igrave;nh v&agrave; nhiều điều cần thiết để ph&aacute;t triển nh&acirc;n c&aacute;ch cho bạn đọc trẻ tuổi, cũng như những bất ngờ th&uacute; vị cho cả người đọc trưởng th&agrave;nh.</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><span style="line-height: 24px; font-size: 12pt;">Năm 1956,&nbsp;<strong>&Ocirc;ng gi&agrave; Khottabych</strong>&nbsp;được chuyển thể th&agrave;nh bộ phim c&ugrave;ng t&ecirc;n - v&agrave; l&agrave; bộ phim được đ&aacute;nh gi&aacute; cao suốt nhiều thập kỷ qua của điện ảnh Nga.</span></span></p>\r\n<p class="MsoNormal" style="margin: 0in 0in 10pt; padding: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-align: justify; line-height: 19.5px;"><span style="font-family: ''Times New Roman'';"><strong><span style="line-height: 24px; font-size: 12pt;">Mời bạn đ&oacute;n đọc.</span></strong></span></p>', '8094ong-gia-khottabuch.jpg', '2013-12-18', 0),
(45, 11, 'Chờ Đến Mẫu Giáo Thì Đã Muộn', 'Ibuka Masaru', 'Văn học', 70, 69000, 240, 'mềm', '20', '<div style="box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 12px; line-height: 19.200000762939453px; vertical-align: baseline; color: #5b5b5b; text-align: justify;"><strong style="box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; font-family: inherit; font-style: inherit; font-variant: inherit; line-height: inherit; vertical-align: baseline;">Chờ đến mẫu gi&aacute;o th&igrave; đ&atilde; muộn</strong>&nbsp;l&agrave; cuốn s&aacute;ch b&agrave;n về phương ph&aacute;p gi&aacute;o dục trẻ trong giai đoạn từ 0 đến 3 tuổi của t&aacute;c giả Ibuka Masaru, người s&aacute;ng lập tập đo&agrave;n Sony đồng thời l&agrave; một nh&agrave; nghi&ecirc;n cứu gi&aacute;o dục. Dựa tr&ecirc;n những nghi&ecirc;n cứu về sinh l&yacute; học của n&atilde;o bộ v&agrave; di truyền học, &ocirc;ng đ&atilde; khẳng định sự ph&aacute;t triển về tr&iacute; tuệ v&agrave; năng lực của trẻ được quyết định trong giai đoạn từ 0 đến 3 tuổi, giai đoạn n&agrave;y l&agrave; &ldquo;thời kỳ th&iacute;ch hợp&rdquo; để &ldquo;nu&ocirc;i dạy một đứa trẻ trở n&ecirc;n ngoan ngo&atilde;n, vui vẻ, c&oacute; tr&iacute; tuệ th&ocirc;ng minh v&agrave; khỏe mạnh&rdquo;. Từ đ&oacute;, &ocirc;ng đưa ra những giải ph&aacute;p để gi&uacute;p c&aacute;c bậc cha mẹ tạo ra m&ocirc;i trường để trẻ c&oacute; thể ph&aacute;t huy hết khả năng của m&igrave;nh.&nbsp;</div>\r\n<div style="box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 12px; line-height: 19.200000762939453px; vertical-align: baseline; color: #5b5b5b; text-align: justify;">&nbsp;</div>\r\n<div style="box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 12px; line-height: 19.200000762939453px; vertical-align: baseline; color: #5b5b5b; text-align: justify;">Sức cuốn h&uacute;t của&nbsp;<strong style="box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; font-family: inherit; font-style: inherit; font-variant: inherit; line-height: inherit; vertical-align: baseline;">Chờ đến mẫu gi&aacute;o th&igrave; đ&atilde; muộn</strong>&nbsp;kh&ocirc;ng phải l&agrave; những b&agrave;i học gi&aacute;o dục cao si&ecirc;u m&agrave; l&agrave; lời nhắn nhủ h&atilde;y khơi dậy tiềm năng của trẻ từ những điều nhỏ nhặt trong cuộc sống bằng t&igrave;nh y&ecirc;u của người l&agrave;m cha mẹ.<br style="box-sizing: border-box; margin: 0px; padding: 0px;" /><br style="box-sizing: border-box; margin: 0px; padding: 0px;" />Mời c&aacute;c bạn t&igrave;m đọc!</div>', 'giao.jpg', '2013-12-18', 0),
(46, 13, 'Cất Nỗi Buồn Vào Hôm Qua', 'Nhà Số 5', 'Báo Sinh Viên VN – Hoa Học Trò', 35, 55000, 200, 'mềm', '20', '<div style="box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 12px; line-height: 19.200000762939453px; vertical-align: baseline; color: #5b5b5b; text-align: justify;">Trời đ&atilde; sang Đ&ocirc;ng. Tr&ecirc;n nền x&aacute;m hanh hao của đất trời, c&uacute;c họa mi trắng rung rinh theo từng v&ograve;ng xe, s&aacute;ng bừng phố x&aacute;. Tầm chiều muộn, m&ugrave;i đồ ăn thơm nức mũi tỏa ra từ những qu&aacute;n h&agrave;ng ven đường. V&agrave; thế l&agrave;, bạn nhớ. Nhớ những b&oacute; hoa m&igrave;nh từng được nhận. Nhớ vốc hạt dẻ n&oacute;ng hổi trong tay. Nhớ g&oacute;c phố từng l&ecirc; la ăn b&aacute;nh khoai, gặm ng&ocirc; nướng, xu&yacute;t xoa v&igrave; ốc cay, t&ecirc; lưỡi v&igrave; ch&egrave; n&oacute;ng... Hơn tất cả, bạn nhớ người đ&atilde; l&agrave;m tất cả những thứ đ&oacute; c&ugrave;ng bạn. Người m&agrave; m&ugrave;a Đ&ocirc;ng n&agrave;y c&oacute; thể chẳng c&ograve;n ở b&ecirc;n bạn nữa&hellip;</div>\r\n<div style="box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 12px; line-height: 19.200000762939453px; vertical-align: baseline; color: #5b5b5b; text-align: justify;">&nbsp;</div>\r\n<div style="box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 12px; line-height: 19.200000762939453px; vertical-align: baseline; color: #5b5b5b; text-align: justify;">Bạn th&acirc;n mến,</div>\r\n<div style="box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 12px; line-height: 19.200000762939453px; vertical-align: baseline; color: #5b5b5b; text-align: justify;">&nbsp;</div>\r\n<div style="box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 12px; line-height: 19.200000762939453px; vertical-align: baseline; color: #5b5b5b; text-align: justify;">Rơi v&agrave;o v&ugrave;ng k&yacute; ức ng&agrave;y-h&ocirc;m-qua, hẳn l&agrave; một trạng th&aacute;i ưu tư l&atilde;ng mạn. C&oacute; buồn thương, nhưng kh&ocirc;ng thiếu ngọt ng&agrave;o (nếu kh&ocirc;ng th&igrave; bạn đ&atilde; chẳng rơi v&agrave;o!). Nhưng như thế, ai sẽ sống cho h&ocirc;m nay? Th&igrave; hiện tại sẽ ra sao khi bạn vẫn vương vất những nỗi buồn ở th&igrave; qu&aacute; khứ?</div>\r\n<div style="box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 12px; line-height: 19.200000762939453px; vertical-align: baseline; color: #5b5b5b; text-align: justify;">&nbsp;</div>\r\n<div style="box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 12px; line-height: 19.200000762939453px; vertical-align: baseline; color: #5b5b5b; text-align: justify;">Thay v&igrave; nhớ những b&oacute; hoa kỷ niệm, sao kh&ocirc;ng mua một b&oacute; hoa m&ugrave;a mới tặng m&igrave;nh v&agrave; những người m&igrave;nh thương y&ecirc;u? Thay v&igrave; những &ldquo;&Agrave;, hồi đ&oacute; m&igrave;nh từng ngồi đ&acirc;y&rdquo;, sao kh&ocirc;ng đặt ngay cuộc hẹn với người bạn mới quen. V&agrave; thay v&igrave; những th&oacute;i quen xưa cũ, sao kh&ocirc;ng thử một trải nghiệm mới. M&ugrave;a Đ&ocirc;ng n&agrave;y, h&atilde;y&nbsp;<strong style="box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; font-family: inherit; font-style: inherit; font-variant: inherit; line-height: inherit; vertical-align: baseline;">Cất nỗi buồn v&agrave;o h&ocirc;m qua</strong>&nbsp;để sống cho h&ocirc;m nay.</div>\r\n<div style="box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 12px; line-height: 19.200000762939453px; vertical-align: baseline; color: #5b5b5b; text-align: justify;">&nbsp;</div>\r\n<div style="box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 12px; line-height: 19.200000762939453px; vertical-align: baseline; color: #5b5b5b; text-align: justify;">Bạn sẽ l&agrave;m được điều n&agrave;y, v&igrave; bạn kh&ocirc;ng lẻ loi đơn độc. Cuốn s&aacute;ch th&aacute;ng Mười Hai của Hoa Học Tr&ograve;, gồm tuyển tập những truyện ngắn tuyệt vời nhất, của những c&acirc;y b&uacute;t xuất sắc nhất &ldquo;nh&agrave; số 5&rdquo;, c&ugrave;ng bạn đồng h&agrave;nh trong m&ugrave;a Đ&ocirc;ng n&agrave;y!</div>\r\n<div style="box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 12px; line-height: 19.200000762939453px; vertical-align: baseline; color: #5b5b5b; text-align: justify;">&nbsp;</div>\r\n<div style="box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 12px; line-height: 19.200000762939453px; vertical-align: baseline; color: #5b5b5b; text-align: justify;">Nh&eacute;, ngoắc tay hẹn th&aacute;ng Mười Hai, bạn y&ecirc;u!</div>', 'hoa.jpg', '2013-12-18', 0),
(47, 3, 'Flashcard blueup Christmas', 'Nhiều tác giả', 'Trẻ', 25, 68000, 102, 'mềm', '25', '<div style="box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 12px; line-height: 19.200000762939453px; vertical-align: baseline; color: #5b5b5b; text-align: justify;"><span style="box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; font-family: inherit; font-size: 14px; font-style: inherit; font-variant: inherit; font-weight: inherit; line-height: inherit; vertical-align: baseline;"><strong style="box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; font-family: inherit; font-style: inherit; font-variant: inherit; line-height: inherit; vertical-align: baseline;">Flashcard Blueup l&agrave; g&igrave;?</strong></span></div>\r\n<div style="box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 12px; line-height: 19.200000762939453px; vertical-align: baseline; color: #5b5b5b; text-align: justify;">&nbsp;</div>\r\n<div style="box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 12px; line-height: 19.200000762939453px; vertical-align: baseline; color: #5b5b5b; text-align: justify;">Flashcard l&agrave; 1 tờ giấy c&oacute; nội dung được thiết kế theo cấu tr&uacute;c &ldquo;1 c&acirc;u hỏi (từ tiếng Anh) &ndash; 1 c&acirc;u trả lời (định nghĩa tiếng Anh hoặc tiếng Việt)&rdquo;. Nghi&ecirc;n cứu khoa học cho thấy Flashcard rất hiệu quả cho việc ghi nhớ nhiều th&ocirc;ng tin trong thời gian ngắn.</div>\r\n<div style="box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 12px; line-height: 19.200000762939453px; vertical-align: baseline; color: #5b5b5b; text-align: justify;">&nbsp;</div>\r\n<div style="box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 12px; line-height: 19.200000762939453px; vertical-align: baseline; color: #5b5b5b; text-align: justify;">&bull; Tăng hiệu quả học tập 194% so với c&aacute;ch th&ocirc;ng thường.</div>\r\n<div style="box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 12px; line-height: 19.200000762939453px; vertical-align: baseline; color: #5b5b5b; text-align: justify;">&bull; Học thuộc 600 từ mới/th&aacute;ng (20 từ mới/ng&agrave;y).</div>\r\n<div style="box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 12px; line-height: 19.200000762939453px; vertical-align: baseline; color: #5b5b5b; text-align: justify;">&bull; H&igrave;nh ảnh minh họa gi&uacute;p học thuộc nhanh hơn v&agrave; l&acirc;u hơn.</div>\r\n<div style="box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 12px; line-height: 19.200000762939453px; vertical-align: baseline; color: #5b5b5b; text-align: justify;">&bull; Đạt điểm cao kỳ thi TOEIC, TOEFL, IELTS.</div>\r\n<div style="box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 12px; line-height: 19.200000762939453px; vertical-align: baseline; color: #5b5b5b; text-align: justify;">&nbsp;</div>\r\n<div style="box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 12px; line-height: 19.200000762939453px; vertical-align: baseline; color: #5b5b5b; text-align: justify;"><strong style="box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; font-family: inherit; font-style: inherit; font-variant: inherit; line-height: inherit; vertical-align: baseline;">Học nhanh v&agrave; nhớ l&acirc;u như ăn &ldquo;B&aacute;nh m&igrave; gi&uacute;p tr&iacute; nhớ&rdquo; của Doraemon</strong></div>\r\n<div style="box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 12px; line-height: 19.200000762939453px; vertical-align: baseline; color: #5b5b5b; text-align: justify;">&nbsp;</div>\r\n<div style="box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 12px; line-height: 19.200000762939453px; vertical-align: baseline; color: #5b5b5b; text-align: justify;">Mỗi ng&agrave;y học 20 từ mới, mỗi th&aacute;ng thuộc 600 từ mới. Ho&agrave;n to&agrave;n thực hiện được với Flashcard Blueup, nhờ v&agrave;o:</div>\r\n<div style="box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 12px; line-height: 19.200000762939453px; vertical-align: baseline; color: #5b5b5b; text-align: justify;">&bull; Sự tập trung cao độ</div>\r\n<div style="box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 12px; line-height: 19.200000762939453px; vertical-align: baseline; color: #5b5b5b; text-align: justify;">&bull; Sự li&ecirc;n tưởng</div>\r\n<div style="box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 12px; line-height: 19.200000762939453px; vertical-align: baseline; color: #5b5b5b; text-align: justify;">&bull; Nội dung chất lượng</div>\r\n<div style="box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 12px; line-height: 19.200000762939453px; vertical-align: baseline; color: #5b5b5b; text-align: justify;">&nbsp;</div>\r\n<div style="box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 12px; line-height: 19.200000762939453px; vertical-align: baseline; color: #5b5b5b; text-align: justify;"><strong style="box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; font-family: inherit; font-style: inherit; font-variant: inherit; line-height: inherit; vertical-align: baseline;">Học mọi l&uacute;c mọi nơi</strong></div>\r\n<div style="box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 12px; line-height: 19.200000762939453px; vertical-align: baseline; color: #5b5b5b; text-align: justify;">&nbsp;</div>\r\n<div style="box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 12px; line-height: 19.200000762939453px; vertical-align: baseline; color: #5b5b5b; text-align: justify;">Tiện dụng, học mọi l&uacute;c mọi nơi (đi xe bus, xếp h&agrave;ng, dừng đ&egrave;n đỏ&hellip;).</div>\r\n<div style="box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 12px; line-height: 19.200000762939453px; vertical-align: baseline; color: #5b5b5b; text-align: justify;">Đặc biệt, mỗi sản phẩm giấy Blueup c&oacute; một m&atilde; k&iacute;ch hoạt ri&ecirc;ng cho ph&eacute;p bạn sử dụng c&aacute;c t&iacute;nh năng tr&ecirc;n www.blueup.vn.</div>\r\n<div style="box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 12px; line-height: 19.200000762939453px; vertical-align: baseline; color: #5b5b5b; text-align: justify;"><br style="box-sizing: border-box; margin: 0px; padding: 0px;" /><span style="box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; font-family: inherit; font-size: 14px; font-style: inherit; font-variant: inherit; font-weight: inherit; line-height: inherit; vertical-align: baseline;"><strong style="box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; font-family: inherit; font-style: inherit; font-variant: inherit; line-height: inherit; vertical-align: baseline;">Flashcard Blueup Christmas</strong></span><br style="box-sizing: border-box; margin: 0px; padding: 0px;" />&nbsp;</div>\r\n<div style="box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 12px; line-height: 19.200000762939453px; vertical-align: baseline; color: #5b5b5b; text-align: justify;">Bao gồm 50 từ vựng c&ugrave;ng 6 fun facts vui nhộn trong chiếc hộp giấy xinh xắn, th&ecirc;m một tấm thiệp nhỏ nhắn để bạn c&oacute; thể ghi lời ch&uacute;c v&agrave;o v&agrave; tặng n&oacute; cho bạn b&egrave; m&igrave;nh.<br style="box-sizing: border-box; margin: 0px; padding: 0px;" />&nbsp;</div>\r\n<div style="box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 12px; line-height: 19.200000762939453px; vertical-align: baseline; color: #5b5b5b; text-align: justify;">Với th&ocirc;ng điệp hướng về m&ocirc;i trường, sản phẩm Flashcard Blueup Christmas đặc biệt gi&agrave;nh cho m&ugrave;a Gi&aacute;ng Sinh được thiết kế ho&agrave;n to&agrave;n bằng giấy t&aacute;i chế.</div>', 'acsca.jpg', '2013-12-18', 0);

